import React, { useState } from "react";
import axios from "axios";
import "./AddTest.css"; // Separate CSS file

export default function AddTest() {
  const [testType, setTestType] = useState("");
  const [numQuestions, setNumQuestions] = useState(1);
  const [questions, setQuestions] = useState([]);
  const [currentQIndex, setCurrentQIndex] = useState(0);
  const [currentQ, setCurrentQ] = useState({
    question: "",
    options: ["", "", "", ""],
    correctOption: "A",
    marks: "",
    expectedOutput: "",
  });

  const [showAI, setShowAI] = useState(false);
  const [aiQuestion, setAiQuestion] = useState({
    question: "",
    options: ["", "", "", ""],
    correctOption: "A",
    marks: "",
    expectedOutput: "",
  });

  // Select test type
  const handleTestTypeSelect = (type) => {
    setTestType(type);
    setQuestions([]);
    setCurrentQIndex(0);
    setCurrentQ({
      question: "",
      options: ["", "", "", ""],
      correctOption: "A",
      marks: "",
      expectedOutput: "",
    });
  };

  // Change option text
  const handleOptionChange = (index, value) => {
    const updatedOptions = [...currentQ.options];
    updatedOptions[index] = value;
    setCurrentQ({ ...currentQ, options: updatedOptions });
  };

  // Add question (from form)
  const handleAddQuestion = (q = currentQ) => {
    if (!q.question.trim()) {
      alert("Please enter the question!");
      return;
    }

    setQuestions([...questions, { ...q, type: testType }]);

    // Reset form
    setCurrentQ({
      question: "",
      options: ["", "", "", ""],
      correctOption: "A",
      marks: "",
      expectedOutput: "",
    });
    setCurrentQIndex(currentQIndex + 1);
  };

  // Submit test
  const handleSubmit = async () => {
    try {
      await axios.post("http://localhost:5000/api/tests", {
        type: testType,
        questions,
      });
      alert("Test saved successfully!");
      setTestType("");
      setQuestions([]);
      setNumQuestions(1);
      setCurrentQIndex(0);
    } catch (error) {
      console.error(error);
      alert("Error saving test!");
    }
  };

  // AI Question generation
  const fetchAIQuestion = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/ai/generate", { testType });
      const aiData = res.data;

      if (testType === "MCQ") {
        setAiQuestion({
          question: aiData.question || "",
          options: aiData.options || ["", "", "", ""],
          correctOption: aiData.correctOption || "A",
          marks: "1",
        });
      } else if (testType === "Long") {
        setAiQuestion({
          question: aiData.question || "",
          marks: aiData.marks || "1",
        });
      } else if (testType === "Coding") {
        setAiQuestion({
          question: aiData.question || "",
          expectedOutput: aiData.expectedOutput || "",
        });
      }
      setShowAI(true);
    } catch (err) {
      console.error(err);
      alert("AI generation failed.");
    }
  };

  const isLastQuestion = currentQIndex + 1 === parseInt(numQuestions);

  return (
    <div className="add-test-container">
      <h1 className="header">Add Test</h1>

      {/* Test Type Buttons */}
      {!testType && (
        <div className="test-type-buttons">
          {["MCQ", "Long", "Coding"].map((type) => (
            <button key={type} onClick={() => handleTestTypeSelect(type)}>
              {type} Test
            </button>
          ))}
        </div>
      )}

      {/* Number of Questions */}
      {testType && questions.length === 0 && (
        <div className="num-questions">
          <label>Number of Questions:</label>
          <input
            type="number"
            min="1"
            value={numQuestions}
            onChange={(e) => setNumQuestions(e.target.value)}
          />
        </div>
      )}

      {/* Question Form */}
      {testType && questions.length < numQuestions && (
        <div className="question-form">
          <h2>
            Question {currentQIndex + 1} of {numQuestions}
          </h2>
          <textarea
            placeholder="Enter question here..."
            value={currentQ.question}
            onChange={(e) => setCurrentQ({ ...currentQ, question: e.target.value })}
          />

          {testType === "MCQ" &&
            ["A", "B", "C", "D"].map((opt, i) => (
              <div key={i} className="mcq-option">
                <input
                  type="radio"
                  name="correctOption"
                  value={opt}
                  checked={currentQ.correctOption === opt}
                  onChange={(e) => setCurrentQ({ ...currentQ, correctOption: e.target.value })}
                />
                <input
                  type="text"
                  placeholder={`Option ${opt}`}
                  value={currentQ.options[i]}
                  onChange={(e) => handleOptionChange(i, e.target.value)}
                />
              </div>
            ))}

          {(testType === "MCQ" || testType === "Long") && (
            <div className="marks-input">
              <label>Marks:</label>
              <input
                type="number"
                value={currentQ.marks}
                onChange={(e) => setCurrentQ({ ...currentQ, marks: e.target.value })}
              />
            </div>
          )}

          {testType === "Coding" && (
            <div className="expected-output">
              <label>Expected Output:</label>
              <input
                type="text"
                value={currentQ.expectedOutput}
                onChange={(e) => setCurrentQ({ ...currentQ, expectedOutput: e.target.value })}
              />
            </div>
          )}

          <div className="form-buttons">
            <button onClick={handleAddQuestion}>
              {isLastQuestion ? "Finish Questions" : "Add Question"}
            </button>
            <button onClick={fetchAIQuestion}>AI Help</button>
          </div>
        </div>
      )}

      {/* AI Modal */}
      {showAI && (
        <div className="ai-modal">
          <h2>AI Generated Question</h2>
          <textarea
            value={aiQuestion.question}
            onChange={(e) => setAiQuestion({ ...aiQuestion, question: e.target.value })}
          />
          {testType === "MCQ" &&
            aiQuestion.options.map((opt, i) => (
              <input
                key={i}
                type="text"
                value={aiQuestion.options[i]}
                onChange={(e) => {
                  const newOptions = [...aiQuestion.options];
                  newOptions[i] = e.target.value;
                  setAiQuestion({ ...aiQuestion, options: newOptions });
                }}
              />
            ))}
          <div className="ai-buttons">
            <button
              onClick={() => {
                handleAddQuestion(aiQuestion);
                setShowAI(false);
              }}
            >
              Add Question
            </button>
            <button onClick={() => setShowAI(false)}>Cancel</button>
          </div>
        </div>
      )}

      {/* Submit Button */}
      {questions.length === parseInt(numQuestions) && (
        <div className="submit-test">
          <button onClick={handleSubmit}>Submit Test</button>
        </div>
      )}

      {/* Preview Questions */}
      {questions.length > 0 && (
        <div className="preview-questions">
          <h3>Questions Added:</h3>
          <ul>
            {questions.map((q, i) => (
              <li key={i}>
                {q.type}: {q.question} {q.marks && `(${q.marks} marks)`}{" "}
                {q.type === "MCQ" && `- Answer: ${q.correctOption}`}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
