import React from "react";
import "../styles/TestCard.css";

export default function TestCard({ test, onTakeTest }) {
  const category = test.category ? test.category.toUpperCase() : "";
  let badgeClass = "px-3 py-1 rounded-full text-xs font-semibold ";
  let cardBgClass = "default-card-bg";
  if (test.category === "mcq") {
    badgeClass += "badge-mcq";
    cardBgClass = "mcq-card-bg";
  } else if (test.category === "long") {
    badgeClass += "badge-long";
    cardBgClass = "long-card-bg";
  } else if (test.category === "coding") {
    badgeClass += "badge-coding";
    cardBgClass = "coding-card-bg";
  }

  return (
    <div className={`bg-white rounded-xl shadow-lg p-6 m-2 flex flex-col ${cardBgClass}`}>
      <div className="flex flex-col items-center mb-2 w-full">
        <span className={badgeClass}>
          {category === "MCQ" && "MCQ"}
          {category === "LONG" && "LONG ANSWER"}
          {category === "CODING" && "CODING"}
          {!category && "UNKNOWN"}
        </span>
        <h2 className="text-xl font-bold">{test.name || "No Name"}</h2>
      </div>
      <div className="mb-2">Duration: {test.duration || "?"} min</div>
      <div className="mb-2">Total Marks: {test.totalMarks || "?"}</div>
      <div className="mb-2">Questions: {test.questions ? test.questions.length : "?"}</div>
      <button
        className="mt-auto bg-blue-600 text-white px-4 py-2 rounded-lg"
        onClick={() => onTakeTest(test)}
      >
        Take Test
      </button>
    </div>
  );
}
