import { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import "../dashboard.css";

export default function DashboardMentor() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatarFile, setAvatarFile] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    fetchMe();
  }, []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("mentor.")) {
      const key = name.split(".")[1];
      setForm({ ...form, mentor: { ...form.mentor, [key]: value } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const updateProfile = async () => {
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("email", form.email);
      if (form.mentor) {
        formData.append("currentInstitution", form.mentor.currentInstitution || "");
        formData.append("phoneNumber", form.mentor.phoneNumber || "");
        formData.append("experience", form.mentor.experience || "");
      }
      if (avatarFile) formData.append("avatar", avatarFile);

      const { data } = await api.put("/auth/me", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      localStorage.setItem("user", JSON.stringify(data.user));
      setEditing(false);
      fetchMe();
    } catch (err) {
      console.error("Update failed:", err.response?.data || err.message);
    }
  };

  return (
    <div className="dashboard-wrapper">
      <div className="dashboard-actions">
        <h2>Mentor Actions</h2>
        <div className="action-card" onClick={() => navigate("/add-test")}>
          ‚ûï Add Test
        </div>
        <div className="action-card" onClick={() => navigate("/view-result")}>
          üìä View Result
        </div>
        <div className="action-card" onClick={() => navigate("/view-report")}>
          üìù View Report
        </div>
        <div className="action-card" onClick={() => navigate("/view-student")}>
          üë®‚Äçüéì View Student
        </div>
      </div>

      <div className="dashboard-profile">
        <div className="profile-card">
          <h1>üë®‚Äçüè´ Mentor Dashboard</h1>
          {user?.avatar && (
            <img
              src={`${user.avatar}?t=${new Date().getTime()}`}
              alt="avatar"
              className="avatar"
            />
          )}

          {!editing ? (
            <>
              <p className="welcome">
                Welcome, <b>{user?.name}</b>
              </p>
              <ul className="details">
                <li>Email: {user?.email}</li>
                <li>Institution: {user?.mentor?.currentInstitution}</li>
                <li>Phone: {user?.mentor?.phoneNumber}</li>
                <li>Experience: {user?.mentor?.experience}</li>
              </ul>
              <button className="btn edit" onClick={() => setEditing(true)}>
                ‚úèÔ∏è Edit Profile
              </button>
            </>
          ) : (
            <div className="form-container">
              <input
                name="name"
                value={form.name || ""}
                onChange={onChange}
                placeholder="Full Name"
              />
              <input
                name="mentor.currentInstitution"
                value={form.mentor?.currentInstitution || ""}
                onChange={onChange}
                placeholder="Current Institution"
              />
              <input
                name="mentor.phoneNumber"
                value={form.mentor?.phoneNumber || ""}
                onChange={onChange}
                placeholder="Phone Number"
              />
              <input
                name="mentor.experience"
                value={form.mentor?.experience || ""}
                onChange={onChange}
                placeholder="Experience"
              />
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setAvatarFile(e.target.files[0])}
              />
              <div className="btn-group">
                <button className="btn save" onClick={updateProfile}>
                  üíæ Save
                </button>
                <button className="btn cancel" onClick={() => setEditing(false)}>
                  ‚ùå Cancel
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
