import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock } from "lucide-react";
import api from "../utils/api";

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [warning, setWarning] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fetch test data
  useEffect(() => {
    if (!test && id) {
      const fetchTest = async () => {
        try {
          // const res = await api.get(`/tests/${id}`);
          const res = await api.get(`/api/tests/${id}`);
          console.log("Fetched test:", res.data);
          setTest(res.data);
          setTimeLeft(res.data.duration * 60);
        } catch (err) {
          console.error(err);
          alert("Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else if (test) {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  // Start test: fullscreen + camera
  const startTest = async () => {
    setStarted(true);
    const elem = document.documentElement;
    if (elem.requestFullscreen) {
      try {
        await elem.requestFullscreen();
      } catch (err) {
        console.error("Fullscreen failed:", err);
      }
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 320, height: 240 },
      });
      setCameraStream(stream);
      const video = document.getElementById("cameraFeed");
      if (video) video.srcObject = stream;
    } catch (err) {
      console.error("Camera access denied:", err);
      alert("Camera access is required for this test!");
    }
  };

  // Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            handleSubmit("Time's up!");
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  // Violation detection
  useEffect(() => {
    if (!started) return;

    const handleViolation = (message) => {
      if (violationCount === 0) {
        setWarning(`⚠️ ${message} First warning - next violation will end the test.`);
        setViolationCount(1);
        setTimeout(() => setWarning(""), 5000);
      } else {
        handleSubmit(`Test ended due to violation: ${message}`);
      }
    };

    const handleCopy = (e) => {
      e.preventDefault();
      handleViolation("Copying is not allowed!");
    };
    const handlePaste = (e) => {
      e.preventDefault();
      handleViolation("Pasting is not allowed!");
    };
    const handleKeyDown = (e) => {
      if (
        e.key === "PrintScreen" ||
        (e.metaKey && e.shiftKey && (e.key === "4" || e.key === "5")) ||
        (e.ctrlKey && e.shiftKey && e.key === "S")
      ) {
        e.preventDefault();
        handleViolation("Screenshots are not allowed!");
      }
      if (e.key === "F12" || (e.ctrlKey && e.shiftKey && e.key === "I")) {
        e.preventDefault();
        handleViolation("Developer tools are not allowed!");
      }
    };
    const handleVisibilityChange = () => {
      if (document.hidden) handleViolation("Switching tabs is not allowed!");
    };
    const handleWindowBlur = () => {
      handleViolation("Switching to another application is not allowed!");
    };
    const handleContextMenu = (e) => {
      e.preventDefault();
      handleViolation("Right-click is disabled!");
    };

    document.addEventListener("copy", handleCopy);
    document.addEventListener("paste", handlePaste);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("visibilitychange", handleVisibilityChange);
    document.addEventListener("contextmenu", handleContextMenu);
    window.addEventListener("blur", handleWindowBlur);

    return () => {
      document.removeEventListener("copy", handleCopy);
      document.removeEventListener("paste", handlePaste);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
      document.removeEventListener("contextmenu", handleContextMenu);
      window.removeEventListener("blur", handleWindowBlur);
    };
  }, [started, violationCount]);

  const handleAnswerChange = (questionId, value) => {
  setAnswers((prev) => ({ ...prev, [String(questionId)]: value }));
};


  // ✅ Updated handleSubmit (no frontend scoring)
  const handleSubmit = async (reason = "Test completed") => {
    if (isSubmitting) return;
    setIsSubmitting(true);

    try {
      const res = await api.post(`/tests/${id}/submit`, {
        answers,
        submissionReason: reason,
      });

      if (cameraStream) cameraStream.getTracks().forEach((track) => track.stop());
      if (document.fullscreenElement) document.exitFullscreen();

      alert(
        `✅ ${reason}\nYour score: ${res.data?.score?.obtainedMarks}/${res.data?.score?.totalMarks}`
      );
      navigate("/my-scores");
    } catch (err) {
      console.error(err);
      alert("Error submitting test. Please try again.");
      setIsSubmitting(false);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  // Instruction Page (Before starting the test)
  if (!test)
    return (
      <div className="flex items-center justify-center min-h-screen text-xl">
        Loading test...
      </div>
    );

  if (!started) {
    return (
      <div className="min-h-screen flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-2xl w-full">
          <h1 className="text-3xl font-bold text-gray-800 mb-4">{test.name}</h1>
          <p>Duration: {test?.duration || 0} minutes</p>
          <p>Total Marks: {test?.totalMarks || 0}</p>
          <button
            onClick={startTest}
            className="mt-6 px-8 py-4 bg-blue-600 text-white rounded-xl"
          >
            Start Test
          </button>
        </div>
      </div>
    );
  }

  // Test Page (After starting the test)
  const questions = Array.isArray(test.questions) ? test.questions : [];
  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {warning && (
        <div className="bg-red-500 text-white px-6 py-3 text-center font-semibold">
          {warning}
        </div>
      )}

      {/* Header */}
      <div className="bg-white shadow-lg px-6 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold">{test.name}</h1>
        <div className="flex items-center space-x-4">
          <div
            className={`flex items-center px-4 py-2 rounded-lg ${
              timeLeft <= 300 ? "bg-red-100 text-red-800" : "bg-blue-100 text-blue-800"
            }`}
          >
            <Clock className="mr-2" size={20} /> {formatTime(timeLeft)}
          </div>
          <video
            id="cameraFeed"
            autoPlay
            playsInline
            muted
            className="w-32 h-24 bg-black rounded-lg border-2 border-blue-500"
          />
        </div>
      </div>

      <div className="flex flex-1">
        {/* Sidebar with questions */}
        <div className="w-64 bg-white shadow-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Questions</h3>
          <div className="grid grid-cols-5 gap-2">
            {questions.map((q, idx) => {
              const isAnswered = !!answers[q._id];
              const isCurrent = idx === currentQuestionIndex;
              return (
                <button
                  key={q._id}
                  onClick={() => setCurrentQuestionIndex(idx)}
                  className={`w-10 h-10 rounded-full border-2 flex items-center justify-center text-sm font-semibold ${
                    isCurrent
                      ? "bg-blue-600 text-white border-blue-600"
                      : isAnswered
                      ? "bg-green-100 text-green-800 border-green-500"
                      : "bg-gray-100 text-gray-600 border-gray-300"
                  }`}
                >
                  {idx + 1}
                </button>
              );
            })}
          </div>
        </div>

        {/* Question Area */}
        <div className="flex-1 p-8">
          {currentQuestion ? (
            <div className="bg-white rounded-xl shadow-lg p-8 max-w-4xl mx-auto">
              <h2 className="text-xl font-semibold mb-4">
                Question {currentQuestionIndex + 1} of {questions.length} (
                {currentQuestion?.marks || 0} marks)
              </h2>
              <p className="text-lg mb-6">{currentQuestion?.question || "No question text available"}</p>
              <div className="space-y-3">
                {currentQuestion?.options?.map((option, idx) => (
                  <label
                    key={idx}
                    className={`flex items-center p-4 rounded-lg border-2 cursor-pointer ${
                      answers[currentQuestion._id] === option
                        ? "border-blue-500 bg-blue-50"
                        : "border-gray-200"
                    }`}
                  >
                    <input
                      type="radio"
                      name={currentQuestion._id}
                      value={option}
                      //checked={answers[currentQuestion._id] === option}
                      checked={answers[String(currentQuestion._id)] === option}
                      onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
                      className="sr-only"
                    />
                    <div
                      className={`w-5 h-5 rounded-full border-2 mr-3 flex items-center justify-center ${
                        answers[currentQuestion._id] === option
                          ? "border-blue-500 bg-blue-500"
                          : "border-gray-300"
                      }`}
                    >
                      {answers[currentQuestion._id] === option && (
                        <div className="w-2 h-2 bg-white rounded-full"></div>
                      )}
                    </div>
                    <span>{option}</span>
                  </label>
                ))}
              </div>

              <div className="flex justify-between mt-8">
                <button
                  onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                  disabled={currentQuestionIndex === 0}
                  className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg disabled:opacity-50"
                >
                  Previous
                </button>
                {currentQuestionIndex < questions.length - 1 ? (
                  <button
                    onClick={() => setCurrentQuestionIndex(currentQuestionIndex + 1)}
                    className="px-6 py-2 bg-blue-600 text-white rounded-lg"
                  >
                    Next
                  </button>
                ) : (
                  <button
                    onClick={() => handleSubmit()}
                    disabled={isSubmitting}
                    className="px-8 py-3 bg-green-600 text-white rounded-lg"
                  >
                    {isSubmitting ? "Submitting..." : "Submit Test"}
                  </button>
                )}
              </div>
            </div>
          ) : (
            <p className="text-center text-xl text-gray-600 py-12">No questions available</p>
          )}
        </div>
      </div>
    </div>
  );
}
