import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import api from "../utils/api";

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams(); // use URL param
  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);

  // Fetch test if not passed via state
  useEffect(() => {
    if (!test) {
      const fetchTest = async () => {
        try {
          const { data } = await api.get(`/student/tests/${id}`);
          setTest(data);
          setTimeLeft(data.duration * 60);
        } catch (err) {
          console.error(err);
          alert("Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  // Start test
  const startTest = () => {
    setStarted(true);
    openFullscreen();
  };

  // Timer countdown
  useEffect(() => {
    if (!started || !timeLeft) return;
    const interval = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);
    if (timeLeft === 0) handleSubmit();
    return () => clearInterval(interval);
  }, [timeLeft, started]);

  // Fullscreen mode
  const openFullscreen = () => {
    const elem = document.documentElement;
    if (elem.requestFullscreen) elem.requestFullscreen();
    else if (elem.mozRequestFullScreen) elem.mozRequestFullScreen();
    else if (elem.webkitRequestFullscreen) elem.webkitRequestFullscreen();
    else if (elem.msRequestFullscreen) elem.msRequestFullscreen();
  };

  // Handle tab switch
  useEffect(() => {
    const handleVisibility = () => {
      if (!started) return;
      if (document.hidden) {
        alert("Warning: Do not switch tabs! One violation allowed.");
        setViolationCount((prev) => prev + 1);
        if (violationCount >= 1) {
          alert("Test ended due to multiple violations!");
          handleSubmit();
        }
      }
    };
    document.addEventListener("visibilitychange", handleVisibility);
    return () => document.removeEventListener("visibilitychange", handleVisibility);
  }, [violationCount, started]);

  // Handle copy
  useEffect(() => {
    const handleCopy = (e) => {
      if (!started) return;
      alert("Copying is not allowed! One violation allowed.");
      setViolationCount((prev) => prev + 1);
      if (violationCount >= 1) {
        alert("Test ended due to multiple violations!");
        handleSubmit();
      }
      e.preventDefault();
    };
    document.addEventListener("copy", handleCopy);
    return () => document.removeEventListener("copy", handleCopy);
  }, [violationCount, started]);

  const handleChange = (qId, value) => {
    setAnswers({ ...answers, [qId]: value });
  };

  const handleSubmit = async () => {
    try {
      await api.post(`/student/tests/${id}/submit`, { answers });
      alert("Test submitted!");
      navigate("/dashboard");
    } catch (err) {
      console.error(err);
    }
  };

  if (!test) return <p>Loading test...</p>;

  // Instruction screen
  if (!started) {
    return (
      <div style={{ padding: "2rem" }}>
        <h1>{test.name}</h1>
        <h3>Instructions:</h3>
        <ul>
          <li>Time allotted: {test.duration} minutes</li>
          <li>Do not switch tabs during the test</li>
          <li>Do not copy questions or answers</li>
          <li>Camera and microphone recording is active</li>
          <li>First violation: warning, second violation: test ends</li>
          <li>Once started, test cannot be paused</li>
        </ul>
        <button
          onClick={startTest}
          style={{
            padding: "1rem 2rem",
            backgroundColor: "#3b82f6",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            marginTop: "1rem",
          }}
        >
          Start Test
        </button>
      </div>
    );
  }

  // Test screen
  return (
    <div style={{ padding: "2rem" }}>
      <h1>{test.name}</h1>
      <p style={{ float: "right" }}>
        Time Left: {Math.floor(timeLeft / 60)}:{("0" + (timeLeft % 60)).slice(-2)}
      </p>
      <div style={{ marginTop: "3rem" }}>
        {test.questions.map((q, index) => (
          <div key={q._id} style={{ marginBottom: "2rem" }}>
            <p>
              <strong>Q{index + 1}:</strong> {q.question}
            </p>
            {q.options ? (
              q.options.map((opt, i) => (
                <label key={i} style={{ display: "block", marginBottom: "0.5rem" }}>
                  <input
                    type="radio"
                    name={q._id}
                    value={opt}
                    onChange={(e) => handleChange(q._id, e.target.value)}
                  />
                  {opt}
                </label>
              ))
            ) : (
              <textarea
                placeholder="Your answer"
                onChange={(e) => handleChange(q._id, e.target.value)}
                style={{ width: "100%", height: "80px" }}
              />
            )}
          </div>
        ))}
        <button
          onClick={handleSubmit}
          style={{
            padding: "1rem 2rem",
            backgroundColor: "#10b981",
            color: "#fff",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            marginTop: "1rem",
          }}
        >
          Submit Test
        </button>
      </div>
    </div>
  );
}
