import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock } from "lucide-react";
import api from "../utils/api";

export default function TakeTest() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { state } = useLocation();

  const [test, setTest] = useState(state?.test || null);
  const [questions, setQuestions] = useState([]);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fetch test data
  useEffect(() => {
    const fetchTest = async () => {
      try {
        const res = await api.get(`/tests/${id}`);
        console.log("Fetched test:", res.data);
        setTest(res.data);
        setQuestions(res.data.questions || []);
        setTimeLeft(res.data.duration * 60);
      } catch (err) {
        console.error(err);
        alert("Failed to fetch test!");
        navigate("/my-tests");
      }
    };

    if (!test && id) {
      fetchTest();
    } else if (test) {
      setQuestions(test.questions || []);
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  // Start test
  const startTest = async () => {
    setStarted(true);

    // Fullscreen
    try {
      await document.documentElement.requestFullscreen();
    } catch (err) {
      console.error("Fullscreen failed:", err);
    }

    // Webcam
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      setCameraStream(stream);
      const video = document.getElementById("cameraFeed");
      if (video) video.srcObject = stream;
    } catch (err) {
      alert("Camera access is required for this test!");
    }
  };

  // Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            handleSubmit("Time's up!");
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  // Malpractice detection
  useEffect(() => {
    if (!started) return;

    const handleViolation = (msg) => {
      if (violationCount === 0) {
        alert(`‚ö†Ô∏è ${msg}. First warning!`);
        setViolationCount(1);
      } else {
        alert(`‚ùå Test ended due to violation: ${msg}`);
        handleSubmit("Violation");
      }
    };

    const handleVisibility = () => {
      if (document.hidden) handleViolation("Tab switching not allowed");
    };
    const handleBlur = () => handleViolation("Switching apps not allowed");
    const handleKey = (e) => {
      if (
        e.key === "PrintScreen" ||
        (e.ctrlKey && e.shiftKey && e.key === "I")
      ) {
        e.preventDefault();
        handleViolation("Screenshots / DevTools not allowed");
      }
    };

    document.addEventListener("visibilitychange", handleVisibility);
    window.addEventListener("blur", handleBlur);
    document.addEventListener("keydown", handleKey);

    return () => {
      document.removeEventListener("visibilitychange", handleVisibility);
      window.removeEventListener("blur", handleBlur);
      document.removeEventListener("keydown", handleKey);
    };
  }, [started, violationCount]);

  const handleAnswerChange = (questionId, value) => {
    setAnswers((prev) => ({ ...prev, [questionId]: value }));
  };

  const handleSubmit = async (reason = "Test completed") => {
    if (isSubmitting) return;
    setIsSubmitting(true);

    try {
      const res = await api.post(`/tests/${id}/submit`, {
        answers,
        submissionReason: reason,
      });

      if (cameraStream) cameraStream.getTracks().forEach((t) => t.stop());
      if (document.fullscreenElement) document.exitFullscreen();

      alert(
        `‚úÖ ${reason}\nYour score: ${res.data?.score?.obtainedMarks}/${res.data?.score?.totalMarks}`
      );
      navigate("/my-scores");
    } catch (err) {
      console.error(err);
      alert("Error submitting test!");
      setIsSubmitting(false);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  if (!test) {
    return (
      <div className="flex items-center justify-center min-h-screen text-xl">
        Loading test...
      </div>
    );
  }

  // Instructions page
  if (!started) {
    return (
      <div className="min-h-screen flex items-center justify-center p-6">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-2xl w-full">
          <h1 className="text-3xl font-bold text-gray-800 mb-4">{test.name}</h1>
          <p>‚è± Duration: {test.duration} minutes</p>
          <p>üìä Total Marks: {test.totalMarks}</p>
          <h2 className="text-lg font-semibold mt-6 mb-2">Instructions:</h2>
          <ul className="list-disc pl-6 text-gray-700 space-y-2">
            <li>No copy, paste, screenshots or DevTools</li>
            <li>No tab switching or minimizing window</li>
            <li>Webcam must be ON during test</li>
            <li>One warning allowed. Second violation ends test</li>
            <li>Auto-submit when time ends</li>
          </ul>
          <button
            onClick={startTest}
            className="mt-6 px-8 py-4 bg-blue-600 text-white rounded-xl"
          >
            Start Test
          </button>
        </div>
      </div>
    );
  }

  // Test page
  const currentQuestion = questions[currentQuestionIndex];
  const allAnswered = Object.keys(answers).length === questions.length;

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-lg px-6 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold">{test.name}</h1>
        <div className="flex items-center space-x-4">
          <div
            className={`flex items-center px-4 py-2 rounded-lg ${
              timeLeft <= 300
                ? "bg-red-100 text-red-800"
                : "bg-blue-100 text-blue-800"
            }`}
          >
            <Clock className="mr-2" size={20} /> {formatTime(timeLeft)}
          </div>
          <video
            id="cameraFeed"
            autoPlay
            playsInline
            muted
            className="w-32 h-24 bg-black rounded-lg border-2 border-blue-500"
          />
        </div>
      </div>

      <div className="flex flex-1">
        {/* Sidebar with questions */}
        <div className="w-64 bg-white shadow-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Questions</h3>
          <div className="grid grid-cols-5 gap-2">
            {questions.map((q, idx) => {
              const isAnswered = !!answers[q._id];
              const isCurrent = idx === currentQuestionIndex;
              return (
                <button
                  key={q._id}
                  onClick={() => setCurrentQuestionIndex(idx)}
                  className={`w-10 h-10 rounded-full border-2 flex items-center justify-center text-sm font-semibold ${
                    isCurrent
                      ? "bg-blue-600 text-white border-blue-600"
                      : isAnswered
                      ? "bg-green-100 text-green-800 border-green-500"
                      : "bg-gray-100 text-gray-600 border-gray-300"
                  }`}
                >
                  {idx + 1}
                </button>
              );
            })}
          </div>
        </div>

        {/* Question area */}
        <div className="flex-1 p-8">
          {currentQuestion ? (
            <div className="bg-white rounded-xl shadow-lg p-8 max-w-4xl mx-auto">
              <h2 className="text-xl font-semibold mb-4">
                Question {currentQuestionIndex + 1} of {questions.length} (
                {currentQuestion.marks} marks)
              </h2>
              <p className="text-lg mb-6">{currentQuestion.question}</p>
              <div className="space-y-3">
                {currentQuestion.options.map((opt, i) => (
                  <label
                    key={i}
                    className={`flex items-center p-4 rounded-lg border-2 cursor-pointer ${
                      answers[currentQuestion._id] === opt
                        ? "border-blue-500 bg-blue-50"
                        : "border-gray-200"
                    }`}
                  >
                    <input
                      type="radio"
                      name={currentQuestion._id}
                      value={opt}
                      checked={answers[currentQuestion._id] === opt}
                      onChange={(e) =>
                        handleAnswerChange(currentQuestion._id, e.target.value)
                      }
                      className="sr-only"
                    />
                    <div
                      className={`w-5 h-5 rounded-full border-2 mr-3 flex items-center justify-center ${
                        answers[currentQuestion._id] === opt
                          ? "border-blue-500 bg-blue-500"
                          : "border-gray-300"
                      }`}
                    >
                      {answers[currentQuestion._id] === opt && (
                        <div className="w-2 h-2 bg-white rounded-full"></div>
                      )}
                    </div>
                    <span>{opt}</span>
                  </label>
                ))}
              </div>

              <div className="flex justify-between mt-8">
                <button
                  onClick={() =>
                    setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))
                  }
                  disabled={currentQuestionIndex === 0}
                  className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg disabled:opacity-50"
                >
                  Previous
                </button>
                {currentQuestionIndex < questions.length - 1 ? (
                  <button
                    onClick={() =>
                      setCurrentQuestionIndex(currentQuestionIndex + 1)
                    }
                    className="px-6 py-2 bg-blue-600 text-white rounded-lg"
                  >
                    Next
                  </button>
                ) : (
                  allAnswered && (
                    <button
                      onClick={() => handleSubmit("Completed")}
                      disabled={isSubmitting}
                      className="px-8 py-3 bg-green-600 text-white rounded-lg"
                    >
                      {isSubmitting ? "Submitting..." : "Submit Test"}
                    </button>
                  )
                )}
              </div>
            </div>
          ) : (
            <p className="text-center text-xl text-gray-600 py-12">
              No questions available
            </p>
          )}
        </div>
      </div>
    </div>
  );
}
