import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import {
  FileText,
  Download,
  Calendar,
  User,
  Clock,
  ArrowLeft,
  Bell,
  AlertTriangle,
  CheckCircle
} from "lucide-react";
import { motion } from "framer-motion";

export default function Assignments() {
  const [assignments, setAssignments] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("assignments");
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [assignmentsRes, notificationsRes] = await Promise.all([
        api.get("/assignments/student"),
        api.get("/notifications/student")
      ]);
      
      setAssignments(assignmentsRes.data);
      setNotifications(notificationsRes.data);
    } catch (err) {
      console.error("Error fetching data:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = async (assignmentId, filename) => {
    try {
      const response = await api.get(`/assignments/download/${assignmentId}`, {
        responseType: 'blob'
      });
      
      // Create blob URL and download
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Error downloading file:", err);
      alert("Failed to download file");
    }
  };

  const markAsRead = async (notificationId) => {
    try {
      await api.patch(`/notifications/${notificationId}/read`);
      fetchData(); // Refresh data
    } catch (err) {
      console.error("Error marking as read:", err);
    }
  };

  const addReaction = async (notificationId, reaction) => {
    try {
      await api.patch(`/notifications/${notificationId}/reaction`, { reaction });
      fetchData(); // Refresh data
    } catch (err) {
      console.error("Error adding reaction:", err);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  const getDaysUntilDue = (dueDate) => {
    const today = new Date();
    const due = new Date(dueDate);
    const diffTime = due - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl">Loading...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50 p-6">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="mb-10">
          <button
            onClick={() => navigate("/dashboard")}
            className="flex items-center text-indigo-600 hover:text-indigo-800 mb-6 group transition-all duration-200"
          >
            <ArrowLeft className="w-5 h-5 mr-2 group-hover:-translate-x-1 transition-transform" />
            Back to Dashboard
          </button>
          <div className="text-center">
            <h1 className="text-5xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent mb-4">
              Learning Hub
            </h1>
            <p className="text-xl text-gray-600">Your assignments and notifications in one place</p>
          </div>
        </div>

        {/* Tabs */}
        <div className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 mb-8 overflow-hidden">
          <div className="flex">
            <button
              onClick={() => setActiveTab("assignments")}
              className={`flex-1 px-8 py-6 font-semibold text-lg transition-all duration-300 ${
                activeTab === "assignments"
                  ? "bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg"
                  : "text-gray-600 hover:text-indigo-600 hover:bg-indigo-50"
              }`}
            >
              <FileText className="w-6 h-6 inline mr-3" />
              Assignments
              <span className="ml-2 px-3 py-1 bg-white/20 rounded-full text-sm">
                {assignments.length}
              </span>
            </button>
            <button
              onClick={() => setActiveTab("notifications")}
              className={`flex-1 px-8 py-6 font-semibold text-lg transition-all duration-300 ${
                activeTab === "notifications"
                  ? "bg-gradient-to-r from-indigo-600 to-purple-600 text-white shadow-lg"
                  : "text-gray-600 hover:text-indigo-600 hover:bg-indigo-50"
              }`}
            >
              <Bell className="w-6 h-6 inline mr-3" />
              Notifications
              <span className="ml-2 px-3 py-1 bg-white/20 rounded-full text-sm">
                {notifications.filter(n => !n.isRead).length} new
              </span>
            </button>
          </div>
        </div>

        {/* Content */}
        {activeTab === "assignments" ? (
          <div className="space-y-8">
            {assignments.length === 0 ? (
              <motion.div 
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="text-center py-20 bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg"
              >
                <div className="bg-gradient-to-r from-gray-100 to-gray-200 w-32 h-32 rounded-full flex items-center justify-center mx-auto mb-6">
                  <FileText className="w-16 h-16 text-gray-400" />
                </div>
                <h3 className="text-2xl font-semibold text-gray-700 mb-3">
                  No assignments yet
                </h3>
                <p className="text-gray-500 text-lg">
                  Assignments from your mentors will appear here
                </p>
              </motion.div>
            ) : (
              assignments.map((assignment, index) => {
                const daysUntilDue = getDaysUntilDue(assignment.dueDate);
                const isOverdue = daysUntilDue < 0;
                const isDueSoon = daysUntilDue <= 3 && daysUntilDue >= 0;

                return (
                  <motion.div
                    key={assignment._id}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 hover:shadow-2xl transition-all duration-300 hover:-translate-y-1 overflow-hidden"
                  >
                    <div className="p-8">
                      <div className="flex items-start justify-between mb-6">
                        <div className="flex-1">
                          <h3 className="text-2xl font-bold text-gray-900 mb-3 group-hover:text-indigo-600 transition-colors">
                            {assignment.title}
                          </h3>
                          <p className="text-gray-700 mb-6 text-lg leading-relaxed">
                            {assignment.description}
                          </p>
                          
                          <div className="flex items-center space-x-8 text-gray-600">
                            <div className="flex items-center bg-gradient-to-r from-blue-50 to-indigo-50 px-4 py-2 rounded-xl">
                              <User className="w-5 h-5 mr-2 text-indigo-500" />
                              <span className="font-medium">By: {assignment.createdBy?.name}</span>
                            </div>
                            <div className="flex items-center bg-gradient-to-r from-purple-50 to-pink-50 px-4 py-2 rounded-xl">
                              <Calendar className="w-5 h-5 mr-2 text-purple-500" />
                              <span className="font-medium">Assigned: {formatDate(assignment.createdAt)}</span>
                            </div>
                          </div>
                        </div>
                        
                        <div className="flex flex-col items-end space-y-4 ml-6">
                          <div className={`flex items-center px-6 py-3 rounded-xl text-sm font-bold shadow-lg ${
                            isOverdue
                              ? "bg-gradient-to-r from-red-500 to-pink-500 text-white"
                              : isDueSoon
                              ? "bg-gradient-to-r from-yellow-400 to-orange-400 text-white"
                              : "bg-gradient-to-r from-green-500 to-emerald-500 text-white"
                          }`}>
                            <Clock className="w-5 h-5 mr-2" />
                            {isOverdue
                              ? `‚ö†Ô∏è Overdue by ${Math.abs(daysUntilDue)} days`
                              : daysUntilDue === 0
                              ? "üìÖ Due today"
                              : `‚è±Ô∏è ${daysUntilDue} days left`
                            }
                          </div>
                          
                          <button
                            onClick={() => handleDownload(assignment._id, assignment.originalFilename)}
                            className="flex items-center px-6 py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-xl hover:from-indigo-700 hover:to-purple-700 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1 font-semibold"
                          >
                            <Download className="w-5 h-5 mr-2" />
                            Download PDF
                          </button>
                        </div>
                      </div>
                      
                      <div className="border-t-2 border-gray-100 pt-6">
                        <div className="flex items-center bg-gradient-to-r from-gray-50 to-gray-100 px-6 py-4 rounded-xl">
                          <Clock className="w-6 h-6 text-indigo-500 mr-3" />
                          <span className="text-lg font-semibold text-gray-700">
                            Due Date: {formatDate(assignment.dueDate)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </motion.div>
                );
              })
            )}
          </div>
        ) : (
          <div className="space-y-4">
            {notifications.length === 0 ? (
              <div className="text-center py-12">
                <Bell className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                <h3 className="text-xl font-medium text-gray-600 mb-2">
                  No notifications yet
                </h3>
                <p className="text-gray-500">
                  Notifications from your mentors will appear here
                </p>
              </div>
            ) : (
              notifications.map((notification, index) => (
                <motion.div
                  key={notification._id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className={`bg-white rounded-lg shadow-sm border hover:shadow-md transition-shadow ${
                    !notification.isRead ? "border-l-4 border-l-blue-500" : ""
                  }`}
                >
                  <div className="p-6">
                    <div className="flex items-start justify-between mb-4">
                      <div className="flex-1">
                        <div className="flex items-center mb-2">
                          <h3 className="text-lg font-semibold text-gray-900">
                            {notification.title}
                          </h3>
                          {!notification.isRead && (
                            <span className="ml-2 px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                              New
                            </span>
                          )}
                          <div className={`ml-2 px-2 py-1 rounded-full text-xs font-medium ${
                            notification.priority === "urgent"
                              ? "bg-red-100 text-red-800"
                              : notification.priority === "high"
                              ? "bg-orange-100 text-orange-800"
                              : notification.priority === "medium"
                              ? "bg-yellow-100 text-yellow-800"
                              : "bg-gray-100 text-gray-800"
                          }`}>
                            {notification.priority}
                          </div>
                        </div>
                        
                        <p className="text-gray-600 mb-4">
                          {notification.message}
                        </p>
                        
                        <div className="flex items-center space-x-6 text-sm text-gray-500">
                          <div className="flex items-center">
                            <User className="w-4 h-4 mr-1" />
                            From: {notification.createdBy?.name}
                          </div>
                          <div className="flex items-center">
                            <Calendar className="w-4 h-4 mr-1" />
                            {formatDate(notification.createdAt)}
                          </div>
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex items-center justify-between border-t pt-4">
                      <div className="flex items-center space-x-2">
                        <span className="text-sm text-gray-500">React:</span>
                        {["like", "love", "helpful", "confused"].map((reaction) => (
                          <button
                            key={reaction}
                            onClick={() => addReaction(notification._id, reaction)}
                            className={`px-2 py-1 rounded text-sm transition-colors ${
                              notification.userReaction === reaction
                                ? "bg-blue-100 text-blue-800"
                                : "bg-gray-100 text-gray-600 hover:bg-gray-200"
                            }`}
                          >
                            {reaction === "like" && "üëç"}
                            {reaction === "love" && "‚ù§Ô∏è"}
                            {reaction === "helpful" && "üí°"}
                            {reaction === "confused" && "üòï"}
                            <span className="ml-1 capitalize">{reaction}</span>
                          </button>
                        ))}
                      </div>
                      
                      {!notification.isRead && (
                        <button
                          onClick={() => markAsRead(notification._id)}
                          className="flex items-center px-3 py-1 bg-green-100 text-green-800 rounded-lg hover:bg-green-200 transition-colors text-sm"
                        >
                          <CheckCircle className="w-4 h-4 mr-1" />
                          Mark as Read
                        </button>
                      )}
                    </div>
                  </div>
                </motion.div>
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
}
