import React from "react";
import "./styles/TestCard.css";

export default function TestCard({ test, onTakeTest }) {
  const category = test.category ? test.category.toUpperCase() : "";
  let badgeClass = "bg-gray-200 text-gray-700";
  if (test.category === "mcq") badgeClass = "bg-blue-100 text-blue-800";
  else if (test.category === "long") badgeClass = "bg-yellow-100 text-yellow-800";
  else if (test.category === "coding") badgeClass = "bg-green-100 text-green-800";

  return (
    <div className="bg-white rounded-xl shadow-lg p-6 m-2 flex flex-col">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-xl font-bold">{test.name || "No Name"}</h2>
        <span className={`px-3 py-1 rounded-full text-xs font-semibold ${badgeClass}`}>
          {category}
        </span>
      </div>
      <div className="mb-2">Duration: {test.duration || "?"} min</div>
      <div className="mb-2">Total Marks: {test.totalMarks || "?"}</div>
      <div className="mb-2">Questions: {test.questions ? test.questions.length : "?"}</div>
      <button
        className="mt-auto bg-blue-600 text-white px-4 py-2 rounded-lg"
        onClick={() => onTakeTest(test)}
      >
        Take Test
      </button>
    </div>
  );
}
