import { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import { User, BarChart3, FileText, Award, Camera, Edit3, Save, X } from "lucide-react";
import CountUp from "react-countup";
import { motion } from "framer-motion";
import "../dashboard.css";

export default function DashboardStudent() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatarFile, setAvatarFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [stats, setStats] = useState({
    totalTests: 0,
    completedExams: 0,
    avgScore: 0
  });

  const navigate = useNavigate();

  useEffect(() => fetchMe(), []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  useEffect(() => {
    if (avatarFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(avatarFile);
    } else {
      setPreview(null);
    }
  }, [avatarFile]);

  const getAvatarUrl = (avatar) => {
    if (!avatar) return null;
    if (avatar.startsWith("http")) return avatar;
    return `http://localhost:5000${avatar}`;
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("student.")) {
      const key = name.split(".")[1];
      setForm({ ...form, student: { ...form.student, [key]: value } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const updateProfile = async () => {
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("email", form.email);
      if (form.student) {
        formData.append("educationLevel", form.student.educationLevel || "");
        formData.append("institutionName", form.student.institutionName || "");
        formData.append("contactNumber", form.student.contactNumber || "");
      }
      if (avatarFile) formData.append("avatar", avatarFile);

      const { data } = await api.put("/auth/me", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      localStorage.setItem("user", JSON.stringify(data.user));
      setEditing(false);
      fetchMe();
      setAvatarFile(null);
      setPreview(null);
    } catch (err) {
      console.error("Update failed:", err.response?.data || err.message);
    }
  };

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const { data } = await api.get("/student/stats");
        setStats(data);
      } catch (err) {
        console.error("Failed to fetch stats:", err);
      }
    };
    fetchStats();
  }, []);

  const studentActions = [
    { title: "My Tests", description: "View and attempt tests", icon: <FileText />, path: "/my-tests", color: "from-blue-500 to-blue-600" },
    { title: "My Scores", description: "Check your exam results", icon: <BarChart3 />, path: "/my-scores", color: "from-green-500 to-green-600" },
    { title: "Achievements", description: "View earned awards", icon: <Award />, path: "/achievements", color: "from-purple-500 to-purple-600" },
  ];

  return (
    <div className="dashboard-container grid grid-cols-1 lg:grid-cols-3 gap-6">
      {/* Left Column */}
      <div className="lg:col-span-2 space-y-6">
        {/* Stats Grid */}
        <div className="stats-grid grid grid-cols-1 sm:grid-cols-3 gap-4">
          {[ 
            { label: "Total Tests", value: stats.totalTests, icon: <FileText />, color: "bg-blue-500" },
            { label: "Completed Exams", value: stats.completedExams, icon: <Award />, color: "bg-purple-500" },
            { label: "Average Score", value: stats.avgScore, icon: <BarChart3 />, color: "bg-orange-500" }
          ].map((stat, index) => (
            <motion.div
              key={index}
              className="stat-card flex items-center p-4 rounded-xl shadow hover:shadow-lg transition"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2, duration: 0.6 }}
            >
              <div className={`stat-icon ${stat.color} p-3 rounded-full text-white mr-4`}>{stat.icon}</div>
              <div>
                <h3 className="text-xl font-bold">
                  <CountUp end={stat.value} duration={1.5} />
                  {stat.label === "Average Score" ? "%" : ""}
                </h3>
                <p className="text-gray-500">{stat.label}</p>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Quick Actions */}
        <div className="dashboard-section">
          <h2 className="section-title">Quick Actions</h2>
          <div className="actions-grid grid grid-cols-1 sm:grid-cols-3 gap-4">
            {studentActions.map((action, index) => (
              <motion.div
                key={index}
                className="action-card-modern p-4 rounded-xl shadow hover:shadow-lg transition cursor-pointer"
                onClick={() => navigate(action.path)}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.15, duration: 0.5 }}
              >
                <div className={`action-icon bg-gradient-to-r ${action.color} p-3 rounded-full text-white mb-2`}>{action.icon}</div>
                <h3 className="font-semibold">{action.title}</h3>
                <p className="text-gray-500 text-sm">{action.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </div>

      {/* Right Column - Profile */}
      <div className="profile-card-modern p-6 rounded-xl shadow-lg">
        <div className="profile-header flex flex-col items-center mb-4">
          <div className="avatar-container relative">
            <img
              src={preview || getAvatarUrl(user?.avatar) || "/default-avatar.png"}
              alt="Profile"
              className="profile-avatar w-24 h-24 rounded-full object-cover"
            />
            {editing && (
              <label className="avatar-upload absolute bottom-0 right-0 bg-purple-600 text-white p-1 rounded-full cursor-pointer">
                <Camera className="w-4 h-4" />
                <input type="file" accept="image/*" className="hidden" onChange={(e)=>setAvatarFile(e.target.files[0])} />
              </label>
            )}
          </div>
          <h3 className="mt-2 text-xl font-bold">{user?.name || "Student"}</h3>
          <p className="text-gray-500">Student</p>
          {!editing && (
            <button className="edit-btn mt-2 flex items-center text-purple-600 font-medium" onClick={()=>setEditing(true)}>
              <Edit3 className="w-4 h-4 mr-1" /> Edit Profile
            </button>
          )}
        </div>

        {!editing ? (
          <div className="profile-details space-y-2 text-gray-700">
            <div><b>Email:</b> {user?.email || "Not provided"}</div>
            <div><b>Education:</b> {user?.student?.educationLevel || "Not provided"}</div>
            <div><b>Institution:</b> {user?.student?.institutionName || "Not provided"}</div>
            <div><b>Contact:</b> {user?.student?.contactNumber || "Not provided"}</div>
          </div>
        ) : (
          <div className="profile-form space-y-3">
            <input name="name" value={form.name || ""} onChange={onChange} placeholder="Full Name" className="input-field" />
            <input name="student.educationLevel" value={form.student?.educationLevel || ""} onChange={onChange} placeholder="Education Level" className="input-field" />
            <input name="student.institutionName" value={form.student?.institutionName || ""} onChange={onChange} placeholder="Institution" className="input-field" />
            <input name="student.contactNumber" value={form.student?.contactNumber || ""} onChange={onChange} placeholder="Contact Number" className="input-field" />
            <div className="flex justify-between mt-2">
              <button className="btn-save flex items-center px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700" onClick={updateProfile}>
                <Save className="w-4 h-4 mr-1"/> Save
              </button>
              <button className="btn-cancel flex items-center px-4 py-2 border border-gray-300 rounded hover:bg-gray-100" onClick={()=>setEditing(false)}>
                <X className="w-4 h-4 mr-1"/> Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
