import React from "react";

export default function TestCard({ test, onTakeTest }) {
  return (
    <div className="bg-white rounded-xl shadow-lg p-6 m-2 flex flex-col">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-xl font-bold">{test.name}</h2>
        <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
          test.category === "mcq" ? "bg-blue-100 text-blue-800" :
          test.category === "long" ? "bg-yellow-100 text-yellow-800" :
          "bg-green-100 text-green-800"
        }`}>
          {test.category.toUpperCase()}
        </span>
      </div>
      <div className="mb-2">Duration: {test.duration} min</div>
      <div className="mb-2">Total Marks: {test.totalMarks}</div>
      <div className="mb-2">Questions: {test.questions.length}</div>
      <button
        className="mt-auto bg-blue-600 text-white px-4 py-2 rounded-lg"
        onClick={() => onTakeTest(test)}
      >
        Take Test
      </button>
    </div>
  );
}
