import { useContext, useState, useEffect } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";

export default function DashboardStudent() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatar, setAvatar] = useState("");

  useEffect(() => {
    fetchMe();
  }, []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const updateProfile = async () => {
    const { data } = await api.put("/auth/me", { ...form, avatar });
    localStorage.setItem("user", JSON.stringify(data.user));
    setEditing(false);
    fetchMe();
  };

  return (
    <div className="page-container">
      <h1>Student Dashboard</h1>
      {user?.avatar && <img src={user.avatar} alt="avatar" width={100} />}

      {!editing ? (
        <>
          <p>Welcome, {user?.name}</p>
          <ul>
            <li>Email: {user?.email}</li>
            <li>Education: {user?.student?.educationLevel}</li>
            <li>Institution: {user?.student?.institutionName}</li>
            <li>Contact: {user?.student?.contactNumber}</li>
          </ul>
          <button onClick={() => setEditing(true)}>Edit Profile</button>
        </>
      ) : (
        <div>
          <input name="name" value={form.name} onChange={onChange} />
          <input name="student.educationLevel" value={form.student?.educationLevel} onChange={onChange} />
          <input name="student.institutionName" value={form.student?.institutionName} onChange={onChange} />
          <input name="student.contactNumber" value={form.student?.contactNumber} onChange={onChange} />
          <input type="text" placeholder="Avatar URL" value={avatar} onChange={(e)=>setAvatar(e.target.value)} />
          <button onClick={updateProfile}>Save</button>
        </div>
      )}
    </div>
  );
}
