import React, { useState } from "react";
import axios from "axios";

export default function AddTest() {
  const [testType, setTestType] = useState(""); // MCQ, Long, Coding
  const [questions, setQuestions] = useState([]);
  const [currentQ, setCurrentQ] = useState({
    question: "",
    options: ["", "", "", ""],
    answer: "",
    marks: "",
    expectedOutput: "",
  });

  const handleAddQuestion = () => {
    if (!currentQ.question.trim()) {
      alert("Please enter the question!");
      return;
    }

    setQuestions([...questions, { ...currentQ, type: testType }]);

    // Reset current question form
    setCurrentQ({
      question: "",
      options: ["", "", "", ""],
      answer: "",
      marks: "",
      expectedOutput: "",
    });
  };

  const handleOptionChange = (index, value) => {
    const updatedOptions = [...currentQ.options];
    updatedOptions[index] = value;
    setCurrentQ({ ...currentQ, options: updatedOptions });
  };

  const handleSubmit = async () => {
    try {
      await axios.post("http://localhost:5000/api/tests", {
        type: testType,
        questions,
      });
      alert("Test saved successfully!");
      setTestType("");
      setQuestions([]);
    } catch (error) {
      console.error(error);
      alert("Error saving test!");
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Add Test</h1>

      {/* Select Test Type */}
      <label className="block mb-2 font-semibold">Select Test Type:</label>
      <select
        className="border p-2 rounded"
        value={testType}
        onChange={(e) => setTestType(e.target.value)}
      >
        <option value="">-- Select --</option>
        <option value="MCQ">MCQ</option>
        <option value="Long">Long Answer</option>
        <option value="Coding">Coding</option>
      </select>

      {/* Form for Questions */}
      {testType && (
        <div className="mt-4 border p-4 rounded">
          <label className="block mb-2 font-semibold">Question:</label>
          <textarea
            className="border p-2 w-full rounded"
            value={currentQ.question}
            onChange={(e) => setCurrentQ({ ...currentQ, question: e.target.value })}
          />

          {testType === "MCQ" && (
            <>
              <label className="block mt-2 font-semibold">Options:</label>
              {currentQ.options.map((opt, i) => (
                <input
                  key={i}
                  type="text"
                  placeholder={`Option ${i + 1}`}
                  className="border p-2 block w-full mb-2 rounded"
                  value={opt}
                  onChange={(e) => handleOptionChange(i, e.target.value)}
                />
              ))}

              <label className="block mt-2 font-semibold">Correct Answer:</label>
              <input
                type="text"
                placeholder="Correct Answer"
                className="border p-2 w-full rounded"
                value={currentQ.answer}
                onChange={(e) => setCurrentQ({ ...currentQ, answer: e.target.value })}
              />

              <label className="block mt-2 font-semibold">Marks:</label>
              <input
                type="number"
                className="border p-2 w-full rounded"
                value={currentQ.marks}
                onChange={(e) => setCurrentQ({ ...currentQ, marks: e.target.value })}
              />
            </>
          )}

          {testType === "Long" && (
            <>
              <label className="block mt-2 font-semibold">Marks:</label>
              <input
                type="number"
                className="border p-2 w-full rounded"
                value={currentQ.marks}
                onChange={(e) => setCurrentQ({ ...currentQ, marks: e.target.value })}
              />
            </>
          )}

          {testType === "Coding" && (
            <>
              <label className="block mt-2 font-semibold">Expected Output:</label>
              <input
                type="text"
                placeholder="Expected Output"
                className="border p-2 w-full rounded"
                value={currentQ.expectedOutput}
                onChange={(e) =>
                  setCurrentQ({ ...currentQ, expectedOutput: e.target.value })
                }
              />
            </>
          )}

          <button
            onClick={handleAddQuestion}
            className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
          >
            Add Question
          </button>
        </div>
      )}

      {/* Display Added Questions */}
      {questions.length > 0 && (
        <div className="mt-6">
          <h2 className="text-lg font-bold mb-2">Added Questions:</h2>
          <ul className="list-disc pl-6">
            {questions.map((q, index) => (
              <li key={index}>
                {q.type}: {q.question} ({q.marks} marks)
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Save Test */}
      {questions.length > 0 && (
        <button
          onClick={handleSubmit}
          className="mt-6 bg-green-600 text-white px-6 py-2 rounded"
        >
          Save Test
        </button>
      )}
    </div>
  );
}
