import { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import { 
  User, Plus, BarChart3, FileText, Users, Edit3, Save, X, Mail, Phone, Building, Award, Camera, Search, Trophy, Target, Eye
} from "lucide-react";
import CountUp from "react-countup";
import { motion } from "framer-motion";
import "../dashboard.css";

export default function DashboardMentor() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatarFile, setAvatarFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [stats, setStats] = useState({
    totalTests: 0,
    activeStudents: 0,
    completedExams: 0,
    totalAssignments: 0,
    totalNotifications: 0
  });
  const [studentResults, setStudentResults] = useState([]);
  const [selectedResult, setSelectedResult] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [loadingResults, setLoadingResults] = useState(false);

  const navigate = useNavigate();

  // Fetch user data
  useEffect(() => {
    fetchMe();
  }, []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  // Fetch dashboard stats
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const { data } = await api.get("/mentor/stats");
        setStats(data);
      } catch (err) {
        console.error("Failed to fetch stats:", err);
      }
    };
    fetchStats();
  }, []);

  // Fetch student results
  useEffect(() => {
    const fetchStudentResults = async () => {
      setLoadingResults(true);
      try {
        const { data } = await api.get("/submissions/all-for-mentor");
        if (data && Array.isArray(data)) {
          setStudentResults(data);
        }
      } catch (err) {
        console.error("Failed to fetch student results:", err);
        // Fallback: try to get from submissions endpoint
        try {
          const { data: allSubmissions } = await api.get("/submissions");
          if (allSubmissions && Array.isArray(allSubmissions)) {
            setStudentResults(allSubmissions);
          }
        } catch (fallbackErr) {
          console.error("Fallback fetch also failed:", fallbackErr);
        }
      } finally {
        setLoadingResults(false);
      }
    };
    fetchStudentResults();
  }, []);

  // Generate avatar preview
  useEffect(() => {
    if (avatarFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(avatarFile);
    } else {
      setPreview(null);
    }
  }, [avatarFile]);

  // Get full avatar URL
  const getAvatarUrl = (avatar) => {
    if (!avatar) return null;
    if (avatar.startsWith("http")) return avatar;
    return `http://localhost:5002${avatar}`; // Adjust port/backend path if needed
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("mentor.")) {
      const key = name.split(".")[1];
      setForm({ ...form, mentor: { ...form.mentor, [key]: value } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  // Update profile including avatar
  const updateProfile = async () => {
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("email", form.email);

      if (form.mentor) {
        formData.append("currentInstitution", form.mentor.currentInstitution || "");
        formData.append("phoneNumber", form.mentor.phoneNumber || "");
        formData.append("experience", form.mentor.experience || "");
      }

      if (avatarFile) {
        formData.append("avatar", avatarFile);
      }

      const { data } = await api.put("/auth/me", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      localStorage.setItem("user", JSON.stringify(data.user));
      setEditing(false);
      fetchMe();

      // Clear preview
      setAvatarFile(null);
      setPreview(null);
    } catch (err) {
      console.error("Update failed:", err.response?.data || err.message);
    }
  };

  // Helper functions for results
  const getColor = (percent) => {
    if (percent < 35) return "#ef4444"; // red-500
    if (percent <= 80) return "#f59e0b"; // amber-500
    return "#10b981"; // emerald-500
  };

  const getGrade = (percent) => {
    if (percent >= 90) return "A+";
    if (percent >= 80) return "A";
    if (percent >= 70) return "B+";
    if (percent >= 60) return "B";
    if (percent >= 50) return "C";
    if (percent >= 35) return "D";
    return "F";
  };

  const formatDate = (timestamp) => {
    if (!timestamp) return "N/A";
    try {
      const date = new Date(timestamp);
      return date.toLocaleDateString("en-US", {
        year: "numeric",
        month: "short",
        day: "numeric",
        hour: "2-digit",
        minute: "2-digit",
      });
    } catch {
      return timestamp;
    }
  };

  // Filter results based on search term
  const filteredResults = studentResults.filter(result => {
    const studentName = result.student?.name || result.studentName || "Unknown Student";
    const testName = result.test?.name || result.testName || "Unknown Test";
    return studentName.toLowerCase().includes(searchTerm.toLowerCase()) ||
           testName.toLowerCase().includes(searchTerm.toLowerCase());
  });

  const dashboardActions = [
    { title: "Add Test", description: "Create new examinations", icon: <Plus />, path: "/add-test", color: "from-blue-500 to-blue-600" },
    { title: "Assignments", description: "Manage assignments", icon: <FileText />, path: "/mentor/assignments", color: "from-indigo-500 to-indigo-600" },
    { title: "Notifications", description: "Send notifications", icon: <Mail />, path: "/mentor/notifications", color: "from-pink-500 to-pink-600" }
  ];

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <div className="header-content">
          <h1 className="dashboard-title">Mentor Dashboard</h1>
          <p className="dashboard-subtitle">Manage your tests and track student progress</p>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        {[
          { label: "Total Tests", value: stats.totalTests, icon: <FileText />, color: "bg-blue-500" },
          { label: "Active Students", value: stats.activeStudents, icon: <Users />, color: "bg-green-500" },
          { label: "Completed Exams", value: stats.completedExams, icon: <Award />, color: "bg-purple-500" },
          { label: "Assignments Given", value: stats.totalAssignments, icon: <FileText />, color: "bg-indigo-500" },
          { label: "Notifications Sent", value: stats.totalNotifications, icon: <Mail />, color: "bg-pink-500" }
        ].map((stat, index) => (
          <motion.div 
            key={index}
            className="stat-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.2, duration: 0.6 }}
          >
            <div className={`stat-icon ${stat.color}`}>{stat.icon}</div>
            <div className="stat-content">
              <h3 className="stat-number">
                <CountUp end={stat.value} duration={1.5} />
              </h3>
              <p className="stat-label">{stat.label}</p>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Main Dashboard */}
      <div className="dashboard-main">
        {/* Quick Actions */}
        <div className="dashboard-section">
          <h2 className="section-title">Quick Actions</h2>
          <div className="actions-grid">
            {dashboardActions.map((action, index) => (
              <motion.div
                key={index}
                className="action-card-modern"
                onClick={() => navigate(action.path)}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.15, duration: 0.5 }}
              >
                <div className={`action-icon bg-gradient-to-r ${action.color}`}>
                  {action.icon}
                </div>
                <div className="action-content">
                  <h3 className="action-title">{action.title}</h3>
                  <p className="action-description">{action.description}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>

        {/* View Results Section */}
        <div className="dashboard-section">
          <div className="flex justify-between items-center mb-6">
            <h2 className="section-title">Student Results</h2>
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search by student or test name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          {loadingResults ? (
            <div className="text-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
              <p className="text-gray-600">Loading results...</p>
            </div>
          ) : filteredResults.length === 0 ? (
            <div className="text-center py-8">
              <Trophy className="h-12 w-12 text-gray-300 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No results found</h3>
              <p className="text-gray-600">
                {searchTerm ? "No results match your search." : "No student results available yet."}
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredResults.map((result, index) => {
                const obtained = result.obtainedMarks || result.score?.obtainedMarks || 0;
                const total = result.totalMarks || result.score?.totalMarks || 0;
                const percent = total > 0 ? (obtained / total) * 100 : 0;
                const grade = getGrade(percent);
                const studentName = result.student?.name || result.studentName || "Unknown Student";
                const testName = result.test?.name || result.testName || "Unknown Test";
                
                return (
                  <motion.div
                    key={result._id}
                    className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-all duration-300 cursor-pointer transform hover:-translate-y-1"
                    style={{ borderTop: `4px solid ${getColor(percent)}` }}
                    onClick={() => setSelectedResult({ ...result, percent, grade, studentName, testName, obtained, total })}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1, duration: 0.3 }}
                  >
                    <div className="flex justify-between items-start mb-4">
                      <div className="flex-1">
                        <h3 className="font-bold text-gray-900 text-lg mb-1 truncate" title={studentName}>
                          {studentName}
                        </h3>
                        <p className="text-sm text-gray-600 truncate" title={testName}>{testName}</p>
                      </div>
                      <span 
                        className="px-3 py-1 rounded-full text-xs font-bold text-white whitespace-nowrap"
                        style={{ backgroundColor: getColor(percent) }}
                      >
                        {grade}
                      </span>
                    </div>
                    
                    <div className="space-y-2 mb-4">
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-600">Score:</span>
                        <span className="font-semibold" style={{ color: getColor(percent) }}>
                          {obtained}/{total} ({percent.toFixed(1)}%)
                        </span>
                      </div>
                      
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-600">Submitted:</span>
                        <span className="text-gray-700 font-medium">{formatDate(result.submittedAt || result.createdAt)}</span>
                      </div>
                    </div>
                    
                    <div className="w-full bg-gray-200 rounded-full h-2 mb-4">
                      <div 
                        className="h-2 rounded-full transition-all duration-500"
                        style={{ 
                          width: `${percent}%`, 
                          backgroundColor: getColor(percent) 
                        }}
                      ></div>
                    </div>
                    
                    <button
                      className="w-full flex items-center justify-center gap-2 py-2.5 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all duration-300 font-medium shadow-sm hover:shadow-md"
                      onClick={(e) => {
                        e.stopPropagation();
                        setSelectedResult({ ...result, percent, grade, studentName, testName, obtained, total });
                      }}
                    >
                      <Eye className="w-4 h-4" />
                      View Result
                    </button>
                  </motion.div>
                );
              })}
            </div>
          )}
        </div>

        {/* Profile Section */}
        <div className="dashboard-section">
          <h2 className="section-title">Profile Information</h2>
          <div className="profile-card-modern">
            <div className="profile-header">
              <div className="avatar-container">
                <img
                  src={preview || getAvatarUrl(user?.avatar) || "/default-avatar.png"}
                  alt="Profile"
                  className="profile-avatar"
                />
                {editing && (
                  <label className="avatar-upload">
                    <Camera className="w-4 h-4" />
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => setAvatarFile(e.target.files[0])}
                    />
                  </label>
                )}
              </div>
              <div className="profile-info">
                <h3 className="profile-name">{user?.name || "Mentor"}</h3>
                <p className="profile-role">Senior Mentor</p>
              </div>
              {!editing && (
                <button className="edit-btn" onClick={()=>setEditing(true)}>
                  <Edit3 className="w-4 h-4" /> Edit Profile
                </button>
              )}
            </div>

            {!editing ? (
              <div className="profile-details">
                <div className="detail-item"><Mail className="w-5 h-5 text-gray-400" /><span className="detail-label">Email:</span><span className="detail-value">{user?.email || "Not provided"}</span></div>
                <div className="detail-item"><Building className="w-5 h-5 text-gray-400" /><span className="detail-label">Institution:</span><span className="detail-value">{user?.mentor?.currentInstitution || "Not provided"}</span></div>
                <div className="detail-item"><Phone className="w-5 h-5 text-gray-400" /><span className="detail-label">Phone:</span><span className="detail-value">{user?.mentor?.phoneNumber || "Not provided"}</span></div>
                <div className="detail-item"><Award className="w-5 h-5 text-gray-400" /><span className="detail-label">Experience:</span><span className="detail-value">{user?.mentor?.experience || "Not provided"}</span></div>
              </div>
            ) : (
              <div className="profile-form">
                <div className="form-group">
                  <label className="form-label">Full Name</label>
                  <input name="name" value={form.name || ""} onChange={onChange} placeholder="Enter your full name" className="form-input" />
                </div>
                <div className="form-group">
                  <label className="form-label">Institution</label>
                  <input name="mentor.currentInstitution" value={form.mentor?.currentInstitution || ""} onChange={onChange} placeholder="Current institution" className="form-input" />
                </div>
                <div className="form-group">
                  <label className="form-label">Phone Number</label>
                  <input name="mentor.phoneNumber" value={form.mentor?.phoneNumber || ""} onChange={onChange} placeholder="Phone number" className="form-input" />
                </div>
                <div className="form-group">
                  <label className="form-label">Experience</label>
                  <input name="mentor.experience" value={form.mentor?.experience || ""} onChange={onChange} placeholder="Years of experience" className="form-input" />
                </div>
                <div className="form-actions">
                  <button className="btn-save" onClick={updateProfile}><Save className="w-4 h-4" /> Save Changes</button>
                  <button className="btn-cancel" onClick={()=>setEditing(false)}><X className="w-4 h-4" /> Cancel</button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Result Details Modal */}
      {selectedResult && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-xl shadow-2xl p-6 max-w-md w-full max-h-96 overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold text-gray-900">Test Result Details</h2>
              <button
                onClick={() => setSelectedResult(null)}
                className="text-gray-400 hover:text-gray-600 text-2xl font-bold"
              >
                
              </button>
            </div>
            
            <div className="space-y-4">
              <div>
                <h3 className="font-semibold text-gray-900 text-lg">
                  {selectedResult.studentName}
                </h3>
                <p className="text-sm text-gray-600">
                  Test: {selectedResult.testName}
                </p>
              </div>
              
              <div className="bg-gray-50 rounded-lg p-4">
                <div className="flex justify-between items-center mb-2">
                  <span className="text-gray-600">Score:</span>
                  <span 
                    className="font-bold text-lg"
                    style={{ color: getColor(selectedResult.percent) }}
                  >
                    {selectedResult.obtained}/{selectedResult.total}
                  </span>
                </div>
                
                <div className="flex justify-between items-center mb-2">
                  <span className="text-gray-600">Percentage:</span>
                  <span 
                    className="font-bold"
                    style={{ color: getColor(selectedResult.percent) }}
                  >
                    {selectedResult.percent.toFixed(1)}%
                  </span>
                </div>
                
                <div className="flex justify-between items-center">
                  <span className="text-gray-600">Grade:</span>
                  <span 
                    className="font-bold text-lg px-3 py-1 rounded text-white"
                    style={{ backgroundColor: getColor(selectedResult.percent) }}
                  >
                    {selectedResult.grade}
                  </span>
                </div>
              </div>
              
              <div>
                <p className="text-sm text-gray-600">
                  <strong>Submitted:</strong> {formatDate(selectedResult.submittedAt || selectedResult.createdAt)}
                </p>
              </div>
              
              <div className="border-t pt-4">
                <h4 className="font-semibold text-gray-900 mb-2">Performance Analysis</h4>
                <p className="text-sm text-gray-700">
                  {selectedResult.percent < 35
                    ? "Student performance needs significant improvement. Consider providing additional support and practice materials."
                    : selectedResult.percent <= 60
                    ? "Student shows basic understanding but needs improvement. Recommend focused practice on weak areas."
                    : selectedResult.percent <= 80
                    ? "Good performance! Student demonstrates solid understanding with room for improvement."
                    : "Excellent performance! Student shows strong mastery of the subject matter."
                  }
                </p>
              </div>
              
              <div className="flex justify-end pt-4">
                <button
                  onClick={() => setSelectedResult(null)}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
