import React, { useState, useEffect } from 'react';
import { X, Sparkles, Plus } from 'lucide-react';
import { generateAIQuestion } from '../services/aiService';

export default function AIQuestionModal({ isOpen, onClose, testType, onAddQuestion }) {
  const [aiQuestion, setAiQuestion] = useState({
    question: '',
    options: ['', '', '', ''],
    correctOption: 'A',
    marks: 1,
    expectedOutput: '',
  });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (isOpen) {
      fetchAIQuestion();
    }
  }, [isOpen, testType]);

  const fetchAIQuestion = async () => {
    setIsLoading(true);
    try {
      const generated = await generateAIQuestion(testType);
      setAiQuestion(generated);
    } catch (error) {
      console.error('Failed to generate AI question:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleOptionChange = (index, value) => {
    const updatedOptions = [...aiQuestion.options];
    updatedOptions[index] = value;
    setAiQuestion({ ...aiQuestion, options: updatedOptions });
  };

  const handleAddQuestion = () => {
    onAddQuestion(aiQuestion);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center">
              <Sparkles className="h-5 w-5 text-purple-600" />
            </div>
            <h2 className="text-xl font-semibold text-gray-900">AI Generated Question</h2>
          </div>
          <button
            onClick={onClose}
            className="p-2 text-gray-400 hover:text-gray-600 transition-colors duration-200"
          >
            <X className="h-5 w-5" />
          </button>
        </div>

        <div className="p-6">
          {isLoading ? (
            <div className="flex items-center justify-center py-12">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-purple-600"></div>
              <span className="ml-3 text-gray-600">Generating question...</span>
            </div>
          ) : (
            <div className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Question</label>
                <textarea
                  value={aiQuestion.question}
                  onChange={(e) => setAiQuestion({ ...aiQuestion, question: e.target.value })}
                  rows={4}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all duration-200 resize-none"
                />
              </div>

              {testType === 'MCQ' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-3">Options</label>
                  <div className="space-y-3">
                    {['A', 'B', 'C', 'D'].map((optionLabel, index) => (
                      <div key={optionLabel} className="flex items-center space-x-3">
                        <input
                          type="radio"
                          name="aiCorrectOption"
                          value={optionLabel}
                          checked={aiQuestion.correctOption === optionLabel}
                          onChange={(e) => setAiQuestion({ ...aiQuestion, correctOption: e.target.value })}
                          className="h-4 w-4 text-purple-600 focus:ring-purple-500 border-gray-300"
                        />
                        <span className="text-sm font-medium text-gray-700 w-6">{optionLabel}.</span>
                        <input
                          type="text"
                          value={aiQuestion.options[index]}
                          onChange={(e) => handleOptionChange(index, e.target.value)}
                          className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all duration-200"
                        />
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {(testType === 'MCQ' || testType === 'Long') && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Marks</label>
                  <input
                    type="number"
                    value={aiQuestion.marks}
                    onChange={(e) => setAiQuestion({ ...aiQuestion, marks: Number(e.target.value) })}
                    min="1"
                    max="20"
                    className="w-32 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all duration-200"
                  />
                </div>
              )}

              {testType === 'Coding' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Expected Output</label>
                  <textarea
                    value={aiQuestion.expectedOutput}
                    onChange={(e) => setAiQuestion({ ...aiQuestion, expectedOutput: e.target.value })}
                    rows={3}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all duration-200 resize-none"
                  />
                </div>
              )}

              <div className="flex space-x-4 pt-4">
                <button
                  onClick={handleAddQuestion}
                  className="flex-1 px-6 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-all duration-200 flex items-center justify-center space-x-2 font-medium"
                >
                  <Plus className="h-4 w-4" />
                  <span>Add Question</span>
                </button>
                <button
                  onClick={fetchAIQuestion}
                  className="px-6 py-3 border-2 border-purple-600 text-purple-600 rounded-lg hover:bg-purple-50 transition-all duration-200 flex items-center space-x-2 font-medium"
                >
                  <Sparkles className="h-4 w-4" />
                  <span>Regenerate</span>
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
