import { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import { User, BarChart3, FileText, Award, Camera, Edit3, Save, X } from "lucide-react";
import CountUp from "react-countup";
import { motion } from "framer-motion";
import "../dashboard.css";

export default function DashboardStudent() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatarFile, setAvatarFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [stats, setStats] = useState({
    totalTests: 0,
    completedExams: 0,
    avgScore: 0
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetchMe();
  }, []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  useEffect(() => {
    if (avatarFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(avatarFile);
    } else {
      setPreview(null);
    }
  }, [avatarFile]);

  const getAvatarUrl = (avatar) => {
    if (!avatar) return null;
    if (avatar.startsWith("http")) return avatar;
    return `http://localhost:5000${avatar}`;
  };

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("student.")) {
      const key = name.split(".")[1];
      setForm({ ...form, student: { ...form.student, [key]: value } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const updateProfile = async () => {
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("email", form.email);
      if (form.student) {
        formData.append("educationLevel", form.student.educationLevel || "");
        formData.append("institutionName", form.student.institutionName || "");
        formData.append("contactNumber", form.student.contactNumber || "");
      }
      if (avatarFile) formData.append("avatar", avatarFile);

      const { data } = await api.put("/auth/me", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      localStorage.setItem("user", JSON.stringify(data.user));
      setEditing(false);
      fetchMe();
      setAvatarFile(null);
      setPreview(null);
    } catch (err) {
      console.error("Update failed:", err.response?.data || err.message);
    }
  };

  // Fetch student stats
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const { data } = await api.get("/student/stats");
        setStats(data);
      } catch (err) {
        console.error("Failed to fetch stats:", err);
      }
    };
    fetchStats();
  }, []);

  const studentActions = [
    { title: "My Tests", description: "View and attempt tests", icon: <FileText />, path: "/my-tests", color: "from-blue-500 to-blue-600" },
    { title: "My Scores", description: "Check your exam results", icon: <BarChart3 />, path: "/my-scores", color: "from-green-500 to-green-600" },
    { title: "Achievements", description: "View earned awards", icon: <Award />, path: "/achievements", color: "from-purple-500 to-purple-600" },
  ];

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <div className="header-content">
          <h1 className="dashboard-title">Student Dashboard</h1>
          <p className="dashboard-subtitle">Track your tests, scores, and profile</p>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        {[
          { label: "Total Tests", value: stats.totalTests, icon: <FileText />, color: "bg-blue-500" },
          { label: "Completed Exams", value: stats.completedExams, icon: <Award />, color: "bg-purple-500" },
          { label: "Average Score", value: stats.avgScore, icon: <BarChart3 />, color: "bg-orange-500" }
        ].map((stat, index) => (
          <motion.div 
            key={index}
            className="stat-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.2, duration: 0.6 }}
          >
            <div className={`stat-icon ${stat.color}`}>{stat.icon}</div>
            <div className="stat-content">
              <h3 className="stat-number">
                <CountUp end={stat.value} duration={1.5} />
                {stat.label === "Average Score" ? "%" : ""}
              </h3>
              <p className="stat-label">{stat.label}</p>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Quick Actions */}
      <div className="dashboard-section">
        <h2 className="section-title">Quick Actions</h2>
        <div className="actions-grid">
          {studentActions.map((action, index) => (
            <motion.div
              key={index}
              className="action-card-modern"
              onClick={() => navigate(action.path)}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.15, duration: 0.5 }}
            >
              <div className={`action-icon bg-gradient-to-r ${action.color}`}>{action.icon}</div>
              <div className="action-content">
                <h3 className="action-title">{action.title}</h3>
                <p className="action-description">{action.description}</p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>

      {/* Profile Section */}
      <div className="dashboard-section">
        <h2 className="section-title">Profile Information</h2>
        <div className="profile-card-modern">
          <div className="profile-header">
            <div className="avatar-container">
              <img
                src={preview || getAvatarUrl(user?.avatar) || "/default-avatar.png"}
                alt="Profile"
                className="profile-avatar"
              />
              {editing && (
                <label className="avatar-upload">
                  <Camera className="w-4 h-4" />
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => setAvatarFile(e.target.files[0])}
                  />
                </label>
              )}
            </div>
            <div className="profile-info">
              <h3 className="profile-name">{user?.name || "Student"}</h3>
              <p className="profile-role">Student</p>
            </div>
            {!editing && (
              <button className="edit-btn" onClick={()=>setEditing(true)}>
                <Edit3 className="w-4 h-4" /> Edit Profile
              </button>
            )}
          </div>

          {!editing ? (
            <div className="profile-details">
              <div>Email: {user?.email || "Not provided"}</div>
              <div>Education: {user?.student?.educationLevel || "Not provided"}</div>
              <div>Institution: {user?.student?.institutionName || "Not provided"}</div>
              <div>Contact: {user?.student?.contactNumber || "Not provided"}</div>
            </div>
          ) : (
            <div className="profile-form">
              <input name="name" value={form.name || ""} onChange={onChange} placeholder="Full Name" />
              <input name="student.educationLevel" value={form.student?.educationLevel || ""} onChange={onChange} placeholder="Education Level" />
              <input name="student.institutionName" value={form.student?.institutionName || ""} onChange={onChange} placeholder="Institution" />
              <input name="student.contactNumber" value={form.student?.contactNumber || ""} onChange={onChange} placeholder="Contact Number" />
              <div className="form-actions">
                <button className="btn-save" onClick={updateProfile}><Save className="w-4 h-4" /> Save Changes</button>
                <button className="btn-cancel" onClick={()=>setEditing(false)}><X className="w-4 h-4" /> Cancel</button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
