import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import {
  Bell,
  Send,
  Users,
  ArrowLeft,
  Plus,
  Trash2,
  Calendar,
  AlertTriangle,
  Info,
  MessageCircle,
  Heart,
  ThumbsUp,
  Lightbulb,
  Frown,
  Clock,
  CheckCircle
} from "lucide-react";
import { motion } from "framer-motion";

export default function MentorNotifications() {
  const [notifications, setNotifications] = useState([]);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newNotification, setNewNotification] = useState({
    title: "",
    message: "",
    type: "general",
    priority: "medium",
    recipients: [],
    expiresAt: ""
  });
  const [submitting, setSubmitting] = useState(false);
  const [stats, setStats] = useState({
    totalNotifications: 0,
    totalReactions: 0,
    readRate: 0,
    reactionRate: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [notificationsRes, studentsRes, statsRes] = await Promise.all([
        api.get("/notifications/mentor"),
        api.get("/admin/users?role=student"), // Assuming this endpoint exists
        api.get("/notifications/stats")
      ]);
      
      setNotifications(notificationsRes.data);
      setStudents(studentsRes.data || []);
      setStats(statsRes.data);
    } catch (err) {
      console.error("Error fetching data:", err);
      setStudents([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateNotification = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    
    try {
      await api.post("/notifications", {
        ...newNotification,
        expiresAt: newNotification.expiresAt || null
      });

      setShowCreateModal(false);
      setNewNotification({
        title: "",
        message: "",
        type: "general",
        priority: "medium",
        recipients: [],
        expiresAt: ""
      });
      fetchData();
      alert("Notification sent successfully!");
    } catch (err) {
      console.error("Error creating notification:", err);
      alert("Failed to send notification: " + (err.response?.data?.message || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  const handleDeleteNotification = async (notificationId) => {
    if (!window.confirm("Are you sure you want to delete this notification?")) {
      return;
    }

    try {
      await api.delete(`/notifications/${notificationId}`);
      fetchData();
      alert("Notification deleted successfully!");
    } catch (err) {
      console.error("Error deleting notification:", err);
      alert("Failed to delete notification");
    }
  };

  const handleStudentToggle = (studentId) => {
    setNewNotification(prev => ({
      ...prev,
      recipients: prev.recipients.includes(studentId)
        ? prev.recipients.filter(id => id !== studentId)
        : [...prev.recipients, studentId]
    }));
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  const getReactionIcon = (reaction) => {
    switch (reaction) {
      case "like": return <ThumbsUp className="w-4 h-4" />;
      case "love": return <Heart className="w-4 h-4" />;
      case "helpful": return <Lightbulb className="w-4 h-4" />;
      case "confused": return <Frown className="w-4 h-4" />;
      default: return null;
    }
  };

  const getReactionCounts = (notification) => {
    const counts = { like: 0, love: 0, helpful: 0, confused: 0 };
    notification.recipients?.forEach(recipient => {
      if (recipient.reaction) {
        counts[recipient.reaction]++;
      }
    });
    return counts;
  };

  const getReadCount = (notification) => {
    return notification.recipients?.filter(r => r.readAt).length || 0;
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl">Loading notifications...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => navigate("/dashboard")}
            className="flex items-center text-blue-600 hover:text-blue-800 mb-4"
          >
            <ArrowLeft className="w-5 h-5 mr-2" />
            Back to Dashboard
          </button>
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Notification Management</h1>
              <p className="text-gray-600 mt-2">Send and manage notifications for your students</p>
            </div>
            <button
              onClick={() => setShowCreateModal(true)}
              className="flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              <Plus className="w-5 h-5 mr-2" />
              New Notification
            </button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="bg-blue-100 p-3 rounded-lg">
                <Bell className="w-6 h-6 text-blue-600" />
              </div>
              <div className="ml-4">
                <h3 className="text-2xl font-bold text-gray-900">{stats.totalNotifications}</h3>
                <p className="text-gray-600">Total Sent</p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="bg-green-100 p-3 rounded-lg">
                <CheckCircle className="w-6 h-6 text-green-600" />
              </div>
              <div className="ml-4">
                <h3 className="text-2xl font-bold text-gray-900">{stats.readRate}%</h3>
                <p className="text-gray-600">Read Rate</p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="bg-purple-100 p-3 rounded-lg">
                <Heart className="w-6 h-6 text-purple-600" />
              </div>
              <div className="ml-4">
                <h3 className="text-2xl font-bold text-gray-900">{stats.totalReactions}</h3>
                <p className="text-gray-600">Total Reactions</p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-lg shadow-sm p-6">
            <div className="flex items-center">
              <div className="bg-orange-100 p-3 rounded-lg">
                <MessageCircle className="w-6 h-6 text-orange-600" />
              </div>
              <div className="ml-4">
                <h3 className="text-2xl font-bold text-gray-900">{stats.reactionRate}%</h3>
                <p className="text-gray-600">Reaction Rate</p>
              </div>
            </div>
          </div>
        </div>

        {/* Notifications List */}
        <div className="bg-white rounded-lg shadow-sm">
          <div className="p-6 border-b">
            <h2 className="text-xl font-semibold text-gray-900">Your Notifications</h2>
          </div>
          
          {notifications.length === 0 ? (
            <div className="text-center py-12">
              <Bell className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-xl font-medium text-gray-600 mb-2">No notifications yet</h3>
              <p className="text-gray-500 mb-6">Send your first notification to students</p>
              <button
                onClick={() => setShowCreateModal(true)}
                className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
              >
                Send Notification
              </button>
            </div>
          ) : (
            <div className="divide-y">
              {notifications.map((notification, index) => {
                const reactionCounts = getReactionCounts(notification);
                const readCount = getReadCount(notification);
                const totalRecipients = notification.recipients?.length || 0;

                return (
                  <motion.div
                    key={notification._id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="p-6 hover:bg-gray-50"
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center mb-2">
                          <h3 className="text-lg font-semibold text-gray-900 mr-3">
                            {notification.title}
                          </h3>
                          <div className={`px-2 py-1 rounded-full text-xs font-medium mr-2 ${
                            notification.type === "general"
                              ? "bg-blue-100 text-blue-800"
                              : notification.type === "assignment"
                              ? "bg-green-100 text-green-800"
                              : notification.type === "test"
                              ? "bg-purple-100 text-purple-800"
                              : "bg-orange-100 text-orange-800"
                          }`}>
                            {notification.type}
                          </div>
                          <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                            notification.priority === "urgent"
                              ? "bg-red-100 text-red-800"
                              : notification.priority === "high"
                              ? "bg-orange-100 text-orange-800"
                              : notification.priority === "medium"
                              ? "bg-yellow-100 text-yellow-800"
                              : "bg-gray-100 text-gray-800"
                          }`}>
                            {notification.priority}
                          </div>
                        </div>
                        
                        <p className="text-gray-600 mb-4">{notification.message}</p>
                        
                        <div className="flex items-center space-x-6 text-sm text-gray-500 mb-4">
                          <div className="flex items-center">
                            <Calendar className="w-4 h-4 mr-1" />
                            Sent: {formatDate(notification.createdAt)}
                          </div>
                          <div className="flex items-center">
                            <Users className="w-4 h-4 mr-1" />
                            {totalRecipients} recipients
                          </div>
                          <div className="flex items-center">
                            <CheckCircle className="w-4 h-4 mr-1" />
                            {readCount}/{totalRecipients} read
                          </div>
                          {notification.expiresAt && (
                            <div className="flex items-center">
                              <Clock className="w-4 h-4 mr-1" />
                              Expires: {formatDate(notification.expiresAt)}
                            </div>
                          )}
                        </div>

                        {/* Reactions */}
                        <div className="flex items-center space-x-4">
                          <span className="text-sm text-gray-500">Reactions:</span>
                          {Object.entries(reactionCounts).map(([reaction, count]) => (
                            count > 0 && (
                              <div key={reaction} className="flex items-center space-x-1">
                                {getReactionIcon(reaction)}
                                <span className="text-sm text-gray-600">{count}</span>
                              </div>
                            )
                          ))}
                          {Object.values(reactionCounts).every(count => count === 0) && (
                            <span className="text-sm text-gray-400">No reactions yet</span>
                          )}
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={() => handleDeleteNotification(notification._id)}
                          className="p-2 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                          title="Delete Notification"
                        >
                          <Trash2 className="w-5 h-5" />
                        </button>
                      </div>
                    </div>
                  </motion.div>
                );
              })}
            </div>
          )}
        </div>

        {/* Create Notification Modal */}
        {showCreateModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6 border-b">
                <h2 className="text-2xl font-bold text-gray-900">Send New Notification</h2>
              </div>
              
              <form onSubmit={handleCreateNotification} className="p-6 space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Title *
                  </label>
                  <input
                    type="text"
                    value={newNotification.title}
                    onChange={(e) => setNewNotification(prev => ({ ...prev, title: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter notification title"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Message *
                  </label>
                  <textarea
                    value={newNotification.message}
                    onChange={(e) => setNewNotification(prev => ({ ...prev, message: e.target.value }))}
                    rows={4}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter notification message"
                    required
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Type
                    </label>
                    <select
                      value={newNotification.type}
                      onChange={(e) => setNewNotification(prev => ({ ...prev, type: e.target.value }))}
                      className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="general">General</option>
                      <option value="assignment">Assignment</option>
                      <option value="test">Test</option>
                      <option value="announcement">Announcement</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Priority
                    </label>
                    <select
                      value={newNotification.priority}
                      onChange={(e) => setNewNotification(prev => ({ ...prev, priority: e.target.value }))}
                      className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                      <option value="urgent">Urgent</option>
                    </select>
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Expires At (optional)
                  </label>
                  <input
                    type="datetime-local"
                    value={newNotification.expiresAt}
                    onChange={(e) => setNewNotification(prev => ({ ...prev, expiresAt: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Send to Students (optional)
                  </label>
                  <div className="max-h-32 overflow-y-auto border border-gray-300 rounded-lg p-3">
                    {students.length === 0 ? (
                      <p className="text-gray-500 text-sm">No students available</p>
                    ) : (
                      <div className="space-y-2">
                        {students.map(student => (
                          <label key={student._id} className="flex items-center">
                            <input
                              type="checkbox"
                              checked={newNotification.recipients.includes(student._id)}
                              onChange={() => handleStudentToggle(student._id)}
                              className="mr-2"
                            />
                            <span className="text-sm">{student.name} ({student.email})</span>
                          </label>
                        ))}
                      </div>
                    )}
                  </div>
                  <p className="text-sm text-gray-500 mt-1">
                    Leave empty to send to all students
                  </p>
                </div>

                <div className="flex justify-end space-x-4 pt-4 border-t">
                  <button
                    type="button"
                    onClick={() => {
                      setShowCreateModal(false);
                      setNewNotification({
                        title: "",
                        message: "",
                        type: "general",
                        priority: "medium",
                        recipients: [],
                        expiresAt: ""
                      });
                    }}
                    className="px-6 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={submitting}
                    className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
                  >
                    {submitting ? "Sending..." : "Send Notification"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
