import axios from "axios";

/**
 * Generate AI-based questions dynamically
 * 
 * @param {Object} params
 * @param {string} params.testType - "MCQ" | "Long" | "Coding"
 * @param {string} params.topic - subject/topic for questions (e.g., "JavaScript")
 * @param {number} params.totalQuestions - number of questions to generate
 * @returns {Array} questions
 */
export const generateAIQuestion = async ({ testType, topic, totalQuestions }) => {
  try {
    // Call your backend AI endpoint (Node.js will talk to OpenAI or similar)
    const response = await axios.post("http://localhost:5000/api/ai/generate", {
      testType,
      topic,
      totalQuestions
    });

    return response.data.questions; // expected array of questions
  } catch (error) {
    console.error("AI generation failed:", error.message);
    return [];
  }
};
