import { useEffect, useState, useRef } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock, Play, Send } from "lucide-react";
import api from "../utils/api";

const INSTRUCTIONS = [
  "• Do not switch tabs, minimize, copy, paste, or take screenshots.",
  "• Your webcam will be monitored during the test.",
  "• You will receive up to 3 warnings for violations. On the 3rd, the test will auto-submit.",
  "• Answer all questions before submitting. Timer is at the top right.",
  "• For coding questions, select your language and write code in the editor.",
];

const LANGUAGES = ["python", "javascript", "java", "cpp"];

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [violationReason, setViolationReason] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedLang, setSelectedLang] = useState(LANGUAGES[0]);
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState(null);
  const [warnings, setWarnings] = useState([]);
  const [cameraError, setCameraError] = useState(false);
  const [loading, setLoading] = useState(!test);

  const videoRef = useRef(null);

  // Fetch test data if not provided via state
  useEffect(() => {
    if (!test && id) {
      const fetchTest = async () => {
        try {
          setLoading(true);
          const res = await api.get(`/tests/${id}`);
          console.log("Fetched test data:", res.data);
          setTest(res.data);
          setTimeLeft((res.data.duration || 60) * 60);
        } catch (err) {
          console.error("Failed to fetch test:", err);
          alert("Failed to fetch test!");
          navigate("/my-tests");
        } finally {
          setLoading(false);
        }
      };
      fetchTest();
    } else if (test) {
      setTimeLeft((test.duration || 60) * 60);
    }
  }, [id, test, navigate]);

  // Monitor camera stream tracks for stop event
  useEffect(() => {
    if (!cameraStream) return;

    const videoTracks = cameraStream.getVideoTracks();
    if (videoTracks.length === 0) {
      setCameraError(true);
      alert("Camera is not available or has been closed. Test cannot start.");
      return;
    }

    const handleTrackEnded = () => {
      setCameraError(true);
      alert("Camera has been turned off or disconnected. Test will be submitted.");
      setViolationReason("Camera disconnected");
      handleSubmit("Test ended due to camera disconnection");
    };

    videoTracks.forEach((track) => {
      track.addEventListener("ended", handleTrackEnded);
    });

    return () => {
      videoTracks.forEach((track) => {
        track.removeEventListener("ended", handleTrackEnded);
      });
    };
  }, [cameraStream]);

  // Start test: fullscreen + camera
  const startTest = async () => {
    if (cameraError) {
      alert("Cannot start test because camera is not accessible.");
      return;
    }
    setStarted(true);
    
    // Request fullscreen
    const elem = document.documentElement;
    if (elem.requestFullscreen) {
      try {
        await elem.requestFullscreen();
      } catch (e) {
        console.log("Fullscreen not supported");
      }
    }
    
    // Start camera
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 320, height: 240 },
      });
      setCameraStream(stream);
      if (videoRef.current) videoRef.current.srcObject = stream;
      setCameraError(false);
    } catch (e) {
      console.error("Camera access failed:", e);
      alert("Camera access is required for this test!");
      setCameraError(true);
    }
  };

  // Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            handleSubmit("Time's up!");
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  // Violation detection
  useEffect(() => {
    if (!started) return;

    const handleViolation = (message) => {
      const newViolationCount = violationCount + 1;
      setViolationCount(newViolationCount);
      setWarnings((prev) => [...prev, message]);

      if (newViolationCount >= 3) {
        setViolationReason(message);
        handleSubmit(`Test ended due to violations (3/3): ${message}`);
      } else {
        alert(`⚠️ Warning ${newViolationCount}/3: ${message}`);
      }
    };

    const handleCopy = (e) => {
      e.preventDefault();
      handleViolation("Copying is not allowed!");
    };

    const handlePaste = (e) => {
      e.preventDefault();
      handleViolation("Pasting is not allowed!");
    };

    const handleKeyDown = (e) => {
      if (
        e.key === "PrintScreen" ||
        (e.metaKey && e.shiftKey && (e.key === "4" || e.key === "5")) ||
        (e.ctrlKey && e.shiftKey && e.key === "S")
      ) {
        e.preventDefault();
        handleViolation("Screenshots are not allowed!");
      }
      if (e.key === "F12" || (e.ctrlKey && e.shiftKey && e.key === "I")) {
        e.preventDefault();
        handleViolation("Developer tools are not allowed!");
      }
    };

    const handleVisibilityChange = () => {
      if (document.hidden) {
        handleViolation("Switching tabs is not allowed!");
      }
    };

    const handleWindowBlur = () => {
      handleViolation("Minimizing or switching applications is not allowed!");
    };

    const handleContextMenu = (e) => {
      e.preventDefault();
      handleViolation("Right-click is disabled!");
    };

    document.addEventListener("copy", handleCopy);
    document.addEventListener("paste", handlePaste);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("visibilitychange", handleVisibilityChange);
    document.addEventListener("contextmenu", handleContextMenu);
    window.addEventListener("blur", handleWindowBlur);

    return () => {
      document.removeEventListener("copy", handleCopy);
      document.removeEventListener("paste", handlePaste);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
      document.removeEventListener("contextmenu", handleContextMenu);
      window.removeEventListener("blur", handleWindowBlur);
    };
  }, [started, violationCount]);

  // Answer change
  const handleAnswerChange = (questionId, value) => {
    setAnswers((prev) => ({
      ...prev,
      [questionId]: value,
    }));
  };

  // Submit test
  const handleSubmit = async (reason = "Test completed") => {
    if (isSubmitting) return;
    setIsSubmitting(true);

    try {
      const res = await api.post(`/tests/${id}/submit`, {
        answers,
        submissionReason: reason,
        violationReason,
      });

      if (cameraStream) {
        cameraStream.getTracks().forEach((track) => track.stop());
      }
      if (document.fullscreenElement) {
        document.exitFullscreen();
      }

      setResultData({
        score: res.data?.score,
        reason,
        violation: violationReason,
      });
      setShowResult(true);

      setTimeout(() => {
        navigate("/my-tests");
      }, 3000);
    } catch (err) {
      console.error("Submit error:", err);
      alert("Error submitting test. Please try again.");
      setIsSubmitting(false);
    }
  };

  // Run code (mock function for coding questions)
  const handleRunCode = () => {
    alert("Code execution feature will be implemented. For now, please submit your solution.");
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  // Loading state
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-xl text-gray-600">Loading test...</div>
      </div>
    );
  }

  // No test found
  if (!test) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-xl text-red-600">Test not found!</div>
      </div>
    );
  }

  const questionsArr = Array.isArray(test?.questions) ? test.questions : [];
  const answeredCount = questionsArr.filter((q) => answers[q._id]).length;
  const notAnsweredCount = questionsArr.length - answeredCount;
  const allAnswered = answeredCount === questionsArr.length;

  // Instructions page
  if (!started) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-2xl w-full">
          <h1 className="text-3xl font-bold text-gray-800 mb-6 text-center">{test.name}</h1>

          <div className="mb-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">Test Instructions</h2>
            <ul className="list-disc list-inside text-gray-600 space-y-2">
              {INSTRUCTIONS.map((inst, i) => (
                <li key={i} className="text-sm">{inst}</li>
              ))}
            </ul>
          </div>

          <div className="grid grid-cols-2 gap-4 mb-6">
            <div className="bg-gray-50 p-4 rounded-lg">
              <span className="font-semibold text-gray-700">Duration:</span>
              <p className="text-2xl font-bold text-blue-600">{test?.duration || 0} minutes</p>
            </div>
            <div className="bg-gray-50 p-4 rounded-lg">
              <span className="font-semibold text-gray-700">Total Marks:</span>
              <p className="text-2xl font-bold text-green-600">{test?.totalMarks || 0}</p>
            </div>
            <div className="bg-gray-50 p-4 rounded-lg">
              <span className="font-semibold text-gray-700">Questions:</span>
              <p className="text-2xl font-bold text-purple-600">{questionsArr.length}</p>
            </div>
            <div className="bg-gray-50 p-4 rounded-lg">
              <span className="font-semibold text-gray-700">Category:</span>
              <p className="text-lg font-bold text-orange-600 capitalize">{test?.category || "Mixed"}</p>
            </div>
          </div>

          <button
            onClick={startTest}
            disabled={cameraError}
            className={`w-full py-4 rounded-xl text-lg font-bold shadow-lg transition duration-200 ${
              cameraError
                ? "bg-gray-400 cursor-not-allowed"
                : "bg-blue-600 text-white hover:bg-blue-700"
            }`}
          >
            Start Test
          </button>
          {cameraError && (
            <p className="mt-4 text-center text-red-600 font-semibold">
              Camera is not accessible. Please enable your camera to start the test.
            </p>
          )}
        </div>
      </div>
    );
  }

  // Test interface
  const currentQuestion = questionsArr[currentQuestionIndex];
  const isCoding = currentQuestion?.type === "coding";
  const isLongAnswer = currentQuestion?.type === "long";

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Left Sidebar - Questions */}
      <div className="bg-white shadow-lg flex flex-col w-80">
        <div className="p-6 border-b">
          <h3 className="text-lg font-semibold text-gray-800 mb-4">Questions</h3>
          <div className="grid grid-cols-5 gap-2">
            {questionsArr.map((q, idx) => {
              const isAnswered = !!answers[q._id];
              const isCurrent = idx === currentQuestionIndex;
              return (
                <button
                  key={q._id}
                  onClick={() => setCurrentQuestionIndex(idx)}
                  className={`w-10 h-10 rounded-full border-2 flex items-center justify-center text-sm font-bold transition-all duration-200 ${
                    isCurrent
                      ? "border-blue-500 bg-blue-500 text-white"
                      : isAnswered
                      ? "border-green-500 bg-green-50 text-green-700"
                      : "border-gray-300 hover:border-gray-400"
                  }`}
                  title={isAnswered ? "Answered" : "Not Answered"}
                >
                  {idx + 1}
                </button>
              );
            })}
          </div>
        </div>

        <div className="p-6">
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium text-gray-600">Progress:</span>
              <span className="text-sm font-bold text-gray-800">{answeredCount}/{questionsArr.length}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                style={{ width: `${questionsArr.length > 0 ? (answeredCount / questionsArr.length) * 100 : 0}%` }}
              ></div>
            </div>
            <div className="flex justify-between text-xs">
              <span className="text-green-600 font-medium">Answered: {answeredCount}</span>
              <span className="text-gray-500">Remaining: {notAnsweredCount}</span>
            </div>
          </div>
        </div>

        {warnings.length > 0 && (
          <div className="p-4 mx-4 mb-4 bg-red-50 border border-red-200 rounded-lg">
            <h4 className="text-sm font-semibold text-red-800 mb-2">Warnings ({violationCount}/3):</h4>
            <div className="space-y-1">
              {warnings.map((warning, idx) => (
                <p key={idx} className="text-xs text-red-600">{warning}</p>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col">
        {/* Top Right - Timer and Webcam */}
        <div className="absolute top-6 right-6 z-10 flex flex-col items-end space-y-4">
          <div
            className={`flex items-center px-4 py-2 rounded-lg font-bold shadow-lg ${
              timeLeft <= 300 
                ? "bg-red-100 text-red-800 border border-red-300" 
                : "bg-blue-100 text-blue-800 border border-blue-300"
            }`}
          >
            <Clock className="mr-2" size={20} />
            {formatTime(timeLeft)}
          </div>
          <video
            ref={videoRef}
            autoPlay
            playsInline
            muted
            className="w-32 h-24 bg-black rounded-lg border-2 border-blue-400 shadow-md"
          />
        </div>

        {/* Question Content */}
        <div className="flex-1 p-8 pr-48 min-w-0">
          {isCoding ? (
            <div className="h-full flex gap-6">
              {/* Problem Statement */}
              <div className="w-1/2 bg-white rounded-xl shadow-lg p-6 overflow-y-auto min-w-0">
                <h2 className="text-xl font-bold mb-4 text-gray-800">
                  Problem {currentQuestionIndex + 1}
                </h2>
                <div className="prose prose-sm max-w-none">
                  <p className="text-gray-700 mb-4">{currentQuestion?.question}</p>
                  {currentQuestion?.sampleInput && (
                    <div className="mb-4">
                      <h4 className="font-semibold text-gray-800">Sample Input:</h4>
                      <pre className="bg-gray-100 p-2 rounded text-sm">{currentQuestion.sampleInput}</pre>
                    </div>
                  )}
                  {currentQuestion?.sampleOutput && (
                    <div className="mb-4">
                      <h4 className="font-semibold text-gray-800">Sample Output:</h4>
                      <pre className="bg-gray-100 p-2 rounded text-sm">{currentQuestion.sampleOutput}</pre>
                    </div>
                  )}
                  <div className="text-sm text-gray-500">
                    <strong>Marks:</strong> {currentQuestion?.marks || 0}
                  </div>
                </div>
              </div>

              {/* Code Editor */}
              <div className="w-1/2 bg-white rounded-xl shadow-lg p-6 flex flex-col min-w-0">
                <div className="flex items-center gap-4 mb-4">
                  <label className="font-semibold text-gray-700">Language:</label>
                  <select
                    value={selectedLang}
                    onChange={(e) => setSelectedLang(e.target.value)}
                    className="border rounded px-3 py-1 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  >
                    {LANGUAGES.map((lang) => (
                      <option key={lang} value={lang}>
                        {lang.charAt(0).toUpperCase() + lang.slice(1)}
                      </option>
                    ))}
                  </select>
                </div>

                <textarea
                  value={answers[currentQuestion._id] || ""}
                  onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
                  className="flex-1 border rounded-lg p-4 font-mono text-sm resize-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  placeholder={`Write your ${selectedLang} code here...`}
                />

                <div className="flex gap-3 mt-4">
                  <button
                    onClick={handleRunCode}
                    className="flex items-center px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition"
                  >
                    <Play size={16} className="mr-2" />
                    Run Code
                  </button>

                  {(allAnswered || timeLeft === 0) && (
                    <button
                      onClick={() => handleSubmit()}
                      disabled={isSubmitting}
                      className="flex items-center px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition disabled:opacity-60"
                    >
                      <Send size={16} className="mr-2" />
                      {isSubmitting ? "Submitting..." : "Submit Test"}
                    </button>
                  )}
                </div>
              </div>
            </div>
          ) : (
            <div className="max-w-4xl mx-auto">
              <div className="bg-white rounded-xl shadow-lg p-8">
                <div className="mb-6">
                  <div className="flex justify-between items-center mb-4">
                    <h2 className="text-2xl font-bold text-gray-800">
                      Question {currentQuestionIndex + 1}
                    </h2>
                    <span className="text-sm bg-blue-100 text-blue-800 px-3 py-1 rounded-full font-medium">
                      {currentQuestion?.marks || 0} marks
                    </span>
                  </div>
                  <p className="text-lg text-gray-700 leading-relaxed">
                    {currentQuestion?.question}
                  </p>
                </div>

                {/* MCQ Options */}
                {currentQuestion?.options && currentQuestion.options.length > 0 && (
                  <div className="space-y-3 mb-6">
                    {currentQuestion.options.map((option, idx) => {
                      const optionLetter = String.fromCharCode(65 + idx);
                      const isSelected = answers[currentQuestion._id] === optionLetter;

                      return (
                        <label
                          key={idx}
                          className={`flex items-center p-4 rounded-lg border-2 cursor-pointer transition-all duration-200 ${
                            isSelected
                              ? "border-blue-500 bg-blue-50"
                              : "border-gray-200 hover:border-gray-300"
                          }`}
                        >
                          <input
                            type="radio"
                            name={currentQuestion._id}
                            value={optionLetter}
                            checked={isSelected}
                            onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
                            className="sr-only"
                          />
                          <div className={`w-5 h-5 rounded-full border-2 mr-4 flex items-center justify-center ${
                            isSelected ? "border-blue-500 bg-blue-500" : "border-gray-300"
                          }`}>
                            {isSelected && <div className="w-2 h-2 bg-white rounded-full"></div>}
                          </div>
                          <span className="mr-3 font-semibold text-gray-600">{optionLetter}.</span>
                          <span className="text-gray-700">{option}</span>
                        </label>
                      );
                    })}
                  </div>
                )}

                {/* Long Answer */}
                {isLongAnswer && (
                  <div className="mb-6">
                    <textarea
                      value={answers[currentQuestion._id] || ""}
                      onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
                      rows={8}
                      className="w-full border rounded-lg p-4 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Type your detailed answer here..."
                    />
                  </div>
                )}

                {/* Navigation */}
                <div className="flex justify-between items-center">
                  <button
                    onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                    disabled={currentQuestionIndex === 0}
                    className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg disabled:opacity-50 hover:bg-gray-300 transition"
                  >
                    Previous
                  </button>

                  <div className="flex gap-3">
                    {currentQuestionIndex < questionsArr.length - 1 ? (
                      <button
                        onClick={() => setCurrentQuestionIndex(currentQuestionIndex + 1)}
                        className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
                      >
                        Next
                      </button>
                    ) : (
                      (allAnswered || timeLeft === 0) && (
                        <button
                          onClick={() => handleSubmit()}
                          disabled={isSubmitting}
                          className="px-8 py-2 bg-green-600 text-white rounded-lg font-bold hover:bg-green-700 transition disabled:opacity-60"
                        >
                          {isSubmitting ? "Submitting..." : "Submit Test"}
                        </button>
                      )
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Result Modal */}
      {showResult && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-md w-full mx-4">
            <h2 className="text-2xl font-bold mb-4 text-center">Test Submitted!</h2>
            <div className="text-center mb-4">
              <p className="text-lg mb-2">
                Score: <span className="font-bold text-blue-600">
                  {resultData?.score?.obtainedMarks || 0} / {resultData?.score?.totalMarks || 0}
                </span>
              </p>
              {resultData?.violation && (
                <p className="text-red-600 font-semibold mb-2">
                  Violation: {resultData.violation}
                </p>
              )}
              <p className="text-gray-600">{resultData?.reason}</p>
            </div>
            <p className="text-center text-gray-500">Redirecting...</p>
          </div>
        </div>
      )}
    </div>
  );
}
