import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock } from "lucide-react";
import api from "../utils/api";

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [warning, setWarning] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (!test && id) {
      const fetchTest = async () => {
        try {
          const res = await api.get(`/tests/${id}`);
          console.log("Fetched test:", res.data); // check this
          setTest(res.data);
          setTimeLeft(res.data.duration * 60);
        } catch (err) {
          console.error(err);
          alert("Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else if (test) {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  const startTest = async () => {
    setStarted(true);

    const elem = document.documentElement;
    if (elem.requestFullscreen) {
      try { await elem.requestFullscreen(); } 
      catch (err) { console.error("Fullscreen failed:", err); }
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: { width: 320, height: 240 } });
      setCameraStream(stream);
      const video = document.getElementById("cameraFeed");
      if (video) video.srcObject = stream;
    } catch (err) {
      alert("Camera access is required!");
    }
  };

  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            handleSubmit("Time's up!");
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  useEffect(() => {
    if (!started) return;

    const handleViolation = message => {
      if (violationCount === 0) {
        setWarning(`⚠️ ${message} First warning - next violation will auto submit.`);
        setViolationCount(1);
        setTimeout(() => setWarning(""), 5000);
      } else {
        handleSubmit(`Test ended due to violation: ${message}`);
      }
    };

    const handleCopy = e => { e.preventDefault(); handleViolation("Copying is not allowed!"); };
    const handlePaste = e => { e.preventDefault(); handleViolation("Pasting is not allowed!"); };
    const handleKeyDown = e => {
      if (["PrintScreen", "F12"].includes(e.key) || (e.ctrlKey && e.shiftKey && ["I","S"].includes(e.key))) {
        e.preventDefault(); handleViolation("Screenshots/DevTools not allowed!");
      }
    };
    const handleVisibilityChange = () => { if (document.hidden) handleViolation("Switching tabs not allowed!"); };
    const handleWindowBlur = () => { handleViolation("Switching apps not allowed!"); };
    const handleContextMenu = e => { e.preventDefault(); handleViolation("Right-click disabled!"); };

    document.addEventListener("copy", handleCopy);
    document.addEventListener("paste", handlePaste);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("visibilitychange", handleVisibilityChange);
    document.addEventListener("contextmenu", handleContextMenu);
    window.addEventListener("blur", handleWindowBlur);

    return () => {
      document.removeEventListener("copy", handleCopy);
      document.removeEventListener("paste", handlePaste);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
      document.removeEventListener("contextmenu", handleContextMenu);
      window.removeEventListener("blur", handleWindowBlur);
    };
  }, [started, violationCount]);

  const handleAnswerChange = (questionId, value) => {
    setAnswers(prev => ({ ...prev, [questionId]: value }));
  };

  const handleSubmit = async (reason = "Test completed") => {
    if (isSubmitting) return;
    setIsSubmitting(true);
    try {
      const questions = Array.isArray(test.questions) ? test.questions : [];
      let score = 0;
      questions.forEach(q => { if (answers[q._id] === q.correctAnswer) score += q.marks; });

      await api.post(`/tests/${id}/submit`, {
        answers,
        score,
        totalMarks: test?.totalMarks || 0,
        submissionReason: reason
      });

      if (cameraStream) cameraStream.getTracks().forEach(track => track.stop());
      if (document.fullscreenElement) document.exitFullscreen();

      alert(`✅ ${reason}\nScore: ${score}/${test?.totalMarks || 0}`);
      navigate("/my-scores");
    } catch (err) {
      console.error(err);
      setIsSubmitting(false);
    }
  };

  const formatTime = seconds => `${Math.floor(seconds/60)}:${String(seconds%60).padStart(2,'0')}`;

  if (!test) return <div>Loading test...</div>;

  if (!started) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="bg-white p-8 rounded-xl shadow-xl">
          <h1 className="text-3xl font-bold mb-4">{test.name}</h1>
          <p>Duration: {test.duration} minutes</p>
          <p>Total Questions: {test.questions.length}</p>
          <p>Total Marks: {test.totalMarks}</p>
          <button onClick={startTest} className="mt-4 px-6 py-3 bg-blue-600 text-white rounded-lg">Start Test</button>
        </div>
      </div>
    );
  }

  const questions = Array.isArray(test.questions) ? test.questions : [];
  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="flex flex-col min-h-screen">
      {warning && <div className="bg-red-500 text-white p-2 text-center font-semibold">{warning}</div>}

      <div className="flex bg-white shadow-lg p-4 justify-between items-center">
        <h1 className="text-xl font-bold">{test.name}</h1>
        <div className="flex items-center space-x-4">
          <div className={`${timeLeft<=300?'bg-red-100 text-red-800':'bg-blue-100 text-blue-800'} px-4 py-2 rounded-lg flex items-center`}>
            <Clock className="mr-2" size={20} /> {formatTime(timeLeft)}
          </div>
          <video id="cameraFeed" autoPlay muted playsInline className="w-32 h-24 border-2 border-blue-500 rounded-lg"/>
        </div>
      </div>

      <div className="flex flex-1">
        {/* Sidebar */}
        <div className="w-64 bg-white shadow-lg p-4">
          <h3 className="text-lg font-semibold mb-4">Questions</h3>
          <div className="grid grid-cols-5 gap-2">
            {questions.map((q, idx) => {
              const isAnswered = !!answers[q._id];
              const isCurrent = idx === currentQuestionIndex;
              return (
                <button key={q._id} onClick={()=>setCurrentQuestionIndex(idx)}
                  className={`w-10 h-10 rounded-full border-2 flex items-center justify-center text-sm font-semibold ${isCurrent?'bg-blue-600 text-white border-blue-600':isAnswered?'bg-green-200 text-green-800 border-green-500':'bg-gray-100 text-gray-600 border-gray-300'}`}>
                  {idx+1}
                </button>
              )
            })}
          </div>
        </div>

        {/* Question Area */}
        <div className="flex-1 p-6">
          {currentQuestion ? (
            <div className="bg-white rounded-xl shadow-lg p-6 max-w-3xl mx-auto">
              <h2 className="text-xl font-semibold mb-4">Q{currentQuestionIndex+1} ({currentQuestion.marks} marks)</h2>
              <p className="mb-4">{currentQuestion.question}</p>
              <div className="space-y-3">
                {currentQuestion.options.map((opt, idx)=>(
                  <label key={idx} className={`flex items-center p-3 rounded-lg border-2 cursor-pointer ${answers[currentQuestion._id]===opt?'border-blue-500 bg-blue-50':'border-gray-200'}`}>
                    <input type="radio" name={currentQuestion._id} value={opt} checked={answers[currentQuestion._id]===opt} onChange={e=>handleAnswerChange(currentQuestion._id, e.target.value)} className="sr-only"/>
                    <div className={`w-5 h-5 rounded-full border-2 mr-3 flex items-center justify-center ${answers[currentQuestion._id]===opt?'border-blue-500 bg-blue-500':'border-gray-300'}`}>
                      {answers[currentQuestion._id]===opt && <div className="w-2 h-2 bg-white rounded-full"/>}
                    </div>
                    {opt}
                  </label>
                ))}
              </div>

              <div className="flex justify-between mt-6">
                <button onClick={()=>setCurrentQuestionIndex(Math.max(0,currentQuestionIndex-1))} disabled={currentQuestionIndex===0} className="px-4 py-2 bg-gray-200 rounded-lg">Previous</button>
                {currentQuestionIndex<questions.length-1?(
                  <button onClick={()=>setCurrentQuestionIndex(currentQuestionIndex+1)} className="px-4 py-2 bg-blue-600 text-white rounded-lg">Next</button>
                ):(
                  <button onClick={()=>handleSubmit()} disabled={isSubmitting} className="px-6 py-2 bg-green-600 text-white rounded-lg">{isSubmitting?'Submitting...':'Submit Test'}</button>
                )}
              </div>
            </div>
          ):<p>No questions available</p>}
        </div>
      </div>
    </div>
  );
}
