import { useContext, useState, useEffect } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";

export default function DashboardMentor() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatar, setAvatar] = useState("");

  useEffect(() => {
    fetchMe();
  }, []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  const onChange = (e) => {
    const { name, value } = e.target;

    // handle nested mentor fields
    if (name.startsWith("mentor.")) {
      const field = name.split(".")[1];
      setForm({
        ...form,
        mentor: { ...form.mentor, [field]: value },
      });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const updateProfile = async () => {
    const { data } = await api.put("/auth/me", { ...form, avatar });
    localStorage.setItem("user", JSON.stringify(data.user));
    setEditing(false);
    fetchMe();
  };

  return (
    <div className="page-container">
      <h1>Mentor Dashboard</h1>
      {user?.avatar && <img src={user.avatar} alt="avatar" width={100} />}

      {!editing ? (
        <>
          <p>Welcome, {user?.name}</p>
          <ul>
            <li>Email: {user?.email}</li>
            <li>Institution: {user?.mentor?.currentInstitution}</li>
            <li>Phone: {user?.mentor?.phoneNumber}</li>
            <li>Experience: {user?.mentor?.experience}</li>
          </ul>
          <button onClick={() => setEditing(true)}>Edit Profile</button>
        </>
      ) : (
        <div>
          <input
            name="name"
            value={form.name || ""}
            onChange={onChange}
            placeholder="Full Name"
          />
          <input
            name="mentor.currentInstitution"
            value={form.mentor?.currentInstitution || ""}
            onChange={onChange}
            placeholder="Current Institution"
          />
          <input
            name="mentor.phoneNumber"
            value={form.mentor?.phoneNumber || ""}
            onChange={onChange}
            placeholder="Phone Number"
          />
          <input
            name="mentor.experience"
            value={form.mentor?.experience || ""}
            onChange={onChange}
            placeholder="Experience"
          />
          <input
            type="text"
            placeholder="Avatar URL"
            value={avatar}
            onChange={(e) => setAvatar(e.target.value)}
          />
          <button onClick={updateProfile}>Save</button>
        </div>
      )}
    </div>
  );
}
