import { useEffect, useState } from "react";
import api from "../utils/api";
import "./myscores.css";

export default function MyScores() {
  const [scores, setScores] = useState([]);
  const [selectedTest, setSelectedTest] = useState(null);

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const { data } = await api.get("/student/scores");
        console.log("Fetched scores:", data);

        const formattedScores = data.map((s) => ({
          id: s._id,
          testName: s.testName,
          totalMarks: s.totalMarks,
          obtainedMarks: s.obtainedMarks,
        }));

        setScores(formattedScores);
      } catch (err) {
        console.error("Error fetching scores:", err);
      }
    };

    fetchScores();
  }, []);

  const getColor = (percent) => {
    if (percent < 40) return "#f87171"; // red
    if (percent <= 85) return "#facc15"; // yellow
    return "#34d399"; // green
  };

  const generateReport = (test) => {
    const percent = ((test.obtainedMarks / test.totalMarks) * 100).toFixed(2);
    return `You attempted the test "${test.testName}". You scored ${test.obtainedMarks} out of ${test.totalMarks}, which is ${percent}%. ${
      percent >= 85
        ? "Excellent performance! You have mastered most of the concepts in this test."
        : percent >= 40
        ? "Good effort! You have passed the test and understood the key concepts, but there is room for improvement."
        : "Unfortunately, your performance was below the passing mark. Review the material and try again to improve your understanding."
    }`;
  };

  return (
    <div className="myscores-container">
      <h1>My Scores</h1>
      <div className="scores-grid">
        {scores.length === 0 && <p>No scores available yet.</p>}

        {scores.map((test) => {
          const percent = (test.obtainedMarks / test.totalMarks) * 100;

          return (
            <div
              key={test.id}
              className="score-card"
              style={{ borderColor: getColor(percent) }}
              onClick={() =>
                setSelectedTest(selectedTest === test.id ? null : test.id)
              }
            >
              <h2>{test.testName}</h2>
              <p>
                Score:{" "}
                <span style={{ color: getColor(percent) }}>
                  {test.obtainedMarks}/{test.totalMarks} ({percent.toFixed(2)}%)
                </span>
              </p>

              {selectedTest === test.id && (
                <div className="report">
                  <p>{generateReport(test)}</p>
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}
