import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import {
  Bell,
  Send,
  Users,
  ArrowLeft,
  Plus,
  Trash2,
  Calendar,
  AlertTriangle,
  Info,
  MessageCircle,
  Heart,
  ThumbsUp,
  Lightbulb,
  Frown,
  Clock,
  CheckCircle
} from "lucide-react";
import { motion } from "framer-motion";

export default function MentorNotifications() {
  const [notifications, setNotifications] = useState([]);
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newNotification, setNewNotification] = useState({
    title: "",
    message: "",
    type: "general",
    priority: "medium",
    recipients: [],
    expiresAt: ""
  });
  const [submitting, setSubmitting] = useState(false);
  const [stats, setStats] = useState({
    totalNotifications: 0,
    totalReactions: 0,
    readRate: 0,
    reactionRate: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [notificationsRes, studentsRes, statsRes] = await Promise.all([
        api.get("/notifications/mentor"),
        api.get("/admin/users?role=student"), // Assuming this endpoint exists
        api.get("/notifications/stats")
      ]);
      
      setNotifications(notificationsRes.data);
      setStudents(studentsRes.data || []);
      setStats(statsRes.data);
    } catch (err) {
      console.error("Error fetching data:", err);
      setStudents([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateNotification = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    
    try {
      await api.post("/notifications", {
        ...newNotification,
        expiresAt: newNotification.expiresAt || null
      });

      setShowCreateModal(false);
      setNewNotification({
        title: "",
        message: "",
        type: "general",
        priority: "medium",
        recipients: [],
        expiresAt: ""
      });
      fetchData();
      alert("Notification sent successfully!");
    } catch (err) {
      console.error("Error creating notification:", err);
      alert("Failed to send notification: " + (err.response?.data?.message || err.message));
    } finally {
      setSubmitting(false);
    }
  };

  const handleDeleteNotification = async (notificationId) => {
    if (!window.confirm("Are you sure you want to delete this notification?")) {
      return;
    }

    try {
      await api.delete(`/notifications/${notificationId}`);
      fetchData();
      alert("Notification deleted successfully!");
    } catch (err) {
      console.error("Error deleting notification:", err);
      alert("Failed to delete notification");
    }
  };

  const handleStudentToggle = (studentId) => {
    setNewNotification(prev => ({
      ...prev,
      recipients: prev.recipients.includes(studentId)
        ? prev.recipients.filter(id => id !== studentId)
        : [...prev.recipients, studentId]
    }));
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit"
    });
  };

  const getReactionIcon = (reaction) => {
    switch (reaction) {
      case "like": return <ThumbsUp className="w-4 h-4" />;
      case "love": return <Heart className="w-4 h-4" />;
      case "helpful": return <Lightbulb className="w-4 h-4" />;
      case "confused": return <Frown className="w-4 h-4" />;
      default: return null;
    }
  };

  const getReactionCounts = (notification) => {
    const counts = { like: 0, love: 0, helpful: 0, confused: 0 };
    notification.recipients?.forEach(recipient => {
      if (recipient.reaction) {
        counts[recipient.reaction]++;
      }
    });
    return counts;
  };

  const getReadCount = (notification) => {
    return notification.recipients?.filter(r => r.readAt).length || 0;
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-xl">Loading notifications...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-purple-50 to-indigo-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-10">
          <button
            onClick={() => navigate("/dashboard")}
            className="flex items-center text-indigo-600 hover:text-indigo-800 mb-6 group transition-all duration-200"
          >
            <ArrowLeft className="w-5 h-5 mr-2 group-hover:-translate-x-1 transition-transform" />
            Back to Dashboard
          </button>
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-4xl font-bold bg-gradient-to-r from-purple-600 to-indigo-600 bg-clip-text text-transparent mb-2">
                Notification Management
              </h1>
              <p className="text-gray-600 text-lg">Send and manage notifications for your students</p>
            </div>
            <button
              onClick={() => setShowCreateModal(true)}
              className="flex items-center px-8 py-4 bg-gradient-to-r from-purple-600 to-indigo-600 text-white rounded-xl hover:from-purple-700 hover:to-indigo-700 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
            >
              <Plus className="w-6 h-6 mr-3" />
              New Notification
            </button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-10">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
            className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-xl p-8 border border-white/20 transition-all duration-300 hover:-translate-y-1"
          >
            <div className="flex items-center">
              <div className="bg-gradient-to-r from-blue-500 to-cyan-500 p-4 rounded-xl shadow-lg">
                <Bell className="w-7 h-7 text-white" />
              </div>
              <div className="ml-6">
                <h3 className="text-3xl font-bold text-gray-900 mb-1">{stats.totalNotifications}</h3>
                <p className="text-gray-600 font-medium">Total Sent</p>
              </div>
            </div>
          </motion.div>
          
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2 }}
            className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-xl p-8 border border-white/20 transition-all duration-300 hover:-translate-y-1"
          >
            <div className="flex items-center">
              <div className="bg-gradient-to-r from-green-500 to-emerald-500 p-4 rounded-xl shadow-lg">
                <CheckCircle className="w-7 h-7 text-white" />
              </div>
              <div className="ml-6">
                <h3 className="text-3xl font-bold text-gray-900 mb-1">{stats.readRate}%</h3>
                <p className="text-gray-600 font-medium">Read Rate</p>
              </div>
            </div>
          </motion.div>
          
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
            className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-xl p-8 border border-white/20 transition-all duration-300 hover:-translate-y-1"
          >
            <div className="flex items-center">
              <div className="bg-gradient-to-r from-purple-500 to-pink-500 p-4 rounded-xl shadow-lg">
                <Heart className="w-7 h-7 text-white" />
              </div>
              <div className="ml-6">
                <h3 className="text-3xl font-bold text-gray-900 mb-1">{stats.totalReactions}</h3>
                <p className="text-gray-600 font-medium">Total Reactions</p>
              </div>
            </div>
          </motion.div>
          
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4 }}
            className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-xl p-8 border border-white/20 transition-all duration-300 hover:-translate-y-1"
          >
            <div className="flex items-center">
              <div className="bg-gradient-to-r from-orange-500 to-red-500 p-4 rounded-xl shadow-lg">
                <MessageCircle className="w-7 h-7 text-white" />
              </div>
              <div className="ml-6">
                <h3 className="text-3xl font-bold text-gray-900 mb-1">{stats.reactionRate}%</h3>
                <p className="text-gray-600 font-medium">Reaction Rate</p>
              </div>
            </div>
          </motion.div>
        </div>

        {/* Notifications List */}
        <motion.div 
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
          className="bg-white/80 backdrop-blur-sm rounded-2xl shadow-xl border border-white/20 overflow-hidden"
        >
          <div className="bg-gradient-to-r from-purple-600 to-indigo-600 p-8">
            <h2 className="text-2xl font-bold text-white flex items-center">
              <Bell className="w-7 h-7 mr-3" />
              Your Notifications
            </h2>
            <p className="text-purple-100 mt-2">Track engagement and manage all sent notifications</p>
          </div>
          
          {notifications.length === 0 ? (
            <div className="text-center py-16">
              <div className="bg-gradient-to-r from-gray-100 to-gray-200 w-32 h-32 rounded-full flex items-center justify-center mx-auto mb-6">
                <Bell className="w-16 h-16 text-gray-400" />
              </div>
              <h3 className="text-2xl font-semibold text-gray-700 mb-3">No notifications yet</h3>
              <p className="text-gray-500 mb-8 text-lg">Send your first notification to students</p>
              <button
                onClick={() => setShowCreateModal(true)}
                className="px-8 py-4 bg-gradient-to-r from-purple-600 to-indigo-600 text-white rounded-xl hover:from-purple-700 hover:to-indigo-700 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1 font-semibold"
              >
                Send Notification
              </button>
            </div>
          ) : (
            <div className="divide-y divide-gray-100">
              {notifications.map((notification, index) => {
                const reactionCounts = getReactionCounts(notification);
                const readCount = getReadCount(notification);
                const totalRecipients = notification.recipients?.length || 0;

                return (
                  <motion.div
                    key={notification._id}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="p-8 hover:bg-gradient-to-r hover:from-purple-50 hover:to-indigo-50 transition-all duration-300 group"
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center mb-4">
                          <h3 className="text-xl font-bold text-gray-900 mr-4 group-hover:text-purple-600 transition-colors">
                            {notification.title}
                          </h3>
                          <div className={`px-4 py-2 rounded-full text-sm font-semibold mr-3 shadow-sm ${
                            notification.type === "general"
                              ? "bg-gradient-to-r from-blue-500 to-cyan-500 text-white"
                              : notification.type === "assignment"
                              ? "bg-gradient-to-r from-green-500 to-emerald-500 text-white"
                              : notification.type === "test"
                              ? "bg-gradient-to-r from-purple-500 to-violet-500 text-white"
                              : "bg-gradient-to-r from-orange-500 to-amber-500 text-white"
                          }`}>
                            {notification.type.toUpperCase()}
                          </div>
                          <div className={`px-4 py-2 rounded-full text-sm font-semibold shadow-sm ${
                            notification.priority === "urgent"
                              ? "bg-gradient-to-r from-red-500 to-pink-500 text-white"
                              : notification.priority === "high"
                              ? "bg-gradient-to-r from-orange-500 to-red-400 text-white"
                              : notification.priority === "medium"
                              ? "bg-gradient-to-r from-yellow-400 to-orange-400 text-white"
                              : "bg-gradient-to-r from-gray-400 to-gray-500 text-white"
                          }`}>
                            {notification.priority.toUpperCase()}
                          </div>
                        </div>
                        
                        <p className="text-gray-700 mb-6 text-lg leading-relaxed">{notification.message}</p>
                        
                        <div className="flex items-center flex-wrap gap-4 text-gray-600 mb-6">
                          <div className="flex items-center bg-gray-50 px-4 py-2 rounded-lg">
                            <Calendar className="w-5 h-5 mr-2 text-purple-500" />
                            <span className="font-medium">Sent: {formatDate(notification.createdAt)}</span>
                          </div>
                          <div className="flex items-center bg-gray-50 px-4 py-2 rounded-lg">
                            <Users className="w-5 h-5 mr-2 text-blue-500" />
                            <span className="font-medium">{totalRecipients} recipients</span>
                          </div>
                          <div className="flex items-center bg-gray-50 px-4 py-2 rounded-lg">
                            <CheckCircle className="w-5 h-5 mr-2 text-green-500" />
                            <span className="font-medium">{readCount}/{totalRecipients} read</span>
                          </div>
                          {notification.expiresAt && (
                            <div className="flex items-center bg-gray-50 px-4 py-2 rounded-lg">
                              <Clock className="w-5 h-5 mr-2 text-orange-500" />
                              <span className="font-medium">Expires: {formatDate(notification.expiresAt)}</span>
                            </div>
                          )}
                        </div>

                        {/* Reactions */}
                        <div className="flex items-center space-x-4">
                          <span className="text-lg font-semibold text-gray-700">Reactions:</span>
                          <div className="flex items-center space-x-3">
                            {Object.entries(reactionCounts).map(([reaction, count]) => (
                              count > 0 && (
                                <div key={reaction} className="flex items-center space-x-2 bg-gradient-to-r from-indigo-50 to-purple-50 px-4 py-2 rounded-xl">
                                  <div className="text-indigo-600">{getReactionIcon(reaction)}</div>
                                  <span className="font-semibold text-gray-700">{count}</span>
                                  <span className="text-sm text-gray-600 capitalize">{reaction}</span>
                                </div>
                              )
                            ))}
                            {Object.values(reactionCounts).every(count => count === 0) && (
                              <span className="text-gray-500 italic">No reactions yet</span>
                            )}
                          </div>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-3 ml-6">
                        <button
                          onClick={() => handleDeleteNotification(notification._id)}
                          className="p-3 text-gray-400 hover:text-red-600 hover:bg-red-100 rounded-xl transition-all duration-200 hover:scale-110 group"
                          title="Delete Notification"
                        >
                          <Trash2 className="w-6 h-6" />
                        </button>
                      </div>
                    </div>
                  </motion.div>
                );
              })}
            </div>
          )}
        </motion.div>

        {/* Create Notification Modal */}
        {showCreateModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6 border-b">
                <h2 className="text-2xl font-bold text-gray-900">Send New Notification</h2>
              </div>
              
              <form onSubmit={handleCreateNotification} className="p-6 space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Title *
                  </label>
                  <input
                    type="text"
                    value={newNotification.title}
                    onChange={(e) => setNewNotification(prev => ({ ...prev, title: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter notification title"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Message *
                  </label>
                  <textarea
                    value={newNotification.message}
                    onChange={(e) => setNewNotification(prev => ({ ...prev, message: e.target.value }))}
                    rows={4}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter notification message"
                    required
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Type
                    </label>
                    <select
                      value={newNotification.type}
                      onChange={(e) => setNewNotification(prev => ({ ...prev, type: e.target.value }))}
                      className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="general">General</option>
                      <option value="assignment">Assignment</option>
                      <option value="test">Test</option>
                      <option value="announcement">Announcement</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Priority
                    </label>
                    <select
                      value={newNotification.priority}
                      onChange={(e) => setNewNotification(prev => ({ ...prev, priority: e.target.value }))}
                      className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    >
                      <option value="low">Low</option>
                      <option value="medium">Medium</option>
                      <option value="high">High</option>
                      <option value="urgent">Urgent</option>
                    </select>
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Expires At (optional)
                  </label>
                  <input
                    type="datetime-local"
                    value={newNotification.expiresAt}
                    onChange={(e) => setNewNotification(prev => ({ ...prev, expiresAt: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Send to Students (optional)
                  </label>
                  <div className="max-h-32 overflow-y-auto border border-gray-300 rounded-lg p-3">
                    {students.length === 0 ? (
                      <p className="text-gray-500 text-sm">No students available</p>
                    ) : (
                      <div className="space-y-2">
                        {students.map(student => (
                          <label key={student._id} className="flex items-center">
                            <input
                              type="checkbox"
                              checked={newNotification.recipients.includes(student._id)}
                              onChange={() => handleStudentToggle(student._id)}
                              className="mr-2"
                            />
                            <span className="text-sm">{student.name} ({student.email})</span>
                          </label>
                        ))}
                      </div>
                    )}
                  </div>
                  <p className="text-sm text-gray-500 mt-1">
                    Leave empty to send to all students
                  </p>
                </div>

                <div className="flex justify-end space-x-4 pt-4 border-t">
                  <button
                    type="button"
                    onClick={() => {
                      setShowCreateModal(false);
                      setNewNotification({
                        title: "",
                        message: "",
                        type: "general",
                        priority: "medium",
                        recipients: [],
                        expiresAt: ""
                      });
                    }}
                    className="px-6 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={submitting}
                    className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:opacity-50"
                  >
                    {submitting ? "Sending..." : "Send Notification"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
