import React, { useState, useEffect } from 'react';
import { 
  Users, 
  Shield, 
  Clock, 
  Award, 
  Star, 
  ChevronLeft, 
  ChevronRight,
  BookOpen,
  Code,
  Brain,
  Globe
} from 'lucide-react';

const App = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      title: "Advanced Online Testing Platform",
      subtitle: "Experience seamless exam management with our cutting-edge technology",
      image: "https://images.pexels.com/photos/5212317/pexels-photo-5212317.jpeg?auto=compress&cs=tinysrgb&w=1200"
    },
    {
      title: "Secure & Reliable Examinations",
      subtitle: "Built-in security features ensure fair and trustworthy testing",
      image: "https://images.pexels.com/photos/5428836/pexels-photo-5428836.jpeg?auto=compress&cs=tinysrgb&w=1200"
    },
    {
      title: "Real-Time Results & Analytics",
      subtitle: "Get instant feedback and detailed performance insights",
      image: "https://images.pexels.com/photos/5676744/pexels-photo-5676744.jpeg?auto=compress&cs=tinysrgb&w=1200"
    },
    {
      title: "Flexible Test Formats",
      subtitle: "Multiple choice, coding challenges, and custom question types",
      image: "https://images.pexels.com/photos/4050287/pexels-photo-4050287.jpeg?auto=compress&cs=tinysrgb&w=1200"
    }
  ];

  const tests = [
    {
      title: "Aptitude Test",
      description: "Comprehensive aptitude assessment covering logical reasoning, quantitative ability, and verbal skills.",
      duration: "60 mins",
      questions: "50 Questions",
      icon: <Brain className="w-8 h-8" />
    },
    {
      title: "Coding Challenge",
      description: "Programming test with algorithm challenges and data structure problems.",
      duration: "90 mins",
      questions: "5 Problems",
      icon: <Code className="w-8 h-8" />
    },
    {
      title: "General Knowledge",
      description: "Current affairs, history, geography, and general awareness test.",
      duration: "45 mins",
      questions: "40 Questions",
      icon: <Globe className="w-8 h-8" />
    },
    {
      title: "English Proficiency",
      description: "Grammar, vocabulary, comprehension, and written communication skills assessment.",
      duration: "50 mins",
      questions: "35 Questions",
      icon: <BookOpen className="w-8 h-8" />
    }
  ];

  const feedback = [
    {
      name: "Sarah Johnson",
      rating: 5,
      comment: "Excellent platform! The interface is user-friendly and the test experience was smooth. Highly recommend for online assessments.",
      avatar: "https://images.pexels.com/photos/1239291/pexels-photo-1239291.jpeg?auto=compress&cs=tinysrgb&w=150"
    },
    {
      name: "Michael Chen",
      rating: 5,
      comment: "Great variety of tests and instant results. The coding challenges were particularly well-designed and challenging.",
      avatar: "https://images.pexels.com/photos/2182970/pexels-photo-2182970.jpeg?auto=compress&cs=tinysrgb&w=150"
    },
    {
      name: "Emily Davis",
      rating: 4,
      comment: "Professional exam environment with secure testing. The real-time monitoring gives confidence in the results.",
      avatar: "https://images.pexels.com/photos/1681010/pexels-photo-1681010.jpeg?auto=compress&cs=tinysrgb&w=150"
    },
    {
      name: "David Rodriguez",
      rating: 5,
      comment: "Best online testing platform I've used. Clean interface, reliable performance, and comprehensive test analytics.",
      avatar: "https://images.pexels.com/photos/1222271/pexels-photo-1222271.jpeg?auto=compress&cs=tinysrgb&w=150"
    }
  ];

  const features = [
    {
      icon: <Shield className="w-12 h-12" />,
      title: "Secure Exams",
      description: "Advanced security measures including browser lockdown, screen recording detection, and AI-powered proctoring."
    },
    {
      icon: <Award className="w-12 h-12" />,
      title: "Real-Time Results",
      description: "Instant scoring and detailed analytics with performance insights and improvement recommendations."
    },
    {
      icon: <Clock className="w-12 h-12" />,
      title: "Flexible Scheduling",
      description: "Take tests anytime, anywhere with our responsive platform that works on all devices."
    },
    {
      icon: <Users className="w-12 h-12" />,
      title: "24/7 Support",
      description: "Round-the-clock technical support and assistance for seamless testing experience."
    }
  ];

  // Auto-scroll functionality
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const renderStars = (rating: number) => {
    return [...Array(5)].map((_, i) => (
      <Star
        key={i}
        className={`w-4 h-4 ${i < rating ? 'text-yellow-400 fill-current' : 'text-gray-300'}`}
      />
    ));
  };

  return (
    <div className="min-h-screen bg-white">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-br from-blue-600 via-blue-700 to-indigo-800 text-white overflow-hidden">
        <div className="absolute inset-0 bg-black opacity-10"></div>
        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-24 lg:py-32">
          <div className="text-center">
            <h1 className="text-4xl sm:text-5xl lg:text-6xl font-bold mb-6 leading-tight">
              Welcome to <span className="text-blue-200">Smart Test Center</span>
            </h1>
            <p className="text-xl sm:text-2xl mb-10 text-blue-100 max-w-3xl mx-auto leading-relaxed">
              Your trusted online exam partner for secure, reliable, and comprehensive testing solutions
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <button className="bg-white text-blue-700 px-8 py-4 rounded-lg font-semibold text-lg hover:bg-blue-50 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl">
                Join Now
              </button>
              <button className="bg-blue-500 text-white px-8 py-4 rounded-lg font-semibold text-lg hover:bg-blue-400 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl border-2 border-blue-400">
                Take Test
              </button>
            </div>
          </div>
        </div>
      </section>

      {/* Image Slider Section */}
      <section className="relative bg-gray-50 py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="relative h-96 rounded-2xl overflow-hidden shadow-2xl">
            <div 
              className="flex transition-transform duration-700 ease-in-out h-full"
              style={{ transform: `translateX(-${currentSlide * 100}%)` }}
            >
              {slides.map((slide, index) => (
                <div key={index} className="min-w-full h-full relative">
                  <img 
                    src={slide.image} 
                    alt={slide.title}
                    className="w-full h-full object-cover"
                  />
                  <div className="absolute inset-0 bg-gradient-to-r from-black/60 to-black/30 flex items-center">
                    <div className="text-white p-8 lg:p-16 max-w-2xl">
                      <h2 className="text-3xl lg:text-5xl font-bold mb-4">{slide.title}</h2>
                      <p className="text-lg lg:text-xl text-gray-200">{slide.subtitle}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Navigation Arrows */}
            <button 
              onClick={prevSlide}
              className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-white/20 hover:bg-white/30 text-white p-3 rounded-full transition-all duration-300"
            >
              <ChevronLeft className="w-6 h-6" />
            </button>
            <button 
              onClick={nextSlide}
              className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-white/20 hover:bg-white/30 text-white p-3 rounded-full transition-all duration-300"
            >
              <ChevronRight className="w-6 h-6" />
            </button>

            {/* Dots Indicator */}
            <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-2">
              {slides.map((_, index) => (
                <button
                  key={index}
                  onClick={() => setCurrentSlide(index)}
                  className={`w-3 h-3 rounded-full transition-all duration-300 ${
                    index === currentSlide ? 'bg-white' : 'bg-white/50'
                  }`}
                />
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Available Tests Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">Available Tests</h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              Choose from our comprehensive collection of professionally designed assessments
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {tests.map((test, index) => (
              <div key={index} className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-2 border border-gray-100">
                <div className="p-8">
                  <div className="text-blue-600 mb-6 flex justify-center">
                    {test.icon}
                  </div>
                  <h3 className="text-xl font-bold text-gray-900 mb-3 text-center">{test.title}</h3>
                  <p className="text-gray-600 mb-6 text-center leading-relaxed">{test.description}</p>
                  <div className="flex justify-between text-sm text-gray-500 mb-6">
                    <span className="flex items-center">
                      <Clock className="w-4 h-4 mr-1" />
                      {test.duration}
                    </span>
                    <span className="flex items-center">
                      <BookOpen className="w-4 h-4 mr-1" />
                      {test.questions}
                    </span>
                  </div>
                  <button className="w-full bg-blue-600 text-white py-3 rounded-lg font-semibold hover:bg-blue-700 transition-all duration-300 transform hover:scale-105">
                    Take Now
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* User Feedback Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">What Our Users Say</h2>
            <p className="text-xl text-gray-600">
              Trusted by thousands of students and professionals worldwide
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {feedback.map((review, index) => (
              <div key={index} className="bg-white rounded-xl shadow-md hover:shadow-lg transition-all duration-300 p-6 border border-gray-100">
                <div className="flex items-center mb-4">
                  <img 
                    src={review.avatar} 
                    alt={review.name}
                    className="w-12 h-12 rounded-full object-cover mr-4"
                  />
                  <div>
                    <h4 className="font-semibold text-gray-900">{review.name}</h4>
                    <div className="flex">
                      {renderStars(review.rating)}
                    </div>
                  </div>
                </div>
                <p className="text-gray-600 leading-relaxed text-sm">"{review.comment}"</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Why Choose Us Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">Why Choose Smart Test Center?</h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              We provide enterprise-grade testing solutions with unmatched reliability and user experience
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => (
              <div key={index} className="text-center group">
                <div className="bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl p-6 mb-6 inline-block group-hover:scale-110 transition-transform duration-300 shadow-lg">
                  <div className="text-white">
                    {feature.icon}
                  </div>
                </div>
                <h3 className="text-xl font-bold text-gray-900 mb-3">{feature.title}</h3>
                <p className="text-gray-600 leading-relaxed">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-900 text-white py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mb-8">
            <div className="col-span-1 md:col-span-2">
              <h3 className="text-2xl font-bold mb-4">Smart Test Center</h3>
              <p className="text-gray-400 leading-relaxed max-w-md">
                Leading online examination platform providing secure, reliable, and comprehensive testing solutions for educational institutions and corporations.
              </p>
            </div>
            <div>
              <h4 className="text-lg font-semibold mb-4">Quick Links</h4>
              <ul className="space-y-2">
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">About Us</a></li>
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Contact Us</a></li>
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Privacy Policy</a></li>
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Terms of Service</a></li>
              </ul>
            </div>
            <div>
              <h4 className="text-lg font-semibold mb-4">Support</h4>
              <ul className="space-y-2">
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Help Center</a></li>
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Documentation</a></li>
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">FAQ</a></li>
                <li><a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Live Chat</a></li>
              </ul>
            </div>
          </div>
          <div className="border-t border-gray-800 pt-8">
            <div className="flex flex-col md:flex-row justify-between items-center">
              <p className="text-gray-400"> 2025 Smart Test Center. All rights reserved.</p>
              <div className="flex space-x-6 mt-4 md:mt-0">
                <a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">About Us</a>
                <a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Contact Us</a>
                <a href="#" className="text-gray-400 hover:text-white transition-colors duration-300">Privacy Policy</a>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
