// src/pages/TakeTest.jsx
import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock } from "lucide-react";
import api from "../utils/api";
// import MonacoEditor from "react-monaco-editor"; // Uncomment if using Monaco
import "./taketest.css";

const INSTRUCTIONS = [
  "• Do not switch tabs, minimize, copy, paste, or take screenshots.",
  "• Your webcam will be monitored during the test.",
  "• You will receive up to 3 warnings for violations. On the 3rd, the test will auto-submit.",
  "• Answer all questions before submitting. Timer is at the top right.",
  "• For coding questions, select your language and write code in the editor.",
];

const LANGUAGES = ["Python", "JavaScript", "Java", "C++"];

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [violationReason, setViolationReason] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedLang, setSelectedLang] = useState(LANGUAGES[0]);
  const [code, setCode] = useState("");
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState(null);

  // Fetch test data
  useEffect(() => {
    if (!test && id) {
      const fetchTest = async () => {
        try {
          const res = await api.get(`/api/tests/${id}`);
          setTest(res.data);
          setTimeLeft(res.data.duration * 60);
        } catch {
          alert("Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else if (test) {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  // Start test: fullscreen + camera
  const startTest = async () => {
    setStarted(true);
    const elem = document.documentElement;
    if (elem.requestFullscreen) {
      try {
        await elem.requestFullscreen();
      } catch {}
    }
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 320, height: 240 },
      });
      setCameraStream(stream);
      const video = document.getElementById("cameraFeed");
      if (video) video.srcObject = stream;
    } catch {
      alert("Camera access is required for this test!");
    }
  };

  // Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            handleSubmit("Time's up!");
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  // Violation detection
  useEffect(() => {
    if (!started) return;
    const handleViolation = (message) => {
      if (violationCount < 2) {
        setViolationCount((v) => v + 1);
        alert(`⚠️ ${message}\nWarning ${violationCount + 1}/3`);
      } else {
        setViolationReason(message);
        handleSubmit(`Test ended due to violation: ${message}`);
      }
    };
    const handleCopy = (e) => { e.preventDefault(); handleViolation("Copying not allowed!"); };
    const handlePaste = (e) => { e.preventDefault(); handleViolation("Pasting not allowed!"); };
    const handleKeyDown = (e) => {
      if (e.key === "PrintScreen" || e.key === "F12") {
        e.preventDefault(); handleViolation("Screenshots/Dev tools not allowed!");
      }
    };
    const handleVisibilityChange = () => {
      if (document.hidden) handleViolation("Switching tabs not allowed!");
    };
    const handleWindowBlur = () => handleViolation("Minimizing not allowed!");
    const handleContextMenu = (e) => { e.preventDefault(); handleViolation("Right-click disabled!"); };

    document.addEventListener("copy", handleCopy);
    document.addEventListener("paste", handlePaste);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("visibilitychange", handleVisibilityChange);
    document.addEventListener("contextmenu", handleContextMenu);
    window.addEventListener("blur", handleWindowBlur);

    return () => {
      document.removeEventListener("copy", handleCopy);
      document.removeEventListener("paste", handlePaste);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
      document.removeEventListener("contextmenu", handleContextMenu);
      window.removeEventListener("blur", handleWindowBlur);
    };
  }, [started, violationCount]);

  // Answer change
  const handleAnswerChange = (qId, value) => {
    setAnswers((prev) => ({
      ...prev,
      [qId]: value,
    }));
  };

  // Submit test
  const handleSubmit = async (reason = "Test completed") => {
    if (isSubmitting) return;
    setIsSubmitting(true);

    try {
      const userId = localStorage.getItem("userId");

      const res = await api.post(`/submissions/${id}/submit`, {
        answers,
        code: test?.category === "coding" ? code : undefined,
        language: test?.category === "coding" ? selectedLang : undefined,
        submissionReason: reason,
        violationReason,
        userId,
      });

      if (cameraStream) cameraStream.getTracks().forEach((t) => t.stop());
      if (document.fullscreenElement) document.exitFullscreen();

      setResultData({
        score: res.data?.score,
        reason,
        violation: violationReason,
      });
      setShowResult(true);

      setTimeout(() => navigate("/my-scores"), 4000);
    } catch {
      alert("Error submitting test. Please try again.");
      setIsSubmitting(false);
    }
  };

  const formatTime = (s) => `${Math.floor(s / 60)}:${(s % 60).toString().padStart(2, "0")}`;

  const questionsArr = Array.isArray(test?.questions) ? test.questions : [];
  const answeredCount = questionsArr.filter((q) => answers[q._id]).length;
  const notAnsweredCount = questionsArr.length - answeredCount;
  const isCoding = test?.category === "coding";
  const currentQ = questionsArr[currentQuestionIndex];

  // Instructions Page
  if (!test) return <div>Loading test...</div>;
  if (!started) {
    return (
      <div className="instructions">
        <h1>{test.name}</h1>
        <ul>{INSTRUCTIONS.map((inst, i) => <li key={i}>{inst}</li>)}</ul>
        <p>Duration: {test.duration} mins</p>
        <p>Total Marks: {test.totalMarks}</p>
        <button onClick={startTest}>Start Test</button>
      </div>
    );
  }

  // Test Page
  return (
    <div className="take-test">
      <div className="fixed top-6 right-8">
        <div>{formatTime(timeLeft)}</div>
        <video id="cameraFeed" autoPlay playsInline muted />
      </div>

      {showResult && (
        <div className="result">
          <p>Score: {resultData?.score?.obtainedMarks}/{resultData?.score?.totalMarks}</p>
          {resultData?.violation && <p>Violation: {resultData.violation}</p>}
          <p>{resultData?.reason}</p>
        </div>
      )}

      {!isCoding ? (
        <div className="mcq-section">
          <h2>Q{currentQuestionIndex + 1}: {currentQ?.question}</h2>
          <div>
            {currentQ?.options?.map((opt, idx) => {
              const letter = String.fromCharCode(65 + idx);
              return (
                <label key={letter}>
                  <input
                    type="radio"
                    name={currentQ._id}
                    value={letter}
                    checked={answers[currentQ._id] === letter}
                    onChange={() => handleAnswerChange(currentQ._id, letter)}
                  />
                  {letter}. {opt}
                </label>
              );
            })}
          </div>
          <div>
            {currentQuestionIndex > 0 && (
              <button onClick={() => setCurrentQuestionIndex(currentQuestionIndex - 1)}>
                Previous
              </button>
            )}
            {currentQuestionIndex < questionsArr.length - 1 ? (
              <button onClick={() => setCurrentQuestionIndex(currentQuestionIndex + 1)}>
                Next
              </button>
            ) : (
              <button onClick={() => handleSubmit()} disabled={answeredCount !== questionsArr.length}>
                Submit Test
              </button>
            )}
          </div>
        </div>
      ) : (
        <div className="coding-section">
          <h2>{currentQ?.question}</h2>
          <textarea value={code} onChange={(e) => setCode(e.target.value)} />
          <button onClick={() => handleSubmit()} disabled={!code.trim()}>
            Submit Code
          </button>
        </div>
      )}
    </div>
  );
}
