import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import {
  ArrowLeft,
  Search,
  Trophy,
  Eye,
  Calendar,
  User,
  Target,
  AlertTriangle,
  RefreshCw
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import "../dashboard.css";

export default function MentorResults() {
  const [studentResults, setStudentResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);
  const [selectedResult, setSelectedResult] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [loadingResults, setLoadingResults] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Fetch student results
  const fetchStudentResults = async () => {
    setLoadingResults(true);
    setError(null);
    try {
      console.log("Fetching mentor results...");
      
      // Try the primary endpoint
      const response = await api.get("/submissions/mentor");
      console.log("Primary response:", response.data);
      
      if (response.data && Array.isArray(response.data) && response.data.length > 0) {
        setStudentResults(response.data);
        setFilteredResults(response.data);
      } else {
        // Try the fallback endpoint
        const fallbackResponse = await api.get("/submissions/all-for-mentor");
        if (fallbackResponse.data && Array.isArray(fallbackResponse.data)) {
          setStudentResults(fallbackResponse.data);
          setFilteredResults(fallbackResponse.data);
        } else {
          setStudentResults([]);
          setFilteredResults([]);
        }
      }
    } catch (err) {
      console.error("Error fetching student results:", err);
      
      // Handle different types of errors
      if (err.response) {
        // Server responded with error status
        if (err.response.status === 401) {
          setError("Authentication failed. Please log in again.");
        } else if (err.response.status === 500) {
          setError("Server error. Please try again later or contact support.");
        } else {
          setError(`Server error: ${err.response.data?.message || err.response.statusText}`);
        }
      } else if (err.request) {
        // Request was made but no response received
        setError("Network error. Please check your connection and try again.");
      } else {
        // Something else happened
        setError(err.message || "Failed to fetch results");
      }
      
      // Try the fallback endpoint on error
      try {
        const fallbackResponse = await api.get("/submissions/all-for-mentor");
        if (fallbackResponse.data && Array.isArray(fallbackResponse.data)) {
          setStudentResults(fallbackResponse.data);
          setFilteredResults(fallbackResponse.data);
        } else {
          setStudentResults([]);
          setFilteredResults([]);
        }
      } catch (fallbackErr) {
        console.error("Error with fallback endpoint:", fallbackErr);
        // Don't override the original error message
        setStudentResults([]);
        setFilteredResults([]);
      }
    } finally {
      setLoadingResults(false);
    }
  };

  // Fetch student results on component mount
  useEffect(() => {
    fetchStudentResults();
  }, []);

  // Filter results
  useEffect(() => {
    let results = [...studentResults];

    if (searchTerm) {
      results = results.filter(result => {
        const studentName =
          result.student?.name ||
          result.userId?.name ||
          result.studentName ||
          "Unknown Student";
        const testName =
          result.test?.name ||
          result.testId?.name ||
          result.testName ||
          "Unknown Test";
        return (
          studentName.toLowerCase().includes(searchTerm.toLowerCase()) ||
          testName.toLowerCase().includes(searchTerm.toLowerCase())
        );
      });
    }

    setFilteredResults(results);
  }, [studentResults, searchTerm]);

  const getColor = (percent) => {
    if (percent < 35) return "#ef4444";
    if (percent <= 80) return "#f59e0b";
    return "#10b981";
  };

  const getGrade = (percent) => {
    if (percent >= 90) return "A+";
    if (percent >= 80) return "A";
    if (percent >= 70) return "B+";
    if (percent >= 60) return "B";
    if (percent >= 50) return "C";
    if (percent >= 35) return "D";
    return "F";
  };

  const formatDate = (timestamp) => {
    if (!timestamp) return "N/A";
    try {
      const date = new Date(timestamp);
      return date.toLocaleDateString("en-US", {
        year: "numeric",
        month: "short",
        day: "numeric",
        hour: "2-digit",
        minute: "2-digit"
      });
    } catch {
      return "Invalid Date";
    }
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <div className="header-content">
          <button
            onClick={() => navigate("/dashboard/mentor")}
            className="flex items-center text-white mb-4 hover:text-blue-200 transition-colors"
          >
            <ArrowLeft className="w-5 h-5 mr-2" />
            Back to Dashboard
          </button>
          <h1 className="dashboard-title">Student Results</h1>
          <p className="dashboard-subtitle">View and analyze student performance</p>
        </div>
      </div>

      {/* Search Section */}
      <div className="dashboard-section mb-6">
        <div className="flex flex-col md:flex-row md:justify-between md:items-center gap-4">
          <h2 className="section-title">All Results</h2>
          <div className="flex gap-3">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search by student or test name..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent w-full sm:w-auto"
              />
            </div>
            <button
              onClick={fetchStudentResults}
              disabled={loadingResults}
              className="flex items-center gap-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:opacity-50 transition-colors duration-200"
            >
              <RefreshCw className={`w-4 h-4 ${loadingResults ? 'animate-spin' : ''}`} />
              Refresh
            </button>
          </div>
        </div>
      </div>

      {/* Results Grid */}
      <div className="dashboard-section">
        {loadingResults ? (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading results...</p>
          </div>
        ) : error ? (
          <div className="text-center py-8">
            <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-2xl mx-auto">
              <h3 className="text-lg font-semibold text-red-800 mb-2">Error Loading Results</h3>
              <p className="text-red-600 mb-4">{error}</p>
              <div className="flex flex-col sm:flex-row gap-3 justify-center">
                <button
                  onClick={fetchStudentResults}
                  className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors duration-200"
                >
                  Try Again
                </button>
                <button
                  onClick={() => navigate("/dashboard/mentor")}
                  className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors duration-200"
                >
                  Back to Dashboard
                </button>
              </div>
            </div>
          </div>
        ) : filteredResults.length === 0 ? (
          <div className="text-center py-8">
            <Trophy className="h-12 w-12 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-gray-900 mb-2">No results found</h3>
            <div className="text-gray-600 max-w-2xl mx-auto">
              <p className="mb-4">
                {searchTerm
                  ? "No results match your search criteria."
                  : "No student results available yet. Students need to complete tests before results will appear here."}
              </p>
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-4">
                <h4 className="font-semibold text-blue-800 mb-2">Important Note:</h4>
                <p className="text-blue-700">
                  You can only see results for tests that you have created. If students have completed exams but you don't see results here, 
                  please check if:
                </p>
                <ul className="list-disc list-inside text-blue-700 mt-2 text-left">
                  <li>You created the test using your current login credentials</li>
                  <li>Students submitted the correct test (matching the one you created)</li>
                  <li>The test was successfully saved in the database</li>
                </ul>
              </div>
              <button
                onClick={fetchStudentResults}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-200"
              >
                Refresh Results
              </button>
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredResults.map((result, index) => {
              const obtained = result.obtainedMarks || 0;
              const total =
                result.totalMarks ||
                result.test?.totalMarks ||
                result.testId?.totalMarks ||
                100;
              const percent = total > 0 ? (obtained / total) * 100 : 0;
              const grade = getGrade(percent);
              const studentName =
                result.student?.name ||
                result.userId?.name ||
                result.studentName ||
                "Unknown Student";
              const testName =
                result.test?.name ||
                result.testId?.name ||
                result.testName ||
                "Unknown Test";
              const submittedAt = result.submittedAt || new Date();
              const submissionReason = result.submissionReason || "Test completed";
              const violationReason = result.violationReason || "";

              return (
                <motion.div
                  key={result._id || index}
                  className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-all duration-300 hover:-translate-y-1"
                  style={{ borderTop: `4px solid ${getColor(percent)}` }}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1, duration: 0.3 }}
                  whileHover={{ y: -5 }}
                >
                  <div className="flex justify-between items-start mb-4">
                    <div className="flex-1">
                      <h3 className="font-bold text-gray-900 text-lg mb-1 truncate" title={studentName}>
                        {studentName}
                      </h3>
                      <p className="text-sm text-gray-600 truncate" title={testName}>
                        {testName}
                      </p>
                    </div>
                    <span
                      className="px-3 py-1 rounded-full text-xs font-bold text-white whitespace-nowrap"
                      style={{ backgroundColor: getColor(percent) }}
                    >
                      {grade}
                    </span>
                  </div>

                  <div className="space-y-2 mb-4">
                    <div className="flex justify-between text-sm">
                      <span className="text-gray-600">Score:</span>
                      <span className="font-semibold" style={{ color: getColor(percent) }}>
                        {obtained}/{total} ({percent.toFixed(1)}%)
                      </span>
                    </div>

                    <div className="flex justify-between text-sm">
                      <span className="text-gray-600">Submitted:</span>
                      <span className="text-gray-700 font-medium">{formatDate(submittedAt)}</span>
                    </div>

                    {submissionReason && submissionReason !== "Test completed" && (
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-600">Reason:</span>
                        <span className="text-gray-700 font-medium truncate" title={submissionReason}>
                          {submissionReason}
                        </span>
                      </div>
                    )}

                    {violationReason && (
                      <div className="flex items-center text-sm text-red-600">
                        <AlertTriangle className="w-3 h-3 mr-1 flex-shrink-0" />
                        <span className="truncate" title={violationReason}>
                          Violation detected
                        </span>
                      </div>
                    )}
                  </div>

                  <div className="w-full bg-gray-200 rounded-full h-2 mb-4">
                    <div
                      className="h-2 rounded-full transition-all duration-500"
                      style={{ width: `${percent}%`, backgroundColor: getColor(percent) }}
                    ></div>
                  </div>

                  <button
                    className="w-full flex items-center justify-center gap-2 py-2.5 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all duration-300 font-medium shadow-sm hover:shadow-md"
                    onClick={() =>
                      setSelectedResult({
                        ...result,
                        percent,
                        grade,
                        studentName,
                        testName,
                        obtained,
                        total,
                        submittedAt,
                        submissionReason,
                        violationReason
                      })
                    }
                  >
                    <Eye className="w-4 h-4" />
                    View Details
                  </button>
                </motion.div>
              );
            })}
          </div>
        )}
      </div>

      {/* Result Details Modal */}
      <AnimatePresence>
        {selectedResult && (
          <motion.div
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setSelectedResult(null)}
          >
            <motion.div
              className="bg-white rounded-xl shadow-2xl p-6 max-w-2xl w-full max-h-[90vh] overflow-y-auto"
              initial={{ scale: 0.9, opacity: 0, y: 20 }}
              animate={{ scale: 1, opacity: 1, y: 0 }}
              exit={{ scale: 0.9, opacity: 0, y: 20 }}
              transition={{ type: "spring", damping: 25, stiffness: 300 }}
              onClick={(e) => e.stopPropagation()}
            >
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-bold text-gray-900">Test Result Details</h2>
                <button
                  onClick={() => setSelectedResult(null)}
                  className="text-gray-400 hover:text-gray-600 text-2xl font-bold leading-none transition-colors duration-200"
                >
                  
                </button>
              </div>

              <div className="space-y-6">
                {/* Student and Test Info */}
                <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl p-5 border border-blue-100">
                  <div className="flex items-center gap-3 mb-4">
                    <div className="bg-gradient-to-r from-blue-500 to-indigo-500 w-12 h-12 rounded-full flex items-center justify-center">
                      <User className="w-6 h-6 text-white" />
                    </div>
                    <div>
                      <h3 className="font-bold text-gray-900 text-lg">{selectedResult.studentName}</h3>
                      <p className="text-sm text-gray-600">Student</p>
                    </div>
                  </div>

                  <div className="bg-white rounded-lg p-4 shadow-sm">
                    <h4 className="font-semibold text-gray-900 mb-2">{selectedResult.testName}</h4>
                    <p className="text-sm text-gray-600">Test Name</p>
                  </div>
                </div>

                {/* Score Summary */}
                <div className="grid grid-cols-3 gap-4">
                  <div className="bg-gradient-to-br from-blue-50 to-cyan-50 p-4 rounded-xl text-center shadow-sm">
                    <p className="text-sm text-gray-600 mb-1">Score</p>
                    <p className="text-xl font-bold text-blue-600">
                      {selectedResult.obtained}/{selectedResult.total}
                    </p>
                  </div>

                  <div className="bg-gradient-to-br from-green-50 to-emerald-50 p-4 rounded-xl text-center shadow-sm">
                    <p className="text-sm text-gray-600 mb-1">Percentage</p>
                    <p
                      className="text-xl font-bold"
                      style={{ color: getColor(selectedResult.percent) }}
                    >
                      {selectedResult.percent?.toFixed(1)}%
                    </p>
                  </div>

                  <div className="bg-gradient-to-br from-purple-50 to-violet-50 p-4 rounded-xl text-center shadow-sm">
                    <p className="text-sm text-gray-600 mb-1">Grade</p>
                    <span
                      className="inline-block px-3 py-1 rounded-full text-sm font-bold text-white"
                      style={{ backgroundColor: getColor(selectedResult.percent) }}
                    >
                      {selectedResult.grade}
                    </span>
                  </div>
                </div>

                {/* Progress Bar */}
                <div className="bg-gray-50 rounded-xl p-5 shadow-sm">
                  <div className="relative mb-2">
                    <div className="w-full bg-gray-200 rounded-full h-3">
                      <motion.div
                        className="h-3 rounded-full"
                        style={{ backgroundColor: getColor(selectedResult.percent) }}
                        initial={{ width: 0 }}
                        animate={{ width: `${selectedResult.percent}%` }}
                        transition={{ duration: 1, ease: "easeOut" }}
                      ></motion.div>
                    </div>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <span className="text-xs font-bold text-gray-700">
                        {selectedResult.percent?.toFixed(1)}%
                      </span>
                    </div>
                  </div>

                  <div className="flex justify-between text-sm text-gray-600 mt-3">
                    <span>0%</span>
                    <span>100%</span>
                  </div>
                </div>

                {/* Submission Info */}
                <div className="border-t border-gray-200 pt-4">
                  <h4 className="font-semibold text-gray-900 mb-3">Submission Information</h4>
                  <div className="space-y-3">
                    <div className="bg-white rounded-lg p-3 border border-gray-200 shadow-sm">
                      <p className="text-sm text-gray-600 mb-1">Reason for Submission</p>
                      <p className="font-medium text-gray-900">
                        {selectedResult.submissionReason || "Test completed"}
                      </p>
                    </div>

                    {selectedResult.violationReason && (
                      <div className="bg-red-50 rounded-lg p-3 border border-red-200 shadow-sm">
                        <p className="text-sm text-red-600 mb-1 flex items-center">
                          <AlertTriangle className="w-4 h-4 mr-1" />
                          Violation Detected
                        </p>
                        <p className="font-medium text-red-800">{selectedResult.violationReason}</p>
                      </div>
                    )}

                    <div className="bg-white rounded-lg p-3 border border-gray-200 shadow-sm">
                      <p className="text-sm text-gray-600 mb-1 flex items-center">
                        <Calendar className="w-4 h-4 mr-1" />
                        Submitted At
                      </p>
                      <p className="font-medium text-gray-900">
                        {formatDate(selectedResult.submittedAt)}
                      </p>
                    </div>
                  </div>
                </div>

                {/* Close Button */}
                <div className="flex justify-end pt-4 border-t border-gray-200">
                  <button
                    onClick={() => setSelectedResult(null)}
                    className="px-6 py-2 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all duration-300 font-medium shadow-sm hover:shadow-md"
                  >
                    Close
                  </button>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
