import React, { useEffect, useState } from "react";
import api from "../utils/api";
import TestCard from "../components/TestCard";
import { useNavigate } from "react-router-dom";

export default function MyTests() {
  const [tests, setTests] = useState([]); // Default to empty array
  const navigate = useNavigate();

  useEffect(() => {
    api.get("/tests")
      .then(res => {
        // Ensure tests is always an array
        setTests(Array.isArray(res.data) ? res.data : []);
      })
      .catch(() => setTests([]));
  }, []);

  const handleTakeTest = (test) => {
    navigate(`/take-test/${test._id}`, { state: { test } });
  };

  return (
    <div className="max-w-6xl mx-auto p-8">
      <h1 className="text-2xl font-bold mb-6">My Tests</h1>
      <div className="test-card-grid">
  {(Array.isArray(tests) ? tests : []).map(test => (
    <TestCard key={test._id} test={test} onTakeTest={handleTakeTest} />
  ))}
</div>
    </div>
  );
}
