import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import { FaClock, FaStar } from "react-icons/fa";
import "./mytests.css";

export default function MyTests() {
  const [tests, setTests] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTests = async () => {
      try {
        const { data } = await api.get("/student/tests"); // ✅ correct URL
        setTests(data.tests || []);
      } catch (err) {
        console.error("Failed to fetch tests:", err.response || err);
      }
    };
    fetchTests();
  }, []);

  const takeTest = (test) => {
    navigate(`/take-test/${test._id}`, { state: { test } });
  };

  const colors = ["#fef3c7", "#d1fae5", "#dbeafe", "#f0e6ff"];

  return (
    <div className="mytests-container">
      <div className="mytests-header">
        <h1>Available Tests</h1>
        <p>Select a test to start your practice</p>
      </div>

      <div className="mytests-grid">
        {tests.length === 0 ? (
          <p>No tests available at the moment.</p>
        ) : (
          tests.map((test, index) => (
            <div
              key={test._id}
              className="mytest-card"
              style={{ backgroundColor: colors[index % colors.length] }}
            >
              <h2>{test.name}</h2>
              <div className="mytest-details">
                <div>
                  <FaClock /> Duration: {test.duration} min
                </div>
                <div>
                  <FaStar /> Max Marks: {test.totalMarks}
                </div>
              </div>
              <button onClick={() => takeTest(test)}>Take Test</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
