import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import api from "../utils/api";
import "./taketest.css";

export default function TakeTest() {
  const { id } = useParams();
  const { state } = useLocation();
  const [test, setTest] = useState(state?.test || null);
  const [totalQuestions, setTotalQuestions] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTest = async () => {
      try {
        const { data } = await api.get(`/tests/${id}`);
        setTest(data.test);
        setTotalQuestions(data.test.questions.length);
      } catch (err) {
        console.error("Failed to fetch test:", err.response || err);
      }
    };

    if (!test) fetchTest();
    else setTotalQuestions(test.questions?.length || 0);
  }, [id]);

  if (!test) return <p>Loading test...</p>;

  return (
    <div className="taketest-instructions">
      <h1>{test.name}</h1>
      <div className="test-info">
        <p><strong>Total Time:</strong> {test.duration} minutes</p>
        <p><strong>Total Marks:</strong> {test.totalMarks}</p>
        <p><strong>Total Questions:</strong> {totalQuestions}</p>
      </div>

      <div className="instructions-box">
        <h2>Instructions</h2>
        <ul>
          <li>Do not copy, switch tabs, or take screenshots.</li>
          <li>On the first malpractice attempt, you will get a warning.</li>
          <li>On the second malpractice attempt, your test will be submitted automatically.</li>
          <li>Webcam monitoring is active. Looking away repeatedly will auto-submit.</li>
          <li>Once the timer ends, the test will be auto-submitted.</li>
        </ul>
      </div>

      <button
        className="start-btn"
        onClick={() => navigate(`/start-test/${test._id}`, { state: { test } })}
      >
        Start Test
      </button>
    </div>
  );
}
