import { useEffect, useState, useRef } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock, Play, Send } from "lucide-react";
import api from "../utils/api";
import "./taketest.css";

const INSTRUCTIONS = [
  "• Do not switch tabs, minimize, copy, paste, or take screenshots.",
  "• Your webcam will be monitored during the test.",
  "• You will receive up to 3 warnings for violations. On the 3rd, the test will auto-submit.",
  "• Answer all questions before submitting. Timer is at the top right.",
  "• For coding questions, select your language and write code in the editor.",
];

const LANGUAGES = ["python", "javascript", "java", "cpp"];

// Utility function to shuffle array
const shuffleArray = (array) => {
  const shuffled = [...array];
  for (let i = shuffled.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
  }
  return shuffled;
};

// Voice alert function
const speakViolation = (message) => {
  if ("speechSynthesis" in window) {
    const utterance = new SpeechSynthesisUtterance(message);
    utterance.volume = 0.8;
    utterance.rate = 0.9;
    utterance.pitch = 1;
    speechSynthesis.speak(utterance);
  }
};

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [questions, setQuestions] = useState([]);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [violationReason, setViolationReason] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedLang, setSelectedLang] = useState(LANGUAGES[0]);
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState(null);
  const [warnings, setWarnings] = useState([]);
  const [cameraError, setCameraError] = useState(false);
  const [loading, setLoading] = useState(true);

  const videoRef = useRef(null);

  // Fetch test data
  useEffect(() => {
    const fetchTestData = async () => {
      try {
        let testData = test;
        if (!testData && id) {
          const res = await api.get(`/tests/${id}`);
          testData = res.data;
          setTest(testData);
        }

        if (testData && testData.questions) {
          const shuffledQuestions = shuffleArray(testData.questions);
          const processedQuestions = shuffledQuestions.map((question) => {
            if (question.type === "mcq" && question.options?.length > 0) {
              const optionsWithIndex = question.options.map((option, index) => ({
                text: option,
                originalIndex: index,
                originalLetter: String.fromCharCode(65 + index),
              }));

              const shuffledOptions = shuffleArray(optionsWithIndex);
              return {
                ...question,
                options: shuffledOptions.map((opt) => opt.text),
                optionMapping: shuffledOptions.map((opt) => opt.originalLetter),
                originalCorrectOption: question.correctOption,
              };
            }
            return question;
          });

          setQuestions(processedQuestions);
          setTimeLeft((testData.duration || 60) * 60);
        }
      } catch (err) {
        console.error("Failed to fetch test:", err);
        alert("Failed to fetch test!");
        navigate("/my-tests");
      } finally {
        setLoading(false);
      }
    };

    fetchTestData();
  }, [id, test, navigate]);

  // Monitor camera stream
  useEffect(() => {
    if (!cameraStream) return;
    const videoTracks = cameraStream.getVideoTracks();
    if (videoTracks.length === 0) {
      setCameraError(true);
      alert("Camera is not available. Test cannot start.");
      return;
    }

    const handleTrackEnded = () => {
      setCameraError(true);
      alert("Camera disconnected. Test will be submitted.");
      speakViolation("Camera disconnected. Test will be submitted automatically.");
      setViolationReason("Camera disconnected");
      handleSubmit("Test ended due to camera disconnection");
    };

    videoTracks.forEach((track) => track.addEventListener("ended", handleTrackEnded));
    return () => {
      videoTracks.forEach((track) => track.removeEventListener("ended", handleTrackEnded));
    };
  }, [cameraStream]);

  // Start test
  const startTest = async () => {
    if (cameraError) {
      alert("Cannot start test because camera is not accessible.");
      return;
    }
    setStarted(true);

    if (document.documentElement.requestFullscreen) {
      try {
        await document.documentElement.requestFullscreen();
      } catch {}
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: { width: 320, height: 240 } });
      setCameraStream(stream);
      if (videoRef.current) videoRef.current.srcObject = stream;
      setCameraError(false);
    } catch (e) {
      console.error("Camera access failed:", e);
      alert("Camera access is required!");
      setCameraError(true);
    }
  };

  // Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            speakViolation("Time is up! Test will be submitted automatically.");
            handleSubmit("Time's up!");
            return 0;
          }
          if (prev === 300) speakViolation("Warning: Only 5 minutes remaining!");
          if (prev === 60) speakViolation("Warning: Only 1 minute remaining!");
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  // Handle answers
  const handleAnswerChange = (questionId, value) => {
    const currentQuestion = questions[currentQuestionIndex];
    if (currentQuestion?.type === "mcq" && currentQuestion?.optionMapping) {
      const originalOption = currentQuestion.optionMapping[value.charCodeAt(0) - 65];
      setAnswers((prev) => ({ ...prev, [questionId]: originalOption }));
    } else {
      setAnswers((prev) => ({ ...prev, [questionId]: value }));
    }
  };

  // Submit test
  const handleSubmit = async (reason = "Test completed") => {
    if (isSubmitting) return;
    setIsSubmitting(true);

    try {
      const res = await api.post(`/submissions/${id}/submit`, {
        answers,
        submissionReason: reason,
        violationReason,
      });

      if (cameraStream) cameraStream.getTracks().forEach((t) => t.stop());
      if (document.fullscreenElement) document.exitFullscreen();

      setResultData({ score: res.data?.score, reason, violation: violationReason });
      setShowResult(true);

      setTimeout(() => navigate("/my-scores"), 3000);
    } catch (err) {
      console.error("Submit error:", err);
      alert("Error submitting test.");
      setIsSubmitting(false);
    }
  };

  const handleRunCode = () => {
    speakViolation("Code execution will be implemented later.");
    alert("Code execution feature will be implemented.");
  };

  const formatTime = (seconds) => `${Math.floor(seconds / 60)}:${(seconds % 60).toString().padStart(2, "0")}`;
  const answeredCount = questions.filter((q) => answers[q._id]).length;
  const notAnsweredCount = questions.length - answeredCount;
  const allAnswered = answeredCount === questions.length;

  // Loading screen
  if (loading) return <div>Loading test...</div>;
  if (!test || questions.length === 0) return <div>No test found</div>;

  // Test not started yet → Instructions
  if (!started) {
    return (
      <div>
        <h1>{test.name}</h1>
        <ul>{INSTRUCTIONS.map((i, idx) => <li key={idx}>{i}</li>)}</ul>
        <button onClick={startTest} disabled={cameraError}>
          Start Test
        </button>
      </div>
    );
  }

  // Main test UI
  const currentQuestion = questions[currentQuestionIndex];
  const isCoding = currentQuestion?.type === "coding";
  const isLongAnswer = currentQuestion?.type === "long";

  return (
    <div>
      {/* Sidebar */}
      <div>
        {questions.map((q, idx) => {
          const isAnswered = !!answers[q._id];
          const isCurrent = idx === currentQuestionIndex;
          return (
            <button key={q._id} onClick={() => setCurrentQuestionIndex(idx)}>
              {isCurrent ? `[${idx + 1}]` : idx + 1}
            </button>
          );
        })}
        <div>Answered: {answeredCount} | Remaining: {notAnsweredCount}</div>
      </div>

      {/* Timer */}
      <div>{formatTime(timeLeft)}</div>

      {/* Question */}
      <div>
        <h2>Question {currentQuestionIndex + 1}</h2>
        <p>{currentQuestion?.question}</p>
        {isCoding && (
          <textarea
            value={answers[currentQuestion._id] || ""}
            onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
          />
        )}
        {currentQuestion?.options?.length > 0 &&
          currentQuestion.options.map((opt, idx) => {
            const letter = String.fromCharCode(65 + idx);
            return (
              <label key={idx}>
                <input
                  type="radio"
                  name={currentQuestion._id}
                  value={letter}
                  checked={answers[currentQuestion._id] === (currentQuestion.optionMapping ? currentQuestion.optionMapping[idx] : letter)}
                  onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
                />
                {letter}. {opt}
              </label>
            );
          })}
        {isLongAnswer && (
          <textarea
            value={answers[currentQuestion._id] || ""}
            onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
          />
        )}
      </div>

      {/* Navigation */}
      <div>
        <button onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))} disabled={currentQuestionIndex === 0}>
          Previous
        </button>
        {currentQuestionIndex < questions.length - 1 ? (
          <button onClick={() => setCurrentQuestionIndex(currentQuestionIndex + 1)}>Next</button>
        ) : (
          <button onClick={() => handleSubmit()} disabled={isSubmitting}>
            {isSubmitting ? "Submitting..." : "Submit Test"}
          </button>
        )}
      </div>

      {/* Result */}
      {showResult && (
        <div>
          <h2>Test Submitted!</h2>
          <p>
            Score: {resultData?.score?.obtainedMarks || 0}/{resultData?.score?.totalMarks || 0}
          </p>
          {resultData?.violation && <p>Violation: {resultData.violation}</p>}
        </div>
      )}
    </div>
  );
}
