// src/pages/MyScores.js
import { useEffect, useState } from "react";
import api from "../utils/api";
import "./myscores.css";

export default function MyScores() {
  const [scores, setScores] = useState([]);
  const [selectedTest, setSelectedTest] = useState(null);

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const { data } = await api.get("/scores"); // GET scores
        console.log("Fetched scores:", data);

        const formattedScores = (data.scores || []).map((score) => ({
          id: score._id,
          testName: score.testName,
          totalMarks: score.totalMarks,
          obtainedMarks: score.obtainedMarks,
        }));

        setScores(formattedScores);
      } catch (err) {
        console.error("Error fetching scores:", err.response || err);
      }
    };

    fetchScores();
  }, []);

  const getColor = (percent) => {
    if (percent < 40) return "#f87171";
    if (percent <= 85) return "#facc15";
    return "#34d399";
  };

  const generateReport = (score) => {
    const percent = ((score.obtainedMarks / score.totalMarks) * 100).toFixed(2);
    return `You attempted "${score.testName}". You scored ${score.obtainedMarks} out of ${score.totalMarks} (${percent}%). ${
      percent >= 85
        ? "Excellent performance!"
        : percent >= 40
        ? "Good effort! You passed."
        : "Performance below passing. Try again."
    }`;
  };

  return (
    <div className="myscores-container">
      <h1>My Scores</h1>
      <div className="scores-grid">
        {scores.length === 0 ? (
          <p>No scores available yet.</p>
        ) : (
          scores.map((score) => {
            const percent = (score.obtainedMarks / score.totalMarks) * 100;
            return (
              <div
                key={score.id}
                className="score-card"
                style={{ borderColor: getColor(percent) }}
                onClick={() =>
                  setSelectedTest(selectedTest === score.id ? null : score.id)
                }
              >
                <h2>{score.testName}</h2>
                <p>
                  Score:{" "}
                  <span style={{ color: getColor(percent) }}>
                    {score.obtainedMarks}/{score.totalMarks} ({percent.toFixed(2)}%)
                  </span>
                </p>
                {selectedTest === score.id && (
                  <div className="report">
                    <p>{generateReport(score)}</p>
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
