// src/pages/MyTests.js
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import { FaClock, FaStar } from "react-icons/fa";
import "./mytests.css";

export default function MyTests() {
  const [tests, setTests] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTests = async () => {
      try {
        const { data } = await api.get("/student/tests");
        const formattedTests = (data?.tests || []).map((test) => ({
          id: test._id,
          name: test.name || "Untitled Test",
          duration: test.duration || 0,
          totalMarks: test.totalMarks || 0,
        }));
        setTests(formattedTests);
      } catch (err) {
        console.error("Failed to fetch tests:", err);
      }
    };
    fetchTests();
  }, []);

  const takeTest = (test) => {
    navigate(`/take-test/${test.id}`, { state: { test } });
  };

  const colors = ["#fef3c7", "#d1fae5", "#dbeafe", "#f0e6ff"];

  return (
    <div className="mytests-container">
      <header className="mytests-header">
        <h1 className="mytests-page-title">Available Tests</h1>
        <p className="mytests-page-subtitle">Select a test to start your practice</p>
      </header>

      <main className="mytests-grid">
        {tests.length === 0 ? (
          <div className="no-tests">
            <p>No tests available at the moment.</p>
          </div>
        ) : (
          tests.map((test, index) => (
            <div
              key={test.id}
              className="mytest-card"
              style={{ backgroundColor: colors[index % colors.length] }}
            >
              <h2 className="mytest-name">{test.name}</h2>

              <div className="mytest-details">
                <div className="mytest-detail-item">
                  <FaClock />
                  <span className="mytest-detail-label">Duration:</span>
                  <span className="mytest-detail-value">{test.duration} min</span>
                </div>
                <div className="mytest-detail-item">
                  <FaStar />
                  <span className="mytest-detail-label">Max Marks:</span>
                  <span className="mytest-detail-value">{test.totalMarks}</span>
                </div>
              </div>

              <button className="take-test-btn" onClick={() => takeTest(test)}>
                Take Test
              </button>
            </div>
          ))
        )}
      </main>
    </div>
  );
}
