import React, { useEffect, useState } from "react";
import api from "../utils/api";
import TestCard from "../components/TestCard";
import { useNavigate } from "react-router-dom";

export default function MyTests() {
  const [tests, setTests] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get("/tests")
      .then(res => setTests(Array.isArray(res.data) ? res.data : []))
      .catch(() => setTests([]));
  }, []);

  const handleTakeTest = (test) => {
    navigate(`/take-test/${test._id}`, { state: { test } });
  };

  // Group tests by category
  const categories = [
    { key: "mcq", label: "MCQ" },
    { key: "long", label: "Long Answer" },
    { key: "coding", label: "Coding" }
  ];

  return (
    <div className="max-w-6xl mx-auto p-8">
      <h1 className="text-2xl font-bold mb-6">My Tests</h1>
      {categories.map(cat => {
        const filtered = tests.filter(t => t.category === cat.key);
        if (filtered.length === 0) return null;
        return (
          <div key={cat.key} className="mb-10">
            <h2 className="text-xl font-semibold mb-4">{cat.label} Tests</h2>
            <div className="test-card-grid">
              {filtered.map(test => (
                <TestCard key={test._id} test={test} onTakeTest={handleTakeTest} />
              ))}
            </div>
          </div>
        );
      })}
    </div>
  );
}
