import { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import { 
  User, 
  Plus, 
  BarChart3, 
  FileText, 
  Users, 
  Edit3, 
  Save, 
  X,
  Mail,
  Phone,
  Building,
  Award,
  Camera
} from "lucide-react";
import "../dashboard.css";

export default function DashboardMentor() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatarFile, setAvatarFile] = useState(null);
  const [stats] = useState({
    totalTests: 12,
    activeStudents: 156,
    completedExams: 89,
    avgScore: 78
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetchMe();
  }, []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  const onChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith("mentor.")) {
      const key = name.split(".")[1];
      setForm({ ...form, mentor: { ...form.mentor, [key]: value } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const updateProfile = async () => {
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("email", form.email);
      if (form.mentor) {
        formData.append("currentInstitution", form.mentor.currentInstitution || "");
        formData.append("phoneNumber", form.mentor.phoneNumber || "");
        formData.append("experience", form.mentor.experience || "");
      }
      if (avatarFile) formData.append("avatar", avatarFile);

      const { data } = await api.put("/auth/me", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      localStorage.setItem("user", JSON.stringify(data.user));
      setEditing(false);
      fetchMe();
    } catch (err) {
      console.error("Update failed:", err.response?.data || err.message);
    }
  };

  const dashboardActions = [
    {
      title: "Add Test",
      description: "Create new examinations",
      icon: <Plus className="w-6 h-6" />,
      path: "/add-test",
      color: "from-blue-500 to-blue-600"
    },
    {
      title: "View Results",
      description: "Check exam results",
      icon: <BarChart3 className="w-6 h-6" />,
      path: "/view-result",
      color: "from-green-500 to-green-600"
    },
    {
      title: "View Reports",
      description: "Generate detailed reports",
      icon: <FileText className="w-6 h-6" />,
      path: "/view-report",
      color: "from-purple-500 to-purple-600"
    },
    {
      title: "Manage Students",
      description: "View student profiles",
      icon: <Users className="w-6 h-6" />,
      path: "/view-student",
      color: "from-orange-500 to-orange-600"
    }
  ];

  return (
    <div className="dashboard-container">
      {/* Header */}
      <div className="dashboard-header">
        <div className="header-content">
          <h1 className="dashboard-title">Mentor Dashboard</h1>
          <p className="dashboard-subtitle">Manage your tests and track student progress</p>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon bg-blue-500">
            <FileText className="w-6 h-6" />
          </div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.totalTests}</h3>
            <p className="stat-label">Total Tests</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon bg-green-500">
            <Users className="w-6 h-6" />
          </div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.activeStudents}</h3>
            <p className="stat-label">Active Students</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon bg-purple-500">
            <Award className="w-6 h-6" />
          </div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.completedExams}</h3>
            <p className="stat-label">Completed Exams</p>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon bg-orange-500">
            <BarChart3 className="w-6 h-6" />
          </div>
          <div className="stat-content">
            <h3 className="stat-number">{stats.avgScore}%</h3>
            <p className="stat-label">Average Score</p>
          </div>
        </div>
      </div>

      <div className="dashboard-main">
        {/* Quick Actions */}
        <div className="dashboard-section">
          <h2 className="section-title">Quick Actions</h2>
          <div className="actions-grid">
            {dashboardActions.map((action, index) => (
              <div 
                key={index}
                className="action-card-modern"
                onClick={() => navigate(action.path)}
              >
                <div className={`action-icon bg-gradient-to-r ${action.color}`}>
                  {action.icon}
                </div>
                <div className="action-content">
                  <h3 className="action-title">{action.title}</h3>
                  <p className="action-description">{action.description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Profile Section */}
        <div className="dashboard-section">
          <h2 className="section-title">Profile Information</h2>
          <div className="profile-card-modern">
            <div className="profile-header">
              <div className="avatar-container">
                {user?.avatar ? (
                  <img
                    src={`${user.avatar}?t=${new Date().getTime()}`}
                    alt="Profile"
                    className="profile-avatar"
                  />
                ) : (
                  <div className="avatar-placeholder">
                    <User className="w-12 h-12 text-gray-400" />
                  </div>
                )}
                {editing && (
                  <label className="avatar-upload">
                    <Camera className="w-4 h-4" />
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => setAvatarFile(e.target.files[0])}
                      className="hidden"
                    />
                  </label>
                )}
              </div>
              <div className="profile-info">
                <h3 className="profile-name">{user?.name || "Mentor"}</h3>
                <p className="profile-role">Senior Mentor</p>
              </div>
              {!editing && (
                <button 
                  className="edit-btn"
                  onClick={() => setEditing(true)}
                >
                  <Edit3 className="w-4 h-4" />
                  Edit Profile
                </button>
              )}
            </div>

            {!editing ? (
              <div className="profile-details">
                <div className="detail-item">
                  <Mail className="w-5 h-5 text-gray-400" />
                  <span className="detail-label">Email:</span>
                  <span className="detail-value">{user?.email || "Not provided"}</span>
                </div>
                <div className="detail-item">
                  <Building className="w-5 h-5 text-gray-400" />
                  <span className="detail-label">Institution:</span>
                  <span className="detail-value">{user?.mentor?.currentInstitution || "Not provided"}</span>
                </div>
                <div className="detail-item">
                  <Phone className="w-5 h-5 text-gray-400" />
                  <span className="detail-label">Phone:</span>
                  <span className="detail-value">{user?.mentor?.phoneNumber || "Not provided"}</span>
                </div>
                <div className="detail-item">
                  <Award className="w-5 h-5 text-gray-400" />
                  <span className="detail-label">Experience:</span>
                  <span className="detail-value">{user?.mentor?.experience || "Not provided"}</span>
                </div>
              </div>
            ) : (
              <div className="profile-form">
                <div className="form-group">
                  <label className="form-label">Full Name</label>
                  <input
                    name="name"
                    value={form.name || ""}
                    onChange={onChange}
                    placeholder="Enter your full name"
                    className="form-input"
                  />
                </div>
                <div className="form-group">
                  <label className="form-label">Institution</label>
                  <input
                    name="mentor.currentInstitution"
                    value={form.mentor?.currentInstitution || ""}
                    onChange={onChange}
                    placeholder="Current institution"
                    className="form-input"
                  />
                </div>
                <div className="form-group">
                  <label className="form-label">Phone Number</label>
                  <input
                    name="mentor.phoneNumber"
                    value={form.mentor?.phoneNumber || ""}
                    onChange={onChange}
                    placeholder="Phone number"
                    className="form-input"
                  />
                </div>
                <div className="form-group">
                  <label className="form-label">Experience</label>
                  <input
                    name="mentor.experience"
                    value={form.mentor?.experience || ""}
                    onChange={onChange}
                    placeholder="Years of experience"
                    className="form-input"
                  />
                </div>
                <div className="form-actions">
                  <button className="btn-save" onClick={updateProfile}>
                    <Save className="w-4 h-4" />
                    Save Changes
                  </button>
                  <button className="btn-cancel" onClick={() => setEditing(false)}>
                    <X className="w-4 h-4" />
                    Cancel
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
