import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Trophy, Calendar, Clock, Target, TrendingUp, Award, BookOpen, ArrowLeft } from "lucide-react";
import api from "../utils/api";

export default function MyScores() {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalTests: 0,
    averageScore: 0,
    highestScore: 0,
    totalMarks: 0,
    obtainedMarks: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchScores = async () => {
      try {
        // You might need to adjust this endpoint based on your backend
        const { data } = await api.get("/student/scores");
        console.log("Fetched scores:", data);
        setScores(data.scores || []);
        calculateStats(data.scores || []);
      } catch (err) {
        console.error("Failed to fetch scores:", err);
        // For demo purposes, using mock data if API fails
        const mockScores = [
          {
            _id: "1",
            testName: "JavaScript Fundamentals",
            totalMarks: 50,
            obtainedMarks: 42,
            percentage: 84,
            createdAt: "2024-01-15T10:30:00Z"
          },
          {
            _id: "2", 
            testName: "React Basics",
            totalMarks: 30,
            obtainedMarks: 25,
            percentage: 83.33,
            createdAt: "2024-01-10T14:20:00Z"
          }
        ];
        setScores(mockScores);
        calculateStats(mockScores);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, []);

  const calculateStats = (scoresData) => {
    if (scoresData.length === 0) {
      setStats({
        totalTests: 0,
        averageScore: 0,
        highestScore: 0,
        totalMarks: 0,
        obtainedMarks: 0
      });
      return;
    }

    const totalTests = scoresData.length;
    const totalMarks = scoresData.reduce((sum, score) => sum + score.totalMarks, 0);
    const obtainedMarks = scoresData.reduce((sum, score) => sum + score.obtainedMarks, 0);
    const averageScore = totalMarks > 0 ? ((obtainedMarks / totalMarks) * 100) : 0;
    const highestScore = Math.max(...scoresData.map(score => 
      score.percentage || ((score.obtainedMarks / score.totalMarks) * 100)
    ));

    setStats({
      totalTests,
      averageScore: Math.round(averageScore * 100) / 100,
      highestScore: Math.round(highestScore * 100) / 100,
      totalMarks,
      obtainedMarks
    });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getScoreColor = (percentage) => {
    if (percentage >= 90) return "text-green-600 bg-green-100";
    if (percentage >= 75) return "text-blue-600 bg-blue-100";
    if (percentage >= 60) return "text-yellow-600 bg-yellow-100";
    return "text-red-600 bg-red-100";
  };

  const getGrade = (percentage) => {
    if (percentage >= 90) return "A+";
    if (percentage >= 80) return "A";
    if (percentage >= 70) return "B+";
    if (percentage >= 60) return "B";
    if (percentage >= 50) return "C";
    return "F";
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-xl font-medium text-gray-700">Loading your scores...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate("/dashboard/student")}
              className="p-2 text-gray-600 hover:text-gray-900 transition-colors duration-200"
            >
              <ArrowLeft className="h-5 w-5" />
            </button>
            <div>
              <h1 className="text-4xl font-bold text-gray-900 flex items-center">
                <Trophy className="mr-3 text-yellow-500" size={40} />
                My Test Scores
              </h1>
              <p className="text-gray-600 mt-2">Track your performance and progress</p>
            </div>
          </div>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Tests</p>
                <p className="text-3xl font-bold text-gray-900">{stats.totalTests}</p>
              </div>
              <div className="p-3 bg-blue-100 rounded-full">
                <BookOpen className="h-6 w-6 text-blue-600" />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Average Score</p>
                <p className="text-3xl font-bold text-gray-900">{stats.averageScore}%</p>
              </div>
              <div className="p-3 bg-green-100 rounded-full">
                <Target className="h-6 w-6 text-green-600" />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Highest Score</p>
                <p className="text-3xl font-bold text-gray-900">{stats.highestScore}%</p>
              </div>
              <div className="p-3 bg-yellow-100 rounded-full">
                <Award className="h-6 w-6 text-yellow-600" />
              </div>
            </div>
          </div>

          <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Points</p>
                <p className="text-3xl font-bold text-gray-900">{stats.obtainedMarks}/{stats.totalMarks}</p>
              </div>
              <div className="p-3 bg-purple-100 rounded-full">
                <TrendingUp className="h-6 w-6 text-purple-600" />
              </div>
            </div>
          </div>
        </div>

        {/* Scores List */}
        <div className="bg-white rounded-xl shadow-lg border border-gray-100">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-xl font-semibold text-gray-900">Test Results</h2>
          </div>

          {scores.length === 0 ? (
            <div className="text-center py-16">
              <div className="w-16 h-16 bg-gray-100 rounded-xl flex items-center justify-center mx-auto mb-4">
                <Trophy className="h-8 w-8 text-gray-400" />
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">No Test Results Yet</h3>
              <p className="text-gray-600 mb-6">Take your first test to see your scores here.</p>
              <button
                onClick={() => navigate("/my-tests")}
                className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-all duration-200"
              >
                Take a Test
              </button>
            </div>
          ) : (
            <div className="divide-y divide-gray-200">
              {scores.map((score, index) => {
                const percentage = score.percentage || ((score.obtainedMarks / score.totalMarks) * 100);
                const grade = getGrade(percentage);
                const colorClass = getScoreColor(percentage);

                return (
                  <div key={score._id || index} className="p-6 hover:bg-gray-50 transition-colors duration-200">
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <h3 className="text-lg font-semibold text-gray-900 mb-2">
                          {score.testName}
                        </h3>
                        <div className="flex items-center space-x-6 text-sm text-gray-600">
                          <div className="flex items-center">
                            <Calendar className="h-4 w-4 mr-1" />
                            {formatDate(score.createdAt)}
                          </div>
                          <div className="flex items-center">
                            <Target className="h-4 w-4 mr-1" />
                            {score.obtainedMarks}/{score.totalMarks} marks
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <div className={`px-4 py-2 rounded-full text-sm font-semibold ${colorClass}`}>
                          {Math.round(percentage * 100) / 100}%
                        </div>
                        <div className="text-2xl font-bold text-gray-700 bg-gray-100 px-3 py-1 rounded-lg">
                          {grade}
                        </div>
                      </div>
                    </div>

                    {/* Progress Bar */}
                    <div className="mt-4">
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div
                          className={`h-2 rounded-full transition-all duration-500 ${
                            percentage >= 90 ? "bg-green-500" :
                            percentage >= 75 ? "bg-blue-500" :
                            percentage >= 60 ? "bg-yellow-500" : "bg-red-500"
                          }`}
                          style={{ width: `${Math.min(percentage, 100)}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="mt-8 flex justify-center space-x-4">
          <button
            onClick={() => navigate("/my-tests")}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-all duration-200 flex items-center space-x-2"
          >
            <BookOpen className="h-5 w-5" />
            <span>Take More Tests</span>
          </button>
          <button
            onClick={() => navigate("/achievements")}
            className="px-6 py-3 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-all duration-200 flex items-center space-x-2"
          >
            <Award className="h-5 w-5" />
            <span>View Achievements</span>
          </button>
        </div>
      </div>
    </div>
  );
}
