import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../utils/api";

export default function TakeTest() {
  const { state } = useLocation();
  const test = state?.test;
  const navigate = useNavigate();
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(test?.duration * 60 || 0);

  // Timer
  useEffect(() => {
    if (!timeLeft) handleSubmit();
    const interval = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);
    return () => clearInterval(interval);
  }, [timeLeft]);

  // Prevent tab switching
  useEffect(() => {
    const handleVisibility = () => {
      if (document.hidden) alert("Do not switch tabs! This may end your test.");
    };
    document.addEventListener("visibilitychange", handleVisibility);
    return () => document.removeEventListener("visibilitychange", handleVisibility);
  }, []);

  const handleChange = (qId, value) => {
    setAnswers({ ...answers, [qId]: value });
  };

  const handleSubmit = async () => {
    try {
      await api.post(`/student/tests/${test._id}/submit`, { answers });
      alert("Test submitted!");
      navigate("/dashboard"); // redirect to dashboard
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>{test?.name}</h1>
      <p style={{ float: "right" }}>Time Left: {Math.floor(timeLeft / 60)}:{timeLeft % 60}</p>
      <div style={{ marginTop: "3rem" }}>
        {test?.questions?.map((q, index) => (
          <div key={q._id} style={{ marginBottom: "2rem" }}>
            <p><strong>Q{index + 1}:</strong> {q.question}</p>
            {q.options ? (
              q.options.map((opt, i) => (
                <label key={i}>
                  <input
                    type="radio"
                    name={q._id}
                    value={opt}
                    onChange={(e) => handleChange(q._id, e.target.value)}
                  />
                  {opt}
                </label>
              ))
            ) : (
              <textarea
                placeholder="Your answer"
                onChange={(e) => handleChange(q._id, e.target.value)}
                style={{ width: "100%", height: "80px" }}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
