import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock } from "lucide-react";
import api from "../utils/api";
import "./taketest.css";

const INSTRUCTIONS = [
  "Do not switch tabs, minimize, copy, paste, or take screenshots.",
  "Your webcam will be monitored during the test.",
  "You will receive up to 3 warnings for violations. On the 3rd, the test will auto-submit.",
  "Answer all questions before submitting. Timer is at the top right.",
  "For coding questions, select your language and write code in the editor.",
];

const LANGUAGES = ["Python", "JavaScript", "Java", "C++"];

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [violationReason, setViolationReason] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedLang, setSelectedLang] = useState(LANGUAGES[0]);
  const [code, setCode] = useState("");
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState(null);

  // Fetch test
  useEffect(() => {
    if (!test && id) {
      const fetchTest = async () => {
        try {
          const res = await api.get(`/api/tests/${id}`);
          setTest(res.data);
          setTimeLeft(res.data.duration * 60);
        } catch {
          alert("Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else if (test) {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  // Start Test
  const startTest = async () => {
    setStarted(true);
    if (document.documentElement.requestFullscreen) {
      try { await document.documentElement.requestFullscreen(); } catch {}
    }
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: { width: 320, height: 240 } });
      setCameraStream(stream);
      const video = document.getElementById("cameraFeed");
      if (video) video.srcObject = stream;
    } catch {
      alert("Camera access is required!");
    }
  };

  // Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            handleSubmit("Time's up!");
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  // Violation detection
  useEffect(() => {
    if (!started) return;
    const handleViolation = (message) => {
      if (violationCount < 2) {
        setViolationCount(v => v + 1);
        alert(`⚠️ ${message}\nWarning ${violationCount + 1}/3`);
      } else {
        setViolationReason(message);
        handleSubmit(`Test ended due to violation: ${message}`);
      }
    };

    const handleCopy = e => { e.preventDefault(); handleViolation("Copying is not allowed!"); };
    const handlePaste = e => { e.preventDefault(); handleViolation("Pasting is not allowed!"); };
    const handleKeyDown = e => {
      if (["F12","PrintScreen"].includes(e.key) || (e.ctrlKey && e.shiftKey && ["I","S"].includes(e.key))) {
        e.preventDefault(); handleViolation("Developer tools or screenshots are not allowed!");
      }
    };
    const handleVisibilityChange = () => { if (document.hidden) handleViolation("Switching tabs is not allowed!"); };
    const handleWindowBlur = () => handleViolation("Minimizing or switching application is not allowed!");
    const handleContextMenu = e => { e.preventDefault(); handleViolation("Right-click is disabled!"); };

    document.addEventListener("copy", handleCopy);
    document.addEventListener("paste", handlePaste);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("visibilitychange", handleVisibilityChange);
    document.addEventListener("contextmenu", handleContextMenu);
    window.addEventListener("blur", handleWindowBlur);

    return () => {
      document.removeEventListener("copy", handleCopy);
      document.removeEventListener("paste", handlePaste);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
      document.removeEventListener("contextmenu", handleContextMenu);
      window.removeEventListener("blur", handleWindowBlur);
    };
  }, [started, violationCount]);

  // Answer change
  const handleAnswerChange = (questionId, value) => setAnswers(prev => ({ ...prev, [questionId]: value }));

  // Submit
  const handleSubmit = async (reason="Test completed") => {
    if (isSubmitting) return;
    setIsSubmitting(true);
    try {
      const res = await api.post(`/tests/${id}/submit`, {
        answers, code: test?.category==="coding"?code:undefined,
        language: test?.category==="coding"?selectedLang:undefined,
        submissionReason: reason, violationReason
      });
      if (cameraStream) cameraStream.getTracks().forEach(track=>track.stop());
      if (document.fullscreenElement) document.exitFullscreen();
      setResultData({ score: res.data?.score, reason, violation: violationReason });
      setShowResult(true);
      setTimeout(()=>navigate("/my-scores"),4000);
    } catch { alert("Error submitting test."); setIsSubmitting(false); }
  };

  const formatTime = seconds => `${Math.floor(seconds/60)}:${(seconds%60).toString().padStart(2,"0")}`;
  const questionsArr = Array.isArray(test?.questions)?test.questions:[];
  const answeredCount = questionsArr.filter(q => answers[q._id]).length;
  const notAnsweredCount = questionsArr.length - answeredCount;
  const currentQuestion = questionsArr[currentQuestionIndex];
  const isCoding = test?.category === "coding";

  // --- Instructions page ---
  if (!started) return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-400 to-purple-300 p-4">
      <div className="bg-white rounded-2xl shadow-2xl p-10 max-w-lg w-full flex flex-col items-center">
        <h1 className="text-3xl font-bold text-gray-800 mb-4">{test?.name}</h1>
        <h2 className="text-xl font-semibold mb-2 text-center">Instructions</h2>
        <ul className="list-disc list-inside text-gray-700 space-y-1 text-base">
          {INSTRUCTIONS.map((inst,i)=> <li key={i}>{inst}</li>)}
        </ul>
        <div className="flex justify-between w-full mt-4">
          <span>Duration:</span><span>{test?.duration || 0} min</span>
        </div>
        <div className="flex justify-between w-full mb-4">
          <span>Total Marks:</span><span>{test?.totalMarks || 0}</span>
        </div>
        <button onClick={startTest} className="mt-4 px-8 py-4 bg-blue-600 text-white rounded-xl text-lg font-bold shadow-lg hover:bg-blue-700 transition">Start Test</button>
      </div>
    </div>
  );

  // --- Test page ---
  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* Timer & Webcam */}
      <div className="fixed top-6 right-8 z-20 flex flex-col items-end space-y-4">
        <div className={`flex items-center px-6 py-3 rounded-xl text-lg font-bold shadow-lg ${timeLeft<=300?"bg-red-100 text-red-800":"bg-blue-100 text-blue-800"}`}>
          <Clock className="mr-2" size={24}/> {formatTime(timeLeft)}
        </div>
        <video id="cameraFeed" autoPlay playsInline muted className="w-36 h-28 bg-black rounded-lg border-2 border-blue-500 shadow"/>
      </div>

      {/* Result Modal */}
      {showResult && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl shadow-2xl p-10 max-w-md w-full text-center">
            <h2 className="text-2xl font-bold mb-4">Test Submitted</h2>
            <p className="mb-2 text-lg">Score: {resultData?.score?.obtainedMarks} / {resultData?.score?.totalMarks}</p>
            {resultData?.violation && <p className="text-red-600 font-semibold mb-2">Violation: {resultData.violation}</p>}
            <p className="text-gray-600">{resultData?.reason}</p>
            <p className="text-gray-500 mt-4">Redirecting to My Scores...</p>
          </div>
        </div>
      )}

      {/* Main layout */}
      <div className="flex flex-1">
        {/* Sidebar */}
        <div className="w-64 bg-white shadow-lg p-6 flex flex-col items-center">
          <h3 className="text-lg font-semibold mb-4">Questions</h3>
          <div className="flex flex-wrap gap-3 justify-center mb-4">
            {questionsArr.map((q, idx) => {
              const isAnswered = !!answers[q._id]; 
              const isCurrent = idx === currentQuestionIndex;
              return (
                <button key={q._id} onClick={()=>setCurrentQuestionIndex(idx)}
                  className={`w-10 h-10 rounded-full border-2 flex items-center justify-center text-base font-bold transition
                    ${isCurrent?"bg-blue-600 text-white border-blue-600 scale-110":isAnswered?"bg-green-100 text-green-800 border-green-500":"bg-gray-100 text-gray-600 border-gray-300"}`}
                  title={isAnswered?"Answered":"Not Answered"}>{idx+1}</button>
              )
            })}
          </div>
          <div className="mt-4 text-center">
            <span className="inline-block px-3 py-1 rounded-full bg-green-100 text-green-700 font-semibold mr-2">Answered: {answeredCount}</span>
            <span className="inline-block px-3 py-1 rounded-full bg-gray-200 text-gray-700 font-semibold">Not Answered: {notAnsweredCount}</span>
          </div>
        </div>

        {/* Question / Coding */}
        <div className="flex-1 flex flex-col items-center justify-center p-8">
          {isCoding ? (
            <div className="w-full flex flex-row gap-6 max-w-5xl">
              <div className="flex-1 bg-white rounded-xl shadow-lg p-6">
                <h2 className="text-xl font-bold mb-4">{currentQuestion?.question || "No problem statement"}</h2>
                <p className="text-gray-700 mb-2">{currentQuestion?.description || ""}</p>
                <div className="mt-2 text-sm text-gray-500">Marks: {currentQuestion?.marks || 0}</div>
              </div>
              <div className="flex-1 flex flex-col gap-4">
                <div className="bg-white rounded-xl shadow-lg p-4 flex flex-col gap-4">
                  <div className="flex items-center gap-2">
                    <label className="font-semibold">Language:</label>
                    <select value={selectedLang} onChange={e=>setSelectedLang(e.target.value)} className="border rounded px-3 py-1">
                      {LANGUAGES.map(lang=><option key={lang} value={lang}>{lang}</option>)}
                    </select>
                  </div>
                  <textarea value={code} onChange={e=>setCode(e.target.value)} rows={14} className="w-full border rounded p-3 font-mono text-base" placeholder="Write your code here..."/>
                </div>
                <button onClick={()=>handleSubmit()} disabled={isSubmitting || !code.trim()} className="px-8 py-3 bg-green-600 text-white rounded-lg font-bold text-lg shadow-lg disabled:opacity-60">{isSubmitting?"Submitting...":"Submit Test"}</button>
              </div>
            </div>
          ) : (
            <div className="bg-white rounded-xl shadow-lg p-10 max-w-2xl w-full flex flex-col items-center">
              <h2 className="text-2xl font-bold mb-4">Question {currentQuestionIndex+1} of {questionsArr.length} <span className="ml-4 text-base font-normal text-gray-500">({currentQuestion?.marks || 0} marks)</span></h2>
              <p className="text-lg mb-6 text-center">{currentQuestion?.question || "No question text available"}</p>

              {/* MCQ */}
              {currentQuestion?.options && <div className="space-y-3 w-full">
                {currentQuestion.options.map((option, idx)=>(
                  <label key={idx} className={`flex items-center p-4 rounded-lg border-2 cursor-pointer w-full transition ${answers[currentQuestion._id]===option?"border-blue-500 bg-blue-50":"border-gray-200"}`}>
                    <input type="radio" name={currentQuestion._id} value={option} checked={answers[currentQuestion._id]===option} onChange={e=>handleAnswerChange(currentQuestion._id,e.target.value)} className="sr-only"/>
                    <div className={`w-5 h-5 rounded-full border-2 mr-3 flex items-center justify-center ${answers[currentQuestion._id]===option?"border-blue-500 bg-blue-500":"border-gray-300"}`}>
                      {answers[currentQuestion._id]===option && <div className="w-2 h-2 bg-white rounded-full"></div>}
                    </div>
                    <span>{option}</span>
                  </label>
                ))}
              </div>}

              {/* Long answer */}
              {currentQuestion?.options==null && <textarea value={answers[currentQuestion._id]||""} onChange={e=>handleAnswerChange(currentQuestion._id,e.target.value)} rows={6} className="w-full border rounded p-3 mt-2" placeholder="Type your answer here..."/>}

              {/* Navigation */}
              <div className="flex justify-between mt-8 w-full">
                <button onClick={()=>setCurrentQuestionIndex(Math.max(0,currentQuestionIndex-1))} disabled={currentQuestionIndex===0} className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg disabled:opacity-50">Previous</button>
                {currentQuestionIndex<questionsArr.length-1 ? (
                  <button onClick={()=>setCurrentQuestionIndex(currentQuestionIndex+1)} className="px-6 py-2 bg-blue-600 text-white rounded-lg">Next</button>
                ) : (
                  <button onClick={()=>handleSubmit()} disabled={isSubmitting || answeredCount!==questionsArr.length} className="px-8 py-3 bg-green-600 text-white rounded-lg font-bold text-lg shadow-lg disabled:opacity-60">{isSubmitting?"Submitting...":"Submit Test"}</button>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
