import React, { useState } from "react";
import axios from "axios";
import "./AddTest.css"; // import the new CSS

export default function AddTest() {
  const [testType, setTestType] = useState("");
  const [numQuestions, setNumQuestions] = useState(1);
  const [questions, setQuestions] = useState([]);
  const [currentQIndex, setCurrentQIndex] = useState(0);
  const [currentQ, setCurrentQ] = useState({
    question: "",
    options: ["", "", "", ""],
    correctOption: "A",
    marks: "",
    expectedOutput: "",
  });
  const [showAI, setShowAI] = useState(false);
  const [aiQuestion, setAiQuestion] = useState({ question: "", options: [], correctOption: "A", marks: "" });

  const handleTestTypeSelect = (type) => {
    setTestType(type);
    setQuestions([]);
    setCurrentQIndex(0);
    setCurrentQ({
      question: "",
      options: ["", "", "", ""],
      correctOption: "A",
      marks: "",
      expectedOutput: "",
    });
  };

  const handleOptionChange = (index, value) => {
    const updatedOptions = [...currentQ.options];
    updatedOptions[index] = value;
    setCurrentQ({ ...currentQ, options: updatedOptions });
  };

  const handleAddQuestion = () => {
    if (!currentQ.question.trim()) {
      alert("Please enter the question!");
      return;
    }

    setQuestions([...questions, { ...currentQ, type: testType }]);
    setCurrentQIndex(currentQIndex + 1);

    setCurrentQ({
      question: "",
      options: ["", "", "", ""],
      correctOption: "A",
      marks: "",
      expectedOutput: "",
    });
  };

  const handleSubmit = async () => {
    try {
      // Make sure your backend schema matches these fields
      const res = await axios.post("http://localhost:5000/api/tests", {
        type: testType,
        questions,
      });
      alert(res.data.message || "Test saved successfully!");
      setTestType("");
      setQuestions([]);
      setNumQuestions(1);
      setCurrentQIndex(0);
    } catch (error) {
      console.error(error.response || error);
      alert("Error saving test!");
    }
  };

  const handleAddAIQuestion = () => {
    if (!aiQuestion.question) return;
    setQuestions([...questions, { ...aiQuestion, type: testType }]);
    setShowAI(false);
    setAiQuestion({ question: "", options: [], correctOption: "A", marks: "" });
  };

  const isLastQuestion = currentQIndex + 1 === parseInt(numQuestions);

  return (
    <div className="dashboard-container">
      <h1 className="dashboard-title">Add Test</h1>

      {!testType && (
        <div className="test-type-buttons">
          {["MCQ", "Long", "Coding"].map((type) => (
            <button
              key={type}
              className="test-type-btn"
              onClick={() => handleTestTypeSelect(type)}
            >
              {type} Test
            </button>
          ))}
        </div>
      )}

      {testType && questions.length === 0 && (
        <div className="num-questions">
          <label>Number of Questions:</label>
          <input
            type="number"
            min="1"
            value={numQuestions}
            onChange={(e) => setNumQuestions(e.target.value)}
          />
        </div>
      )}

      {testType && questions.length < numQuestions && (
        <div className="question-form">
          <h2>
            Question {currentQIndex + 1} of {numQuestions}
          </h2>
          <textarea
            placeholder="Enter your question here..."
            value={currentQ.question}
            onChange={(e) => setCurrentQ({ ...currentQ, question: e.target.value })}
          />

          {testType === "MCQ" &&
            ["A", "B", "C", "D"].map((opt, i) => (
              <div key={i} className="mcq-option">
                <input
                  type="radio"
                  name="correctOption"
                  value={opt}
                  checked={currentQ.correctOption === opt}
                  onChange={(e) => setCurrentQ({ ...currentQ, correctOption: e.target.value })}
                />
                <input
                  type="text"
                  placeholder={`Option ${opt}`}
                  value={currentQ.options[i]}
                  onChange={(e) => handleOptionChange(i, e.target.value)}
                />
              </div>
            ))}

          {(testType === "MCQ" || testType === "Long") && (
            <div className="marks-input">
              <label>Marks:</label>
              <input
                type="number"
                value={currentQ.marks}
                onChange={(e) => setCurrentQ({ ...currentQ, marks: e.target.value })}
              />
            </div>
          )}

          {testType === "Coding" && (
            <div className="coding-output">
              <label>Expected Output:</label>
              <input
                type="text"
                value={currentQ.expectedOutput}
                onChange={(e) => setCurrentQ({ ...currentQ, expectedOutput: e.target.value })}
              />
            </div>
          )}

          <div className="form-buttons">
            <button onClick={handleAddQuestion}>
              {isLastQuestion ? "Finish Adding Questions" : "Add Question"}
            </button>
            <button onClick={() => setShowAI(true)} className="ai-btn">
              AI Help
            </button>
          </div>
        </div>
      )}

      {questions.length === parseInt(numQuestions) && (
        <div className="submit-btn-container">
          <button onClick={handleSubmit}>Submit Test</button>
        </div>
      )}

      {/* Preview Questions */}
      {questions.length > 0 && (
        <div className="questions-preview">
          <h3>Questions Added:</h3>
          <ul>
            {questions.map((q, i) => (
              <li key={i}>
                {q.type}: {q.question} {q.marks && `(${q.marks} marks)`}{" "}
                {q.type === "MCQ" && `- Answer: ${q.correctOption}`}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* AI Modal */}
      {showAI && (
        <div className="ai-modal">
          <div className="ai-content">
            <h2>AI Question Generator</h2>
            <textarea
              placeholder="AI generated question..."
              value={aiQuestion.question}
              onChange={(e) => setAiQuestion({ ...aiQuestion, question: e.target.value })}
            />
            {testType === "MCQ" &&
              [0, 1, 2, 3].map((i) => (
                <input
                  key={i}
                  type="text"
                  placeholder={`Option ${String.fromCharCode(65 + i)}`}
                  value={aiQuestion.options[i] || ""}
                  onChange={(e) => {
                    const opts = [...(aiQuestion.options || ["", "", "", ""])];
                    opts[i] = e.target.value;
                    setAiQuestion({ ...aiQuestion, options: opts });
                  }}
                />
              ))}
            <div className="ai-buttons">
              <button onClick={handleAddAIQuestion}>Add Question</button>
              <button onClick={() => setShowAI(false)}>Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
