import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import api from "../utils/api";
import "./taketest.css";

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [warning, setWarning] = useState("");

  // ✅ Fetch test if not passed via state
  useEffect(() => {
    if (!test) {
      const fetchTest = async () => {
        try {
          const res = await api.get(`/api/tests/${id}`); // ✅ FIXED ROUTE
          console.log("✅ Test fetched:", res.data);
          setTest(res.data);
          setTimeLeft(res.data.duration * 60);
        } catch (err) {
          console.error(err);
          alert("Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  // ✅ Start test + fullscreen
  const startTest = () => {
    setStarted(true);
    const elem = document.documentElement;
    if (elem.requestFullscreen) elem.requestFullscreen();
  };

  // ✅ Camera setup
  useEffect(() => {
    if (started) {
      const video = document.getElementById("cameraFeed");
      if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
        navigator.mediaDevices
          .getUserMedia({ video: true })
          .then((stream) => {
            video.srcObject = stream;
          })
          .catch((err) => {
            console.error("Camera access denied:", err);
          });
      }
    }
  }, [started]);

  // ✅ Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);

      return () => clearInterval(timer);
    }

    if (timeLeft === 0 && started) {
      alert("⏱ Time is up! Test will be submitted.");
      handleSubmit();
    }
  }, [started, timeLeft]);

  // ✅ Copy detection
  useEffect(() => {
    if (started) {
      const handleCopy = (e) => {
        e.preventDefault();
        if (violationCount === 0) {
          setWarning("⚠️ Copying is not allowed! This is your first warning.");
          setViolationCount(1);
        } else {
          alert("❌ Test ended due to copying.");
          handleSubmit();
        }
      };
      document.addEventListener("copy", handleCopy);

      return () => document.removeEventListener("copy", handleCopy);
    }
  }, [started, violationCount]);

  // ✅ Tab switching detection
  useEffect(() => {
    if (started) {
      const handleVisibilityChange = () => {
        if (document.hidden) {
          if (violationCount === 0) {
            setWarning(
              "⚠️ Switching tabs is not allowed! This is your first warning."
            );
            setViolationCount(1);
          } else {
            alert("❌ Test ended due to tab switching.");
            handleSubmit();
          }
        }
      };
      document.addEventListener("visibilitychange", handleVisibilityChange);

      return () =>
        document.removeEventListener("visibilitychange", handleVisibilityChange);
    }
  }, [started, violationCount]);

  // ✅ Answer selection
  const handleChange = (qId, value) => {
    setAnswers((prev) => ({ ...prev, [qId]: value }));
  };

  // ✅ Submit
  const handleSubmit = async () => {
    try {
      await api.post(`/api/tests/${id}/submit`, { answers });
      alert("✅ Test submitted!");
      navigate("/my-scores");
    } catch (err) {
      console.error(err);
    }
  };

  if (!test) return <p>Loading test...</p>;

  const questions = Array.isArray(test.questions) ? test.questions : [];

  // ✅ Show instructions before start
  if (!started) {
    return (
      <div className="instruction-container">
        <div className="instruction-box">
          <h1>{test.name}</h1>
          <h3>Instructions:</h3>
          <ul>
            <li>Time allotted: {test.duration} minutes</li>
            <li>Do not switch tabs</li>
            <li>Do not copy questions or answers</li>
            <li>First violation: warning, second violation: test ends</li>
            <li>Once started, test cannot be paused</li>
          </ul>
          <button onClick={startTest}>Start Test</button>
        </div>
      </div>
    );
  }

  return (
    <div className="taketest-container">
      {warning && <div className="warning">{warning}</div>}

      {/* Header */}
      <div className="taketest-header">
        <h1>{test.name}</h1>
        <div className="timer">
          Time Left: {Math.floor(timeLeft / 60)}:
          {("0" + (timeLeft % 60)).slice(-2)}
        </div>
      </div>

      <div className="taketest-body">
        {/* Left Sidebar */}
        <div className="sidebar">
          <div className="question-circle">{questions.length}</div>
          <p>Total Questions</p>
        </div>

        {/* Middle - Questions */}
        <div className="question-area">
          {questions.length === 0 && (
            <p>No questions found for this test. Please check backend.</p>
          )}

          {questions.map((q, idx) => (
            <div key={q._id} className="question-card">
              <p>
                Q{idx + 1}: {q.question}
              </p>
              {q.options && q.options.length > 0 ? (
                q.options.map((opt, i) => (
                  <label key={i} className="option">
                    <input
                      type="radio"
                      name={q._id}
                      value={opt}
                      onChange={(e) => handleChange(q._id, e.target.value)}
                    />
                    {opt}
                  </label>
                ))
              ) : (
                <textarea
                  placeholder="Your answer"
                  onChange={(e) => handleChange(q._id, e.target.value)}
                />
              )}
            </div>
          ))}

          {questions.length > 0 && (
            <button className="submit-btn" onClick={handleSubmit}>
              Submit Test
            </button>
          )}
        </div>

        {/* Right - Camera */}
        <div className="camera-container">
          <video id="cameraFeed" autoPlay playsInline></video>
        </div>
      </div>
    </div>
  );
}
