import React, { useState } from "react";
import axios from "axios";

export default function AddTest() {
  const [testType, setTestType] = useState("");
  const [numQuestions, setNumQuestions] = useState(1);
  const [questions, setQuestions] = useState([]);
  const [currentQIndex, setCurrentQIndex] = useState(0);
  const [currentQ, setCurrentQ] = useState({
    question: "",
    options: ["", "", "", ""],
    correctOption: "A",
    marks: "",
    expectedOutput: "",
  });

  const handleTestTypeSelect = (type) => {
    setTestType(type);
    setQuestions([]);
    setCurrentQIndex(0);
    setCurrentQ({
      question: "",
      options: ["", "", "", ""],
      correctOption: "A",
      marks: "",
      expectedOutput: "",
    });
  };

  const handleOptionChange = (index, value) => {
    const updatedOptions = [...currentQ.options];
    updatedOptions[index] = value;
    setCurrentQ({ ...currentQ, options: updatedOptions });
  };

  const handleAddQuestion = () => {
    if (!currentQ.question.trim()) {
      alert("Please enter the question!");
      return;
    }

    setQuestions([...questions, { ...currentQ, type: testType }]);

    // Reset form for next question
    setCurrentQ({
      question: "",
      options: ["", "", "", ""],
      correctOption: "A",
      marks: "",
      expectedOutput: "",
    });

    setCurrentQIndex(currentQIndex + 1);
  };

  const handleSubmit = async () => {
    try {
      await axios.post("http://localhost:5000/api/tests", {
        type: testType,
        questions,
      });
      alert("Test saved successfully!");
      setTestType("");
      setQuestions([]);
      setNumQuestions(1);
      setCurrentQIndex(0);
    } catch (error) {
      console.error(error);
      alert("Error saving test!");
    }
  };

  const isLastQuestion = currentQIndex + 1 === parseInt(numQuestions);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center">Add Test</h1>

      {/* Test Type Selection */}
      {!testType && (
        <div className="flex justify-center gap-4 mb-6">
          {["MCQ", "Long", "Coding"].map((type) => (
            <button
              key={type}
              className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded shadow-lg transition"
              onClick={() => handleTestTypeSelect(type)}
            >
              {type} Test
            </button>
          ))}
        </div>
      )}

      {/* Number of Questions */}
      {testType && questions.length === 0 && (
        <div className="mb-6 text-center">
          <label className="font-semibold mr-2">Number of Questions:</label>
          <input
            type="number"
            min="1"
            value={numQuestions}
            onChange={(e) => setNumQuestions(e.target.value)}
            className="border p-2 rounded w-20 text-center"
          />
        </div>
      )}

      {/* Question Form */}
      {testType && questions.length < numQuestions && (
        <div className="border p-6 rounded shadow-md bg-white mb-6">
          <h2 className="text-xl font-semibold mb-4">
            Question {currentQIndex + 1} of {numQuestions}
          </h2>

          <label className="block mb-2 font-medium">Question:</label>
          <textarea
            className="border p-2 w-full rounded mb-4"
            value={currentQ.question}
            onChange={(e) => setCurrentQ({ ...currentQ, question: e.target.value })}
          />

          {/* MCQ Options */}
          {testType === "MCQ" && (
            <>
              {["A", "B", "C", "D"].map((opt, i) => (
                <div key={i} className="flex items-center gap-2 mb-2">
                  <input
                    type="radio"
                    name="correctOption"
                    value={opt}
                    checked={currentQ.correctOption === opt}
                    onChange={(e) => setCurrentQ({ ...currentQ, correctOption: e.target.value })}
                  />
                  <input
                    type="text"
                    placeholder={`Option ${opt}`}
                    className="border p-2 w-full rounded"
                    value={currentQ.options[i]}
                    onChange={(e) => handleOptionChange(i, e.target.value)}
                  />
                </div>
              ))}

              <label className="block mt-2 font-medium">Marks:</label>
              <input
                type="number"
                className="border p-2 w-32 rounded"
                value={currentQ.marks}
                onChange={(e) => setCurrentQ({ ...currentQ, marks: e.target.value })}
              />
            </>
          )}

          {/* Long Answer */}
          {testType === "Long" && (
            <>
              <label className="block mt-2 font-medium">Marks:</label>
              <input
                type="number"
                className="border p-2 w-32 rounded"
                value={currentQ.marks}
                onChange={(e) => setCurrentQ({ ...currentQ, marks: e.target.value })}
              />
            </>
          )}

          {/* Coding */}
          {testType === "Coding" && (
            <>
              <label className="block mt-2 font-medium">Expected Output:</label>
              <input
                type="text"
                className="border p-2 w-full rounded"
                value={currentQ.expectedOutput}
                onChange={(e) => setCurrentQ({ ...currentQ, expectedOutput: e.target.value })}
              />
            </>
          )}

          <button
            onClick={handleAddQuestion}
            className="mt-4 bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 transition"
          >
            {isLastQuestion ? "Finish Adding Questions" : "Add Question"}
          </button>
        </div>
      )}

      {/* Submit Button */}
      {questions.length === parseInt(numQuestions) && (
        <div className="text-center mb-6">
          <button
            onClick={handleSubmit}
            className="bg-blue-600 text-white px-8 py-3 rounded hover:bg-blue-700 shadow-lg transition"
          >
            Submit Test
          </button>
        </div>
      )}

      {/* AI ChatGPT Button */}
      <div className="fixed bottom-6 right-6">
        <a
          href="https://chat.openai.com/"
          target="_blank"
          rel="noopener noreferrer"
          className="bg-purple-600 text-white px-4 py-3 rounded-full shadow-lg hover:bg-purple-700 transition flex items-center gap-2"
        >
          AI Help
        </a>
      </div>

      {/* Preview Added Questions */}
      {questions.length > 0 && (
        <div className="mt-6">
          <h2 className="text-lg font-semibold mb-2">Questions Added:</h2>
          <ul className="list-disc pl-6">
            {questions.map((q, index) => (
              <li key={index}>
                {q.type}: {q.question}{" "}
                {q.marks && `(${q.marks} marks)`}{" "}
                {q.type === "MCQ" && `- Answer: ${q.correctOption}`}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
