import { useContext, useState, useEffect } from "react";
import { AuthContext } from "../context/AuthContext";
import api from "../utils/api";
import "../dashboard.css"; // import styles

export default function DashboardStudent() {
  const { user, fetchMe } = useContext(AuthContext);
  const [editing, setEditing] = useState(false);
  const [form, setForm] = useState({});
  const [avatarFile, setAvatarFile] = useState(null);

  useEffect(() => {
    fetchMe();
  }, []);

  useEffect(() => {
    if (user) setForm(user);
  }, [user]);

  const onChange = (e) => {
    const { name, value } = e.target;
    // Handle nested student fields properly
    if (name.startsWith("student.")) {
      const key = name.split(".")[1];
      setForm({ ...form, student: { ...form.student, [key]: value } });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  const updateProfile = async () => {
    try {
      const formData = new FormData();
      formData.append("name", form.name);
      formData.append("email", form.email);
      if (form.student) {
        formData.append("educationLevel", form.student.educationLevel || "");
        formData.append("institutionName", form.student.institutionName || "");
        formData.append("contactNumber", form.student.contactNumber || "");
      }
      if (avatarFile) {
        formData.append("avatar", avatarFile);
      }

      const { data } = await api.put("/auth/me", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      localStorage.setItem("user", JSON.stringify(data.user));
      setEditing(false);
      fetchMe();
    } catch (err) {
      console.error("Update failed:", err.response?.data || err.message);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-card">
        <h1>üéì Student Dashboard</h1>

        {user?.avatar && (
          <img src={user.avatar} alt="avatar" className="avatar" />
        )}

        {!editing ? (
  <>
    <div className="profile-header">
      {user?.avatar && (
        <img src={user.avatar} alt="avatar" className="avatar" />
      )}
      <div className="profile-info">
        <p className="welcome">
          Welcome, <b>{user?.name}</b>
        </p>
        <ul className="details">
          {/* Student dashboard */}
          {user?.student && (
            <>
              <li>Email: {user.email}</li>
              <li>Education: {user.student.educationLevel}</li>
              <li>Institution: {user.student.institutionName}</li>
              <li>Contact: {user.student.contactNumber}</li>
            </>
          )}
          {/* Mentor dashboard */}
          {user?.mentor && (
            <>
              <li>Email: {user.email}</li>
              <li>Institution: {user.mentor.currentInstitution}</li>
              <li>Phone: {user.mentor.phoneNumber}</li>
              <li>Experience: {user.mentor.experience}</li>
            </>
          )}
        </ul>
      </div>
    </div>
    <button className="btn edit" onClick={() => setEditing(true)}>
      ‚úèÔ∏è Edit Profile
    </button>
  </>
) : (
          <div className="form-container">
            <input
              name="name"
              value={form.name || ""}
              onChange={onChange}
              placeholder="Full Name"
            />
            <input
              name="student.educationLevel"
              value={form.student?.educationLevel || ""}
              onChange={onChange}
              placeholder="Education Level"
            />
            <input
              name="student.institutionName"
              value={form.student?.institutionName || ""}
              onChange={onChange}
              placeholder="Institution"
            />
            <input
              name="student.contactNumber"
              value={form.student?.contactNumber || ""}
              onChange={onChange}
              placeholder="Contact Number"
            />
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setAvatarFile(e.target.files[0])}
            />
            <div className="btn-group">
              <button className="btn save" onClick={updateProfile}>üíæ Save</button>
              <button className="btn cancel" onClick={() => setEditing(false)}>‚ùå Cancel</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
