import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Clock, AlertTriangle, Camera, CheckCircle } from "lucide-react";
import api from "../utils/api";

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [warning, setWarning] = useState("");
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [cameraStream, setCameraStream] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showInstructions, setShowInstructions] = useState(true);

  // Fetch test data
  useEffect(() => {
    if (!test && id) {
      const fetchTest = async () => {
        try {
          const res = await api.get(`/tests/${id}`);
          console.log("Fetched test:", res.data);
          setTest(res.data);
          setTimeLeft(res.data.duration * 60);
        } catch (err) {
          console.error(err);
          alert("âŒ Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else if (test) {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  // Start test: fullscreen + camera
  const startTest = async () => {
    setShowInstructions(false);
    setStarted(true);
    
    // Request fullscreen
    const elem = document.documentElement;
    if (elem.requestFullscreen) {
      try {
        await elem.requestFullscreen();
      } catch (err) {
        console.error("Fullscreen failed:", err);
        alert("âš ï¸ Fullscreen mode is required for this test!");
      }
    }

    // Request camera access
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { width: 320, height: 240 },
        audio: false
      });
      setCameraStream(stream);
      const video = document.getElementById("cameraFeed");
      if (video) {
        video.srcObject = stream;
      }
    } catch (err) {
      console.error("Camera access denied:", err);
      alert("ðŸ“· Camera access is required for this test! Please allow camera permissions and refresh.");
    }
  };

  // Timer countdown
  useEffect(() => {
    if (started && timeLeft > 0 && !isSubmitting) {
      const timer = setInterval(() => {
        setTimeLeft((prev) => {
          if (prev <= 1) {
            handleSubmit("â° Time's up! Test auto-submitted.");
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [started, timeLeft, isSubmitting]);

  // Enhanced violation detection
  useEffect(() => {
    if (!started) return;

    const handleViolation = (message) => {
      if (violationCount === 0) {
        const warningMsg = `âš ï¸ WARNING: ${message}\n\nThis is your ONLY warning. Any further violation will immediately end your test and submit it automatically.`;
        alert(warningMsg);
        setWarning(`First Warning: ${message}`);
        setViolationCount(1);
        setTimeout(() => setWarning(""), 10000);
      } else {
        alert(`ðŸš¨ TEST TERMINATED: ${message}\n\nYour test has been submitted automatically due to violation of test rules.`);
        handleSubmit(`Test terminated due to violation: ${message}`);
      }
    };

    const handleCopy = (e) => {
      e.preventDefault();
      handleViolation("Copying text is not allowed during the test!");
    };

    const handlePaste = (e) => {
      e.preventDefault();
      handleViolation("Pasting content is not allowed during the test!");
    };

    const handleKeyDown = (e) => {
      // Screenshot detection
      if (
        e.key === "PrintScreen" ||
        (e.metaKey && e.shiftKey && (e.key === "4" || e.key === "5")) ||
        (e.ctrlKey && e.shiftKey && e.key === "S")
      ) {
        e.preventDefault();
        handleViolation("Taking screenshots is strictly prohibited!");
      }
      
      // Developer tools detection
      if (
        e.key === "F12" || 
        (e.ctrlKey && e.shiftKey && e.key === "I") ||
        (e.ctrlKey && e.shiftKey && e.key === "J") ||
        (e.ctrlKey && e.key === "u")
      ) {
        e.preventDefault();
        handleViolation("Opening developer tools is not allowed!");
      }

      // Refresh page detection
      if (e.key === "F5" || (e.ctrlKey && e.key === "r")) {
        e.preventDefault();
        handleViolation("Refreshing the page is not allowed!");
      }
    };

    const handleVisibilityChange = () => {
      if (document.hidden) {
        handleViolation("Switching tabs or minimizing window is not allowed!");
      }
    };

    const handleWindowBlur = () => {
      handleViolation("Switching to another application is not allowed!");
    };

    const handleContextMenu = (e) => {
      e.preventDefault();
      handleViolation("Right-click is disabled during the test!");
    };

    const handleBeforeUnload = (e) => {
      e.preventDefault();
      e.returnValue = '';
      return '';
    };

    // Add event listeners
    document.addEventListener("copy", handleCopy);
    document.addEventListener("paste", handlePaste);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("visibilitychange", handleVisibilityChange);
    document.addEventListener("contextmenu", handleContextMenu);
    window.addEventListener("blur", handleWindowBlur);
    window.addEventListener("beforeunload", handleBeforeUnload);

    return () => {
      document.removeEventListener("copy", handleCopy);
      document.removeEventListener("paste", handlePaste);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
      document.removeEventListener("contextmenu", handleContextMenu);
      window.removeEventListener("blur", handleWindowBlur);
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, [started, violationCount]);

  const handleAnswerChange = (questionId, value) => {
    setAnswers((prev) => ({ ...prev, [questionId]: value }));
  };

  const handleSubmit = async (reason = "Test completed successfully") => {
    if (isSubmitting) return;
    setIsSubmitting(true);

    try {
      const res = await api.post(`/tests/${id}/submit`, {
        answers,
        submissionReason: reason,
      });

      // Stop camera and exit fullscreen
      if (cameraStream) {
        cameraStream.getTracks().forEach((track) => track.stop());
      }
      if (document.fullscreenElement) {
        await document.exitFullscreen();
      }

      const score = res.data?.score;
      alert(
        `âœ… ${reason}\n\nYour Score: ${score?.obtainedMarks || 0}/${score?.totalMarks || 0}\nPercentage: ${score?.percentage || 0}%`
      );
      
      navigate("/my-scores");
    } catch (err) {
      console.error("Submission error:", err);
      alert("âŒ Error submitting test. Please try again or contact support.");
      setIsSubmitting(false);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  const getAnsweredQuestionsCount = () => {
    const questions = Array.isArray(test?.questions) ? test.questions : [];
    return questions.filter(q => answers[q._id]).length;
  };

  const isAllQuestionsAnswered = () => {
    const questions = Array.isArray(test?.questions) ? test.questions : [];
    return questions.length > 0 && questions.every(q => answers[q._id]);
  };

  // Loading state
  if (!test) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-xl font-medium text-gray-700">Loading test...</p>
        </div>
      </div>
    );
  }

  // Instructions Page
  if (showInstructions && !started) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-4xl w-full">
          <div className="text-center mb-8">
            <h1 className="text-4xl font-bold text-gray-800 mb-2">{test.name}</h1>
            <div className="flex justify-center space-x-8 text-lg text-gray-600 mb-6">
              <div className="flex items-center">
                <Clock className="mr-2" size={20} />
                <span>Duration: {test.duration} minutes</span>
              </div>
              <div className="flex items-center">
                <CheckCircle className="mr-2" size={20} />
                <span>Total Questions: {test.questions?.length || 0}</span>
              </div>
              <div className="flex items-center">
                <span>ðŸ“Š Total Marks: {test.totalMarks}</span>
              </div>
            </div>
          </div>

          <div className="bg-red-50 border-l-4 border-red-400 p-6 mb-8">
            <h2 className="text-2xl font-bold text-red-800 mb-4 flex items-center">
              <AlertTriangle className="mr-2" size={24} />
              Important Test Instructions
            </h2>
            <ul className="space-y-3 text-red-700">
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>Fullscreen Mode:</strong> The test will run in fullscreen mode. Do not exit fullscreen during the test.</span>
              </li>
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>Camera Monitoring:</strong> Your camera will be active throughout the test for monitoring purposes.</span>
              </li>
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>Tab Switching:</strong> Switching tabs or minimizing the window is strictly prohibited.</span>
              </li>
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>No Copy/Paste:</strong> Copying or pasting content is not allowed.</span>
              </li>
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>No Screenshots:</strong> Taking screenshots or screen recordings is prohibited.</span>
              </li>
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>Developer Tools:</strong> Opening browser developer tools is not allowed.</span>
              </li>
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>Single Warning:</strong> You will receive only ONE warning for violations. Second violation will automatically submit and end your test.</span>
              </li>
              <li className="flex items-start">
                <span className="text-red-500 mr-2">â€¢</span>
                <span><strong>Auto-Submit:</strong> Test will be automatically submitted when time runs out.</span>
              </li>
            </ul>
          </div>

          <div className="text-center">
            <button
              onClick={startTest}
              className="px-12 py-4 bg-gradient-to-r from-blue-600 to-blue-700 text-white text-lg font-semibold rounded-xl hover:from-blue-700 hover:to-blue-800 transform hover:scale-105 transition-all duration-200 shadow-lg"
            >
              I Understand - Start Test
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Test Interface
  const questions = Array.isArray(test.questions) ? test.questions : [];
  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* Warning Banner */}
      {warning && (
        <div className="bg-red-500 text-white px-6 py-3 text-center font-semibold flex items-center justify-center">
          <AlertTriangle className="mr-2" size={20} />
          {warning}
        </div>
      )}

      {/* Header */}
      <div className="bg-white shadow-lg px-6 py-4">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-800">{test.name}</h1>
            <p className="text-gray-600">
              Question {currentQuestionIndex + 1} of {questions.length} | 
              Answered: {getAnsweredQuestionsCount()}/{questions.length}
            </p>
          </div>
          
          <div className="flex items-center space-x-6">
            {/* Timer */}
            <div className={`flex items-center px-4 py-2 rounded-lg ${
              timeLeft <= 300 ? "bg-red-100 text-red-800 border-2 border-red-300" : 
              timeLeft <= 600 ? "bg-yellow-100 text-yellow-800 border-2 border-yellow-300" :
              "bg-blue-100 text-blue-800 border-2 border-blue-300"
            }`}>
              <Clock className="mr-2" size={20} />
              <span className="text-lg font-bold">{formatTime(timeLeft)}</span>
            </div>
            
            {/* Camera Feed */}
            <div className="relative">
              <video
                id="cameraFeed"
                autoPlay
                playsInline
                muted
                className="w-32 h-24 bg-black rounded-lg border-2 border-blue-500"
              />
              <div className="absolute top-1 left-1">
                <Camera className="text-white" size={16} />
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="flex flex-1">
        {/* Question Navigation Sidebar */}
        <div className="w-64 bg-white shadow-lg p-6">
          <h3 className="text-lg font-semibold mb-4 text-gray-800">Questions</h3>
          <div className="grid grid-cols-5 gap-2 mb-6">
            {questions.map((q, idx) => {
              const isAnswered = !!answers[q._id];
              const isCurrent = idx === currentQuestionIndex;
              return (
                <button
                  key={q._id}
                  onClick={() => setCurrentQuestionIndex(idx)}
                  className={`w-10 h-10 rounded-full border-2 flex items-center justify-center text-sm font-semibold transition-all duration-200 ${
                    isCurrent
                      ? "bg-blue-600 text-white border-blue-600 shadow-lg"
                      : isAnswered
                      ? "bg-green-500 text-white border-green-500 hover:bg-green-600"
                      : "bg-gray-100 text-gray-600 border-gray-300 hover:bg-gray-200"
                  }`}
                >
                  {idx + 1}
                </button>
              );
            })}
          </div>
          
          {/* Progress Summary */}
          <div className="border-t pt-4">
            <div className="text-sm text-gray-600 mb-2">Progress Summary:</div>
            <div className="flex items-center justify-between text-sm">
              <span className="flex items-center">
                <div className="w-3 h-3 bg-green-500 rounded-full mr-2"></div>
                Answered: {getAnsweredQuestionsCount()}
              </span>
              <span className="flex items-center">
                <div className="w-3 h-3 bg-gray-300 rounded-full mr-2"></div>
                Remaining: {questions.length - getAnsweredQuestionsCount()}
              </span>
            </div>
          </div>
        </div>

        {/* Question Area */}
        <div className="flex-1 p-8">
          {currentQuestion ? (
            <div className="bg-white rounded-xl shadow-lg p-8 max-w-4xl mx-auto">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-xl font-semibold text-gray-800">
                  Question {currentQuestionIndex + 1} of {questions.length}
                </h2>
                <span className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium">
                  {currentQuestion.marks} {currentQuestion.marks === 1 ? 'mark' : 'marks'}
                </span>
              </div>
              
              <div className="mb-8">
                <p className="text-lg text-gray-800 leading-relaxed">
                  {currentQuestion.question || "No question text available"}
                </p>
              </div>
              
              <div className="space-y-3 mb-8">
                {currentQuestion.options?.map((option, idx) => {
                  const optionLabel = String.fromCharCode(65 + idx); // A, B, C, D
                  return (
                    <label
                      key={idx}
                      className={`flex items-center p-4 rounded-lg border-2 cursor-pointer transition-all duration-200 ${
                        answers[currentQuestion._id] === option
                          ? "border-blue-500 bg-blue-50 shadow-md"
                          : "border-gray-200 hover:border-gray-300 hover:bg-gray-50"
                      }`}
                    >
                      <input
                        type="radio"
                        name={currentQuestion._id}
                        value={option}
                        checked={answers[currentQuestion._id] === option}
                        onChange={(e) => handleAnswerChange(currentQuestion._id, e.target.value)}
                        className="sr-only"
                      />
                      <div className={`w-6 h-6 rounded-full border-2 mr-4 flex items-center justify-center text-sm font-bold ${
                        answers[currentQuestion._id] === option
                          ? "border-blue-500 bg-blue-500 text-white"
                          : "border-gray-300 text-gray-500"
                      }`}>
                        {optionLabel}
                      </div>
                      <span className="text-gray-800">{option}</span>
                    </label>
                  );
                })}
              </div>

              {/* Navigation Buttons */}
              <div className="flex justify-between items-center">
                <button
                  onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))}
                  disabled={currentQuestionIndex === 0}
                  className="px-6 py-3 bg-gray-200 text-gray-700 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-300 transition-colors"
                >
                  Previous
                </button>
                
                <div className="flex space-x-4">
                  {currentQuestionIndex < questions.length - 1 ? (
                    <button
                      onClick={() => setCurrentQuestionIndex(currentQuestionIndex + 1)}
                      className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                    >
                      Next Question
                    </button>
                  ) : (
                    <button
                      onClick={() => {
                        if (isAllQuestionsAnswered() || window.confirm("You haven't answered all questions. Are you sure you want to submit?")) {
                          handleSubmit();
                        }
                      }}
                      disabled={isSubmitting}
                      className={`px-8 py-3 rounded-lg font-semibold transition-colors ${
                        isAllQuestionsAnswered() 
                          ? "bg-green-600 hover:bg-green-700 text-white" 
                          : "bg-orange-500 hover:bg-orange-600 text-white"
                      }`}
                    >
                      {isSubmitting ? "Submitting..." : isAllQuestionsAnswered() ? "Submit Test" : "Submit Incomplete"}
                    </button>
                  )}
                </div>
              </div>
            </div>
          ) : (
            <div className="text-center text-xl text-gray-600 py-12">
              <p>No questions available for this test.</p>
              <button
                onClick={() => navigate("/my-tests")}
                className="mt-4 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
              >
                Return to My Tests
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
