import { useEffect, useState, useContext } from "react";
import api from "../utils/api";
import { AuthContext } from "../context/AuthContext";

export default function DashboardAdmin() {
  const { user } = useContext(AuthContext);
  const [stats, setStats] = useState(null);

  useEffect(() => {
    api.get("/admin/stats").then(res => setStats(res.data));
  }, []);

  return (
    <div className="page-container">
      <h1>Admin Dashboard</h1>
      <p>Welcome, {user?.name}</p>

      {!stats ? (
        <p>Loading stats...</p>
      ) : (
        <div>
          <h3>System Overview</h3>
          <ul>
            <li>Total Students: {stats.studentsCount}</li>
            <li>Total Mentors: {stats.mentorsCount}</li>
            <li>Total Quizzes: {stats.quizzesCount}</li>
            <li>
              Best Student: {stats.bestStudent?.name || "N/A"} (
              {stats.bestStudent?.email})
            </li>
            <li>
              Best Mentor: {stats.bestMentor?.name || "N/A"} (
              {stats.bestMentor?.email})
            </li>
          </ul>
        </div>
      )}
    </div>
  );
}
