import { useEffect, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import api from "../utils/api";
import "./taketest.css";

export default function TakeTest() {
  const { state } = useLocation();
  const navigate = useNavigate();
  const { id } = useParams();

  const [test, setTest] = useState(state?.test || null);
  const [started, setStarted] = useState(false);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(0);
  const [violationCount, setViolationCount] = useState(0);
  const [warning, setWarning] = useState("");

  // Fetch test if not passed via state
  useEffect(() => {
    if (!test) {
      const fetchTest = async () => {
        try {
          const { data } = await api.get(`/student/tests/${id}`);
          setTest(data);
          setTimeLeft(data.duration * 60);
        } catch (err) {
          console.error(err);
          alert("Failed to fetch test!");
          navigate("/my-tests");
        }
      };
      fetchTest();
    } else {
      setTimeLeft(test.duration * 60);
    }
  }, [id, test, navigate]);

  const startTest = () => {
    setStarted(true);
    openFullscreen();
  };

  // Fullscreen mode
  const openFullscreen = () => {
    const elem = document.documentElement;
    if (elem.requestFullscreen) elem.requestFullscreen();
    else if (elem.mozRequestFullScreen) elem.mozRequestFullScreen();
    else if (elem.webkitRequestFullscreen) elem.webkitRequestFullscreen();
    else if (elem.msRequestFullscreen) elem.msRequestFullscreen();
  };

  // Timer countdown
  useEffect(() => {
    if (!started) return;
    if (timeLeft <= 0) handleSubmit();
    const interval = setInterval(() => setTimeLeft(prev => prev - 1), 1000);
    return () => clearInterval(interval);
  }, [timeLeft, started]);

  // Tab switching and copy warnings
  useEffect(() => {
    const handleViolation = (message) => {
      if (!started) return;
      setViolationCount(prev => prev + 1);
      setWarning(message);
      setTimeout(() => setWarning(""), 3000);
      if (violationCount >= 1) handleSubmit();
    };

    const handleVisibility = () => {
      if (document.hidden) handleViolation("Do not switch tabs! Test will end after 2nd violation.");
    };
    const handleCopy = (e) => {
      e.preventDefault();
      handleViolation("Copying is not allowed!");
    };

    document.addEventListener("visibilitychange", handleVisibility);
    document.addEventListener("copy", handleCopy);

    return () => {
      document.removeEventListener("visibilitychange", handleVisibility);
      document.removeEventListener("copy", handleCopy);
    };
  }, [violationCount, started]);

  const handleChange = (qId, value) => {
    setAnswers(prev => ({ ...prev, [qId]: value }));
  };

  const handleSubmit = async () => {
    try {
      await api.post(`/student/tests/${id}/submit`, { answers });
      alert("Test submitted!");
      navigate("/dashboard");
    } catch (err) {
      console.error(err);
    }
  };

  if (!test) return <p>Loading test...</p>;

  // Instruction screen
  if (!started) {
    return (
      <div className="instruction-container">
        <div className="instruction-box animate-fade-in">
          <h1>{test.name}</h1>
          <h3>Instructions:</h3>
          <ul>
            <li>Time allotted: {test.duration} minutes</li>
            <li>Do not switch tabs</li>
            <li>Do not copy questions or answers</li>
            <li>First violation: warning, second violation: test ends</li>
            <li>Once started, test cannot be paused</li>
          </ul>
          <button onClick={startTest}>Start Test</button>
        </div>
      </div>
    );
  }

  // Test screen
  return (
    <div className="taketest-container">
      {warning && <div className="warning">{warning}</div>}
      <div className="taketest-header">
        <h1>{test.name}</h1>
        <div className="timer">Time Left: {Math.floor(timeLeft / 60)}:{("0" + (timeLeft % 60)).slice(-2)}</div>
      </div>

      <div className="taketest-body">
        <div className="question-nav">
          {test.questions.map((q, idx) => (
            <div
              key={q._id}
              className={`q-circle ${answers[q._id] ? "answered" : ""}`}
            >
              {idx + 1}
            </div>
          ))}
        </div>

        <div className="questions-section">
          {test.questions.map((q, index) => (
            <div key={q._id} className="question-card">
              <p className="question-text">Q{index + 1}: {q.question}</p>
              <div className="options-container">
                {q.options ? q.options.map((opt, i) => (
                  <label key={i} className="option">
                    <input
                      type="radio"
                      name={q._id}
                      value={opt}
                      onChange={(e) => handleChange(q._id, e.target.value)}
                    />
                    {opt}
                  </label>
                )) : (
                  <textarea
                    placeholder="Your answer"
                    onChange={(e) => handleChange(q._id, e.target.value)}
                  />
                )}
              </div>
            </div>
          ))}
          {Object.keys(answers).length === test.questions.length && (
            <button className="submit-btn" onClick={handleSubmit}>Submit Test</button>
          )}
        </div>
      </div>
    </div>
  );
}
