import { useEffect, useState } from "react";
import api from "../utils/api";
import { FaSpinner, FaExclamationTriangle, FaTrophy, FaChartLine } from "react-icons/fa";
import "./myscores.css";

export default function MyScores() {
  const [scores, setScores] = useState([]);
  const [selectedTest, setSelectedTest] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchScores = async () => {
      try {
        setLoading(true);
        setError(null);
        
        console.log("Fetching scores from /student/scores...");
        const { data } = await api.get("/student/scores");
        console.log("Raw scores response:", data);

        // Handle different response formats
        let scoresArray = [];
        if (Array.isArray(data)) {
          scoresArray = data;
        } else if (data.scores && Array.isArray(data.scores)) {
          scoresArray = data.scores;
        } else if (data.data && Array.isArray(data.data)) {
          scoresArray = data.data;
        } else {
          console.warn("Unexpected scores data format:", data);
          scoresArray = [];
        }

        console.log("Scores array:", scoresArray);

        const formattedScores = scoresArray.map((score, index) => {
          console.log(`Formatting score ${index}:`, score);
          return {
            id: score._id || score.id,
            testName: score.testName || "Unknown Test",
            totalMarks: score.totalMarks || 0,
            obtainedMarks: score.obtainedMarks || 0,
            testId: score.testId,
            createdAt: score.createdAt || score.attemptedAt,
          };
        });

        console.log("Formatted scores:", formattedScores);
        setScores(formattedScores);
      } catch (err) {
        console.error("Error fetching scores:", err);
        let errorMessage = "Failed to load scores";
        
        if (err.response) {
          errorMessage = err.response.data?.message || `Server error: ${err.response.status}`;
        } else if (err.request) {
          errorMessage = "Unable to connect to server. Please check if the server is running.";
        } else {
          errorMessage = err.message || "An unexpected error occurred";
        }
        
        setError(errorMessage);
      } finally {
        setLoading(false);
      }
    };

    fetchScores();
  }, []);

  // Determine color based on percentage
  const getColor = (percent) => {
    if (percent < 40) return "#f87171"; // red
    if (percent <= 85) return "#facc15"; // yellow
    return "#34d399"; // green
  };

  // Generate basic report
  const generateReport = (test) => {
    const percent = ((test.obtainedMarks / test.totalMarks) * 100).toFixed(2);
    return `You attempted the test "${test.testName}". You scored ${test.obtainedMarks} out of ${test.totalMarks}, which is ${percent}%. ${
      percent >= 85
        ? "Excellent performance! You have mastered most of the concepts in this test."
        : percent >= 40
        ? "Good effort! You have passed the test and understood the key concepts, but there is room for improvement."
        : "Unfortunately, your performance was below the passing mark. Review the material and try again to improve your understanding."
    }`;
  };

  const calculateStats = () => {
    if (scores.length === 0) return { average: 0, totalTests: 0, highestScore: 0 };
    
    const totalPercentages = scores.map(score => (score.obtainedMarks / score.totalMarks) * 100);
    const average = totalPercentages.reduce((sum, percent) => sum + percent, 0) / scores.length;
    const highestScore = Math.max(...totalPercentages);
    
    return {
      average: average.toFixed(2),
      totalTests: scores.length,
      highestScore: highestScore.toFixed(2)
    };
  };

  if (loading) {
    return (
      <div className="myscores-container">
        <div className="loading-container">
          <FaSpinner className="spinner" />
          <p>Loading your test scores...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="myscores-container">
        <div className="error-container">
          <FaExclamationTriangle className="error-icon" />
          <h2>Unable to Load Scores</h2>
          <p>{error}</p>
          <button 
            className="retry-btn" 
            onClick={() => window.location.reload()}
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  const stats = calculateStats();

  return (
    <div className="myscores-container">
      <div className="myscores-header">
        <h1>My Test Scores</h1>
        
        {scores.length > 0 && (
          <div className="stats-container">
            <div className="stat-card">
              <FaTrophy className="stat-icon" />
              <div>
                <span className="stat-value">{stats.totalTests}</span>
                <span className="stat-label">Tests Completed</span>
              </div>
            </div>
            <div className="stat-card">
              <FaChartLine className="stat-icon" />
              <div>
                <span className="stat-value">{stats.average}%</span>
                <span className="stat-label">Average Score</span>
              </div>
            </div>
            <div className="stat-card">
              <FaTrophy className="stat-icon" />
              <div>
                <span className="stat-value">{stats.highestScore}%</span>
                <span className="stat-label">Highest Score</span>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="scores-grid">
        {scores.length === 0 ? (
          <div className="no-scores">
            <p>No test scores available yet.</p>
            <p>Complete some tests to see your scores here.</p>
          </div>
        ) : (
          scores.map((test) => {
            const percent = (test.obtainedMarks / test.totalMarks) * 100;
            return (
              <div
                key={test.id}
                className="score-card"
                style={{ borderColor: getColor(percent) }}
                onClick={() =>
                  setSelectedTest(selectedTest === test.id ? null : test.id)
                }
              >
                <h2>{test.testName}</h2>
                <div className="score-display">
                  <span className="score-main">
                    {test.obtainedMarks}/{test.totalMarks}
                  </span>
                  <span 
                    className="score-percentage"
                    style={{ color: getColor(percent) }}
                  >
                    {percent.toFixed(2)}%
                  </span>
                </div>

                {selectedTest === test.id && (
                  <div className="report">
                    <p>{generateReport(test)}</p>
                  </div>
                )}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}
