import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Trophy,
  Calendar,
  Target,
  TrendingUp,
  Award,
  BookOpen,
  ArrowLeft,
} from "lucide-react";
import api from "../utils/api";
import "./myscores.css";

export default function MyScores() {
  const [scores, setScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalTests: 0,
    averageScore: 0,
    highestScore: 0,
    totalMarks: 0,
    obtainedMarks: 0,
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const { data } = await api.get("/student/scores");
        setScores(data.scores || []);
        calculateStats(data.scores || []);
      } catch (err) {
        console.error("Failed to fetch scores:", err);
        const mockScores = [
          {
            _id: "1",
            testName: "JavaScript Fundamentals",
            totalMarks: 50,
            obtainedMarks: 42,
            percentage: 84,
            createdAt: "2024-01-15T10:30:00Z",
          },
          {
            _id: "2",
            testName: "React Basics",
            totalMarks: 30,
            obtainedMarks: 25,
            percentage: 83.33,
            createdAt: "2024-01-10T14:20:00Z",
          },
        ];
        setScores(mockScores);
        calculateStats(mockScores);
      } finally {
        setLoading(false);
      }
    };
    fetchScores();
  }, []);

  const calculateStats = (scoresData) => {
    if (scoresData.length === 0) {
      setStats({
        totalTests: 0,
        averageScore: 0,
        highestScore: 0,
        totalMarks: 0,
        obtainedMarks: 0,
      });
      return;
    }
    const totalTests = scoresData.length;
    const totalMarks = scoresData.reduce((sum, score) => sum + score.totalMarks, 0);
    const obtainedMarks = scoresData.reduce(
      (sum, score) => sum + score.obtainedMarks,
      0
    );
    const averageScore =
      totalMarks > 0 ? (obtainedMarks / totalMarks) * 100 : 0;
    const highestScore = Math.max(
      ...scoresData.map(
        (score) =>
          score.percentage ||
          (score.totalMarks > 0
            ? (score.obtainedMarks / score.totalMarks) * 100
            : 0)
      )
    );

    setStats({
      totalTests,
      averageScore: Math.round(averageScore * 100) / 100,
      highestScore: Math.round(highestScore * 100) / 100,
      totalMarks,
      obtainedMarks,
    });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getScoreColor = (percentage) => {
    if (percentage >= 90) return "bg-gradient-to-r from-green-400 to-green-600 text-white";
    if (percentage >= 75) return "bg-gradient-to-r from-blue-400 to-blue-600 text-white";
    if (percentage >= 60) return "bg-gradient-to-r from-yellow-400 to-yellow-600 text-white";
    return "bg-gradient-to-r from-red-400 to-red-600 text-white";
  };

  const getGrade = (percentage) => {
    if (percentage >= 90) return "A+";
    if (percentage >= 80) return "A";
    if (percentage >= 70) return "B+";
    if (percentage >= 60) return "B";
    if (percentage >= 50) return "C";
    return "F";
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-animated">
        <div className="text-center animate-fadeIn">
          <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-pink-600 mx-auto mb-6"></div>
          <p className="text-xl font-bold text-pink-900">
            Fetching your scores...
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-animated py-10">
      <div className="max-w-7xl mx-auto px-6">
        
        {/* Header */}
        <div className="flex items-center justify-between mb-12 animate-slideDown">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate("/dashboard/student")}
              className="p-3 bg-white/70 backdrop-blur-md rounded-full shadow-md hover:shadow-xl transition-all"
            >
              <ArrowLeft className="h-5 w-5 text-indigo-600" />
            </button>
            <div>
              <h1 className="text-4xl font-extrabold text-white flex items-center drop-shadow-md">
                <Trophy className="mr-3 text-yellow-300" size={42} />
                My Test Scores
              </h1>
              <p className="text-white/90 mt-2 text-lg">
                Track your journey and celebrate your progress ðŸŽ¯
              </p>
            </div>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 mb-14">
          {[
            { label: "Total Tests", value: stats.totalTests, color: "from-blue-400 to-blue-600" },
            { label: "Average Score", value: `${stats.averageScore}%`, color: "from-green-400 to-green-600" },
            { label: "Highest Score", value: `${stats.highestScore}%`, color: "from-yellow-400 to-yellow-600" },
            { label: "Total Points", value: `${stats.obtainedMarks}/${stats.totalMarks}`, color: "from-purple-400 to-purple-600" },
          ].map((card, idx) => (
            <div
              key={idx}
              className={`p-6 rounded-2xl shadow-lg bg-gradient-to-r ${card.color} text-white transform transition hover:scale-105 animate-fadeIn`}
              style={{ animationDelay: `${idx * 0.2}s` }}
            >
              <p className="text-sm opacity-90">{card.label}</p>
              <p className="text-4xl font-extrabold">{card.value}</p>
            </div>
          ))}
        </div>

        {/* Scores List */}
        <div className="bg-white/80 backdrop-blur-md rounded-2xl shadow-2xl overflow-hidden animate-fadeIn">
          <div className="px-6 py-5 border-b bg-gradient-to-r from-indigo-100 to-pink-100">
            <h2 className="text-2xl font-bold text-gray-800">ðŸ“Š Test Results</h2>
          </div>

          {scores.length === 0 ? (
            <div className="text-center py-16">
              <Trophy className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-2xl font-bold text-gray-800 mb-3">
                No Results Yet
              </h3>
              <p className="text-gray-600 mb-6">
                Take your first test and see results here.
              </p>
              <button
                onClick={() => navigate("/my-tests")}
                className="px-6 py-3 bg-gradient-to-r from-indigo-500 to-indigo-700 text-white rounded-xl shadow-md hover:shadow-lg transition-all"
              >
                Take a Test
              </button>
            </div>
          ) : (
            <div className="divide-y divide-gray-200">
              {scores.map((score, index) => {
                const percentage =
                  score.percentage ||
                  (score.totalMarks > 0
                    ? (score.obtainedMarks / score.totalMarks) * 100
                    : 0);
                const grade = getGrade(percentage);
                const colorClass = getScoreColor(percentage);

                return (
                  <div
                    key={score._id || index}
                    className="p-6 hover:bg-indigo-50/70 transition-all"
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="text-lg font-bold text-gray-900">
                          {score.testName}
                        </h3>
                        <div className="flex items-center space-x-6 text-sm text-gray-600 mt-1">
                          <span className="flex items-center">
                            <Calendar className="h-4 w-4 mr-1 text-indigo-500" />
                            {formatDate(score.createdAt)}
                          </span>
                          <span className="flex items-center">
                            <Target className="h-4 w-4 mr-1 text-green-500" />
                            {score.obtainedMarks}/{score.totalMarks} marks
                          </span>
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <div
                          className={`px-4 py-2 rounded-full text-sm font-semibold shadow-md ${colorClass}`}
                        >
                          {Math.round(percentage * 100) / 100}%
                        </div>
                        <div className="text-2xl font-bold text-gray-800 bg-indigo-100 px-3 py-1 rounded-lg shadow-sm">
                          {grade}
                        </div>
                      </div>
                    </div>

                    {/* Progress Bar */}
                    <div className="mt-4">
                      <div className="w-full bg-gray-200 rounded-full h-3 overflow-hidden">
                        <div
                          className={`h-3 rounded-full progress-bar ${
                            percentage >= 90
                              ? "bg-green-500"
                              : percentage >= 75
                              ? "bg-blue-500"
                              : percentage >= 60
                              ? "bg-yellow-500"
                              : "bg-red-500"
                          }`}
                          style={{ width: `${Math.min(percentage, 100)}%` }}
                        ></div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>

        {/* Action Buttons */}
        <div className="mt-12 flex justify-center space-x-6 animate-fadeIn">
          <button
            onClick={() => navigate("/my-tests")}
            className="px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-700 text-white rounded-xl shadow-md hover:scale-105 transition-all flex items-center space-x-2"
          >
            <BookOpen className="h-5 w-5" />
            <span>Take More Tests</span>
          </button>
          <button
            onClick={() => navigate("/achievements")}
            className="px-6 py-3 bg-gradient-to-r from-purple-500 to-purple-700 text-white rounded-xl shadow-md hover:scale-105 transition-all flex items-center space-x-2"
          >
            <Award className="h-5 w-5" />
            <span>View Achievements</span>
          </button>
        </div>
      </div>
    </div>
  );
}
