import { useEffect, useState } from "react";
import api from "../utils/api";
import { useNavigate } from "react-router-dom";
import "..pages/MyTests"; // Make sure you create this CSS file

export default function MyTests() {
  const [tests, setTests] = useState([]);
  const navigate = useNavigate();

  // Fetch all available tests from backend
  useEffect(() => {
    const fetchTests = async () => {
      try {
        const { data } = await api.get("/student/tests"); // Make sure this endpoint returns your tests
        setTests(data);
      } catch (err) {
        console.error("Failed to fetch tests:", err);
      }
    };
    fetchTests();
  }, []);

  // Navigate to Take Test page
  const startTest = (test) => {
    navigate(`/take-test/${test._id}`, { state: { test } });
  };

  return (
    <div className="tests-container">
      <h1 className="page-title">Available Tests</h1>
      <div className="tests-grid">
        {tests.length === 0 && <p>No tests available at the moment.</p>}
        {tests.map((test) => (
          <div
            key={test._id}
            className="test-card"
            style={{
              backgroundColor: "#f0f4f8",
              padding: "1.5rem",
              borderRadius: "0.75rem",
              boxShadow: "0 4px 6px rgba(0,0,0,0.1)",
              display: "flex",
              flexDirection: "column",
              gap: "0.5rem",
            }}
          >
            <h2 className="test-name">{test.name}</h2>
            <p>
              <strong>Max Marks:</strong> {test.totalMarks}
            </p>
            <p>
              <strong>Duration:</strong> {test.duration} min
            </p>
            <button
              className="take-test-btn"
              onClick={() => startTest(test)}
              style={{
                marginTop: "auto",
                padding: "0.5rem 1rem",
                backgroundColor: "#3b82f6",
                color: "white",
                border: "none",
                borderRadius: "0.5rem",
                cursor: "pointer",
              }}
            >
              Take Test
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
